INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1, 1, 1, '양지머리로 육수를 낸 후 식혀 기름을 걷어낸 후, 불린 쌀을 넣어 고슬고슬하게 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2, 1, 2, '안심은 불고기 양념하여 30분간 재워 국물 없이 구워 한 김 식으면 한입 크기로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (3, 1, 3, '청포묵은 고기와 비슷한 크기로 잘라 끓는 물에 데쳐내고 계란은 노른자와 흰자를 분리해 지단부쳐 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (4, 1, 4, '콩나물과 숙주, 미나리는 데쳐서 국간장과 참기름으로 간하고, 고사리와 도라지는 참기름을 두른 프라이팬에 살짝 볶아놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (5, 1, 5, '밥을 참기름으로 무쳐 그릇에 담고 준비한 재료를 고루 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (6, 2, 1, '찹쌀과 멥쌀은 깨끗이 씻어 건져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (7, 2, 2, '차수수는 붉은 물이 안 나올 때까지 깨끗이 씻어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (8, 2, 3, '콩은 불려서 일어 건져놓고, 팥은 삶아서 건져놓고 삶은 물은 받아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (9, 2, 4, '차조는 씻어서 잘 일은 후 건져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (10, 2, 5, '차조를 뺀 모든 재료를 고루 섞어 밥솥에 앉혀 놓고 팥 삶은 물에 소금(1/3작은술 정도)을 넣은 밥물을 붓는데, 밥물은 보통 짓는 밥물보다 20% 적게 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (11, 2, 6, '밥이 끓기 시작하면 차조를 고루 얹어 뜸을 들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (12, 3, 1, '당면은 따뜻한 물에 불려 적당한 길이로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (13, 3, 2, '고기와 버섯, 채소는 모두 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (14, 3, 3, '달군 팬에 기름을 두르고 고기와, 버섯, 당근, 고추, 호박을 볶다가 숨이 죽으면 부추를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (15, 3, 4, '다진 파, 마늘, 생강을 넣고 소금과 통후추, 진간장을 넣어 간을 한 다음 당면을 넣어 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (16, 3, 5, '뜨거운 밥을 그릇에 담고 잡채를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (17, 4, 1, '쌀은 미리 씻어 불려놓고 콩나물은 씻어 소금물에 살짝 데쳐 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (18, 4, 2, '쇠고기는 곱게 다져 파, 마늘, 진간장으로 양념하여 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (19, 4, 3, '콩나물 삶은 물을 냄비에 붓고 쌀을 앉혀 밥을 짓다가 끓으면 삶은 콩나물과 쇠고기를 얹어 뜸들인다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (20, 4, 4, '뜸이 들면 고루 섞어 그릇에 담고 양념장과 함께 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (21, 5, 1, '흰 설탕에 물을 붓고 끓이다가 거품이 일면서 한 부분부터 타기 시작하면 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (22, 5, 2, '압력솥에 물기 뺀 쌀을 담고 분량의 흑설탕, 간장, 참기름, 식물성기름, 계핏가루를 넣고 ①에서 만든 카라멜소스를 부어 물이들게 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (23, 5, 3, '②에 밤, 대추, 잣을 넣고 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (24, 5, 4, '물 3컵을 붓고 센불로 가열하다가 끓기 시작하면 중불로 줄여 30분 지난뒤 불을 끄고 남은 열로 뜸들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (25, 6, 1, '청동호박은 반을 갈라 씨를 빼고 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (26, 6, 2, '찹쌀은 깨끗이 씻어 불렸다가 가루로 빻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (27, 6, 3, '팥은 씻어 일어 물을 넣고 한소끔 끓으면 첫물은 버리고 다시 물을 부어 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (28, 6, 4, '호박은 1cm 두께로 썰어 냄비에 담고 물과 설탕을 넣어 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (29, 6, 5, '호박이 푹 무르면 찹쌀가루를 물에 풀어 넣고 팥도 함께 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (30, 6, 6, '다 끓으면 설탕, 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (31, 7, 1, '쌀은 충분히 불려서 소쿠리에 건져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (32, 7, 2, '깨는 깨끗이 일어 건져서 고소한 향이 나도록 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (33, 7, 3, '분쇄기에 쌀과 깨를 따로따로 넣어 물을 조금씩 넣어가며 갈아 고운 체에 밭친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (34, 7, 4, '밑이 두터운 냄비에 곱게 간 쌀과 물을 부어 나무주걱으로 저으며 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (35, 7, 5, '냄비가 뜨거워 지면 갈은 깨를 조금씩 부어 멍울지지 않도록 가끔 저어주며 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (36, 7, 6, '확 끓어오르면 불을 약하게 줄이고 잘 섞이도록 서서히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (37, 8, 1, '쇠고기와 채소는 도톰하게 깍뚝썰기하여 버터에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (38, 8, 2, '카레는 찬물 2컵에 물을 조금씩 넣어가며 풀어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (39, 8, 3, '①의 냄비에 완두를 넣고 물을 자작하게 붓고 끓이다가 ②를 붓고 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (40, 8, 4, '국물이 되직하게 졸면 우유를 붓고 소금과 후춧가루로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (41, 8, 5, '따뜻한 밥위에 완성된 카레를 넉넉히 얹어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (42, 9, 1, '청피망, 홍피망, 양파, 오이, 당근은 잘게 다져 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (43, 9, 2, '프라이팬에 버터를 두르고 당근, 양파를 볶다가 양파가 투명해지면 남은 야채 재료를 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (44, 9, 3, '찬밥을 넣어 야채와 잘 섞은 후 소금, 후춧가루로 간을 약하게 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (45, 9, 4, '계란을 멍울 없이 풀어 소금, 후춧가루로 간하고, 반쯤 익으면 계란 중앙에 밥을 놓고 잘 감싸서 접시에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (46, 9, 5, '다시마와 멸치로 다시국물을 만들어 체에 거른 후 프라이팬에 육수 2컵을 넣고 쌈장을 잘 풀어 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (47, 9, 6, '팔팔 끓어오르면 전분가루를 넣어 농도를 되직하게 만든 후 오므라이스 위에 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (48, 10, 1, '밀가루에 소금과 따뜻한 물을 넣어 말랑하게 반죽하여 젖은 면보에 싸 냉장고에 30분간 넣어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (49, 10, 2, '감자와 애호박은 도톰하게 반달썰기한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (50, 10, 3, '실파는 4cm 길이로 자르고 홍고추는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (51, 10, 4, '멸치는 장국을 끓여 국간장으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (52, 10, 5, '국물이 끓어오르면 감자를 넣고, 반쯤 익으면 수제비 반죽을 얇게 뜯어 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (53, 10, 6, '호박을 넣어 파랗게 익으면 홍고추와 실파, 참기름을 넣고 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (54, 11, 1, '쇠고기는 삶아 건져 편육으로 썰고 국물은 식혀 기름을 걷어 육수로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (55, 11, 2, '동치미무는 길쭉하고 얇게 썰고 오이는 어슷썰어 소금에 20분 동안 절였다가 물기를 꼭 짜서 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (56, 11, 3, '배는 납작하게 썰고 계란은 삶아 반 가른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (57, 11, 4, '육수와 동치미국물을 섞어 소금과 설탕, 식초로 간을 맞춰 차게 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (58, 11, 5, '냉면국수는 삶아 찬물에 비벼 빨듯이 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (59, 11, 6, '대접에 면을 담고 편육과 무, 오이, 배, 계란을 얹은 후 육수를 부어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (60, 12, 1, '쇠고기는 채썰어 분량의 재료로 갖은양념하여 약간 재웠다가 팬에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (61, 12, 2, '오이는 가늘게 채썰어 소금에 절였다가 물기를 꼭 짜고 살짝 볶아 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (62, 12, 3, '동치미무는 반달모양으로 얄팍하게 썰어 깨소금과 참기름으로 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (63, 12, 4, '국수는 넉넉한 끓는 물에 삶아 찬물에 헹구어 건져 1인분씩 사리지어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (64, 12, 5, '그릇에 국수를 담고 고기채, 오이채, 동치미무, 계란지단채, 붉은 고추채를 얹어 동치미 국물을 가만히 붓는다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (65, 13, 1, '열무는 다듬어 소금에 절여둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (66, 13, 2, '냄비에 물 1컵과 찹쌀가루 2큰술을 넣고 나무주걱으로 저어가며 약불에서 찹쌀풀을 쑨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (67, 13, 3, '볼에 물 4컵과 고춧가루 4큰술을 풀고 찹쌀풀을 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (68, 13, 4, '③에 절인 열무와 어슷썬 고추, 채썬 파, 마늘, 생강을 넣고 버무려 열무김치를 담아 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (69, 13, 5, '알맞게 익은 열무김치에 식초, 설탕, 소금으로 간을 맞추어 차게 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (70, 13, 6, '냉면을 삶아 그릇에 담고 열무김치를 부은 다음 겨자 갠 것을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (71, 14, 1, '오이는 두께 0.3cm x 폭 0.3cm, 길이 5cm로 채썰어 소금에 절였다가 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (72, 14, 2, '쇠고기는 두께와 폭이 0.3cm, 길이 5cm로 썰어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (73, 14, 3, '표고도 물에 불려 쇠고기와 같은 크기로 채썰어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (74, 14, 4, '부추는 잘 씻어 5cm 길이로 잘라둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (75, 14, 5, '계란은 노른자와 흰자를 분리하여 지단을 붙이고 채썰어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (76, 14, 6, '번철에 오이, 표고, 쇠고기순으로 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (77, 15, 1, '고기는 저며서 녹말가루, 계란흰자, 청주, 소금으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (78, 15, 2, '해물은 한 입 크기로 손질해 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (79, 15, 3, '채소는 얄팍하게 저며썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (80, 15, 4, '팬에 기름을 둘러 다진 마늘을 볶다가 고기를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (81, 15, 5, '어느정도 익으면 멸치국물을 부어 끓이다가 해물과 채소를 넣고 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (82, 15, 6, '삶은 국수에 ⑤의 해물장국을 부어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (83, 16, 1, '김치는 소를 털고 송송 썰어 물기를 꼭 짜고 숙주는 삶아 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (84, 16, 2, '갈은 돼지고기나 갈은 쇠고기를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (85, 16, 3, '두부는 수분을 완전히 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (86, 16, 4, '양파, 마늘, 대파는 곱게 다져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (87, 16, 5, '김치, 숙주, 갈은 고기, 다진 양파, 마늘, 대파에 참기름과 후춧가루, 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (88, 16, 6, '밀가루 반죽을 하여 얇게 민다음 지름이 6cm 정도 되게 하여 그 안에 ⑤에서 만든 만둣속을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (89, 17, 1, '다시마는 두텁고 광택이 있는 것으로 물에 씻어 젖은 행주로 여러 번 닦은 뒤 찬물 5컵에 담가 30분 정도 불렸다가 건져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (90, 17, 2, '오이는 소금으로 비벼 씻어 헹궈 가늘게 채썬 뒤 찬물에 잠시 담갔다가 건져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (91, 17, 3, '홍고추는 반으로 갈라 씨를 털어 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (92, 17, 4, '손질한 다시마를 돌돌 말아 가늘게 채썰어 달라붙지 않게 털어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (93, 17, 5, '넓은 그릇에 다시마, 채썬 오이, 홍고추를 담고 다진 파와 마늘, 고춧가루, 깨소금, 소금으로 양념하여 차게 해둔 ①을 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (94, 18, 1, '쇠고기는 얄팍하게 썰어 다진 마늘과 후춧가루, 진간장으로 조물조물 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (95, 18, 2, '양념한 고기를 냄비에 넣고 육수를 부어 끓이다가 고추장을 연하게 풀고 국간장이나 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (96, 18, 3, '두부를 1cm 폭으로 길쭉하게 썰어 끓고 있는 국물에 쏟아 넣고 두부가 떠오를 때까지 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (97, 18, 4, '두부가 떠 오르면 채썬 파, 다진 마늘을 넣고 조금 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (98, 19, 1, '모시조개는 소금물로 해감시킨 후 물을 붓고 끓이다가 모시는 건져내고 국물은 걸러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (99, 19, 2, '콩나물은 다듬어 씻고 두부는 큼직하게 썰며, 붉은 고추는 어슷썰어 씨를 털고 실파는 4cm 길이로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (100, 19, 3, '북어포는 물에 씻어 물기를 짠 뒤 소금과 후춧가루로 양념한 뒤 밀가루를 묻혀 계란을 푼 물에 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (101, 19, 4, '조개 삶아낸 국물에 다진 마늘을 넣고 끓이다가 손질한 콩나물을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (102, 19, 5, '한소끔 끓으면 모시조개 두부, 붉은 고추, 실파를 넣고 계란물에 섞어 놓은 북어를 넣어 조금 더 끓이다가 소금, 후춧가루로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (103, 20, 1, '쇠고기는 2cm 길이로 도톰하게 저며썰고 무는 길이 3cm 정도씩 토막을 낸 뒤 반을 갈라 0.2cm 두께로 나박썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (104, 20, 2, '썰어놓은 쇠고기는 다진 마늘 1작은술과 국간장 1큰술, 후춧가루 약간으로 양념해 간이 골고루 배도록 조물조물 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (105, 20, 3, '냄비에 물 5컵을 붓고 팔팔 끓이다가 양념해 놓은 쇠고기를 넣고 고기가 익을 때까지 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (106, 20, 4, '끓는 쇠고기 장국에 나박썬 무를 넣는다. 끓을 때 생기는 거품은 걷어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (107, 20, 5, '무가 말갛게 익으면 국간장과 소금을 1:1의 비율로 넣어 간을 하고 다진 마늘과 실파를 넣어 조금 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (108, 21, 1, '찬물에 씻어 담가 충분히 불려 물기를 뺀 뒤 손으로 적당하게 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (109, 21, 2, '쇠고기는 연한 살코기만을 골라 곱게 다져 다진 마늘, 국간장, 참기름을 넣고 간이 배도록 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (110, 21, 3, '냄비에 식용유를 두르고 양념한 쇠고기를 넣어 볶다가 고기가 익으면 손질한 미역을 넣고 같이 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (111, 21, 4, '미역이 푸른색을 띠게 볶아지면 분량의 물을 붓고 한소끔 끓인 뒤 다진 마늘을 넣고 국간장, 후춧가루로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (112, 22, 1, '미역은 줄기를 떼어 티를 골라낸 다음 찬물에 담가30분 정도 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (113, 22, 2, '불린 미역을 깨끗이 씻어 손으로 잘게 찢은 다음 끓는 물에 재빨리 데쳐 물기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (114, 22, 3, '오이는 소금에 비벼 씻어 가늘게 채썰어 찬물에 잠깐 담가 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (115, 22, 4, '대파 흰부분만 다듬어 씻은 다음 반으로 갈라 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (116, 22, 5, '큰 그릇에 데친 미역을 담고 분량의 양념 재료를 넣어 무쳐준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (117, 22, 6, '양념한 미역에 맛이 배어들면 차갑게 준비한 미역 우린물을 붓고 채썬 오이와 대파를 넣고 차게 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (118, 23, 1, '생태는 씻어 내장을 빼고 비늘을 긁은 뒤 다시 씻어 5cm 길이로 토막내어 소금을 살짝 뿌려둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (119, 23, 2, '콩나물은 뿌리를 떼고 무는 0.5cm 두께로 얄팍하게 나박썬다. 붉은 고추는 어슷썰어 씨를 털고 미나리는 줄기만 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (120, 23, 3, '마지막에 씻은 쌀뜨물을 받아 냄비에 붓고 센불에서 끓이다가 손질한 생태를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (121, 23, 4, '국물이 끓어 생태가 익으면 무와 콩나물을 넣고 뚜껑을 덮은채 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (122, 23, 5, '거의 끓었을 때 붉은 고추와 다진 마늘, 미나리를 넣고 소금과 후춧가루로 간 한 뒤 더 끓이다가 미나리가 살짝 숨이 죽으면 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (123, 24, 1, '[연어준비] 신선한 연어는 깨끗하게 포를 3장 떠놓고 포 위에 레몬즙을 발라 놓는다.(미리 발라두면 색이 변하므로 페스토 소스바르기 직전에 한다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (124, 24, 2, '양파는 잘게 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (125, 24, 3, '양송이는 3mm 두께로 슬라이스 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (126, 24, 4, '차이브는 물에 담궈 살려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (127, 24, 5, '[페스토소스 만들기] 바질, 이태리파슬리, 잣, 엔초비, 마늘을 믹서에 넣고 올리브오일를 넣어 가며 살짝만 갈아 꺼내고, 올리브오일와 파르메쟌을 넣어 농도와 맛을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (128, 24, 6, '[양송이 샐러드 만들기] 양송이는 5mm두께로 썰어 다진 양파, 레몬주스, 올리브오일, 소금, 후춧가루로 맛을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (129, 25, 1, '오이는 굵은 소금으로 박박 문질러 씻은 뒤 채썰거나 얇게 통썰기 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (130, 25, 2, '손질한 오이에 다진 파와 마늘, 고춧가루, 식초를 넣어 골고루 무쳐놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (131, 25, 3, '물 4컵을 끓였다 식힌 뒤 설탕 1큰술과 간장, 식초 2큰술씩, 소금 약간을 넣고 나머지 6컵의 생수를 넣어 냉국물을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (132, 25, 4, '상에 낼 때에는 양념해 놓은 오이에 차갑게 식힌 냉국물을 붓고 깨소금을 살짝 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (133, 26, 1, '[채소 준비] 양파, 샐러리, 당근을 채썰고 트레비소와 루콜라는 먹기좋은 크기고 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (134, 26, 2, '바질과 이태리파슬리를 다져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (135, 26, 3, '[해산물 준비] 모든 해산물은 깨끗이 씻어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (136, 26, 4, '냄비에 물을 붓고 적당량의 소금을 넣고 끓인다. 물이 끓으면 해산물을 넣고 살짝 데쳐 꺼내놓는다. 채썰어 놓은 채소도 함께 살짝 데쳐 내어 체에 밭쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (137, 26, 5, '익은 해산물은 알맞은 크기로 잘라놓고, 채소와 함께 버무려 냉장고에 차갑게 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (138, 26, 6, '[소스 만들기] 믹싱볼에 올리브오일, 레몬즙, 백포도주식초, 다진 바질, 다진 파슬리, 소금, 후춧가루를 넣고 잘 혼합하여 소스를 완성한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (139, 27, 1, '재첩은 맹물에 한나절 담가 해감시킨 뒤 깨끗이 문질러 씻어 소쿠리에 건져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (140, 27, 2, '냄비에 물 4컵을 붓고 재첩을 넣어 입이 벌어지도록 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (141, 27, 3, '국물이 끓으면 4cm 길이로 부추를 썰어 넣고 소금으로 간을 하여 잠깐 더 끓인 후 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (142, 28, 1, '신선한 도미는 비늘을 제거하고 3장뜨기 하여 준비한다. 감자는 야주 얇게 둥글게 썰어서 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (143, 28, 2, '방울토마토는 반을 잘라둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (144, 28, 3, '오븐용 종이를 바닥에 깔고 가운데에 올리브오일을 브러쉬를 이용해 잘 바른 후 슬라이스한 감자를 겹겹이 붙여 원형으로 돌려 깔고 위에 소금을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (145, 28, 4, '도미를 올리고 다진 향초를 뿌리고 방울토마토를 생선의 가장자리 부분에 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (146, 28, 5, '녹색과 검은색 올리브, 바질을 올리고 생선살 위에 와인을 조금 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (147, 28, 6, '종이 가장자리에 노른자를 바르고 조금씩 접어 덮고 종이 양쪽은 사탕모양으로 뭉쳐 호일로 끈을 만들어 묶는다. (종이위에 "ㅍ" 모양으로 노른자를 바른다. ㅍ모양의 한가운데 내용물이 들어가는것)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (148, 29, 1, '쇠고기는 적간으로 도톰하게 떠서 두드리듯 앞 뒤로 잔 칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (149, 29, 2, '분량의 갖은 재료를 섞어 고기를 넣고 무쳐 30분 정도 재워 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (150, 29, 3, '느타리버섯은 끓는물에 데쳐 식혀서 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (151, 29, 4, '실파는 6cm 길이로 자르고 느타라버섯은 잘게 찢어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (152, 29, 5, '햇살담은간장, 파, 마늘, 참기름, 후춧가루로 버섯을 간이 배도록 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (153, 29, 6, '꼬치에 쇠고기, 실파, 느타리버섯을 순서대로 꿰고 마지막엔 쇠고기를 한번 더 꿰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (154, 30, 1, '쇠고기는 잔칼집을 넣어 한 입 크기로 도톰하게 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (155, 30, 2, '피망은 한 입 크기로 네모 썰고 양송이는 모양대로 도톰하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (156, 30, 3, '양파는 피망과 같은 크기로 네모 썰고 마늘은 얇게 편썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (157, 30, 4, '기름 두른 프라이팬에 쇠고기, 양파, 마늘을 볶다가 분량의 양념 재료를 넣고 끓으면 피망, 양송이를 넣고 숨이 죽으면 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (158, 30, 5, '소금과 후춧가루를 제외한 분량의 양념재료를 넣어 약불에 졸여 농도가 걸죽해지면 소금과 후춧가루로 간을 해 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (159, 31, 1, '붉은 색을 띠는 팥을 준비하여 깨끗이 씻어 돌이 없게 일어 놓았다가 냄비에 담고 물을 넉넉히 부어 삶는다. 한참을 삶아 팥이 푹 물러지면 믹서에 곱게 갈아 체에 걸러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (160, 31, 2, '분량의 밀가루와 날콩가루에 소금을 조금 넣고 물 1컵을 조금씩 부어가며 반죽하여 잘 치댄다. 골고루 반죽이 되면 비닐 봉지에 넣고 30분 정도 그대로 두어 끈기가 생기게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (161, 31, 3, '밀가루 반죽에 끈기가 생기면 좀 더 치댄 뒤 편평한 상에 밀가루를 바르고 방망이로 얇게 밀어 0.3cm 두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (162, 31, 4, '체에 걸러 놓은 팥국물을 냄비에 담아 다시 끓인다. 팥물이 끓으면 국수를 넣고 서로 붙지 않게 나무젓가락으로 저어 가며 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (163, 31, 5, '팥국물이 끓어 국수가 떠오르면 소금으로 간을 하고 불에서 내려 그릇에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (164, 32, 1, '죽순은 빗살모양을 살려 얇게 저며 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (165, 32, 2, '불린 표고는 기둥을 뗀 후 2, 3등분하여 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (166, 32, 3, '양념장은 간장 1큰술에 다진 파, 마늘, 설탕, 소금, 후춧가루, 참기름을 섞어 만들어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (167, 32, 4, '준비한 죽순과 표고를 그릇에 담고 양념장을 넣어 간이 배도록 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (168, 32, 5, '달군 팬에 기름을 두르고 센불에 ④를 넣고 볶다가 마지막으로 파를 넣고 살짝 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (169, 33, 1, '돼지고기는 살만 골라 4cm 길이로 얇게 채썬다. 볼에 넣고 청주와 간장으로 밑간해 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (170, 33, 2, '부추는 씻어 물기를 빼 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (171, 33, 3, '표고는 잘 불려서 짜 얇게 채썬다. 굵은 파는 어슷썰고 생강은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (172, 33, 4, '달군 팬에 기름을 두르고 생강과 파를 볶다가 저민 돼지고기를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (173, 33, 5, '고기가 익으면 남은 재료를 모두 넣어 볶다가 반쯤 익었을 때 간장으로 다시 한번 볶는다. 채소는 너무 숨이 죽지 않도록 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (174, 34, 1, '야채는 깨끗이 씻어 손질한 후 찬물에 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (175, 34, 2, '두부는 흐르는 물에 한번 헹군 후 물기를 살짝 제거하고 분량의 양념 재료를 넣어 믹서에 곱게 갈아 두부드레싱을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (176, 34, 3, '당근은 가로로 얇게 썰고, 파프리카는 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (177, 34, 4, '③을 제외한 야채는 물기를 제거하고 적당하게 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (178, 34, 5, '넓은 접시에 야채를 올리고 두부드레싱을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (179, 35, 1, '콩나물은 줄기가 희고 통통한 것으로 준비하여 뿌리를 다듬어 씻어 물기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (180, 35, 2, '냄비에 콩나물을 담고 물을 넣어 뚜껑을 덮고 데친 다음 콩나물을 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (181, 35, 3, '②의 콩나물을 참기름으로 먼저 버무린 후 다진 파, 다진 마늘, 실고추, 깨소금, 후춧가루, 소금을 넣어 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (182, 36, 1, '우엉은 칼등으로 가볍게 긁어 껍질을 벗겨 물에 행군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (183, 36, 2, '긴 것은 4cm 길이로 토막내 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (184, 36, 3, '끓는 물에 소금을 넣고 살짝 데쳐 검은 물을 빠지게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (185, 36, 4, '데친 우엉을 냄비에 담고 간장과 물을 붓고 조린다. 조리시 센불에서 서서히 줄여 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (186, 36, 5, '반쯤 조려졌을 때 물엿을 넣고 자작할 때까지 조려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (187, 36, 6, '그릇에 가지런히 담은 뒤 통깨를 솔솔 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (188, 37, 1, '바질은 찬물에 헹궈 물기를 제거해준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (189, 37, 2, '①의 분량의 드레싱재료와 함께 섞어 믹서에 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (190, 37, 3, '두부를 지금 6cm 원형 틀로 찍어 낸 다음 1cm 두께로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (191, 37, 4, '토마토는 윗부분과 아랫부분을 잘라내고 두부와 같이 1cm두께로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (192, 37, 5, '접시 위에 토마토를 놓고, 그 위에 두부, 다시 토마토 순으로 반복하여 쌓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (193, 37, 6, '②의 드레싱과 다진 양파를 섞어 접시 바닥에 뿌리고 두부와 토마토를 찍어 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (194, 38, 1, '잔 멸치는 체에 담아 살살 흔들어 잔부스러기 없이 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (195, 38, 2, '당근은 껍질을 벗겨 4cm 길이로 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (196, 38, 3, '청고추는 반으로 갈라 안을 긁어내고 4cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (197, 38, 4, '팬을 뜨겁게 달군 후 잔멸치, 당근, 청고추 순으로 넣어 센불에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (198, 38, 5, '간장, 설탕, 청주를 섞어 만든 조림장을 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (199, 38, 6, '간이 배면 통깨를 솔솔 뿌려 그릇에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (200, 39, 1, '갈치는 토막내 비늘을 벗기고 칼집을 넣어 준비하며, 대파와 홍고추는 어슷썰고, 마늘은 편썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (201, 39, 2, '무는 큼직하게 나박썰어 냄비 바닥에 깔아 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (202, 39, 3, '그릇에 분량의 양념 재료를 넣고 준비한 파와 마늘을 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (203, 39, 4, '냄비에 깔아 둔 무 위에 갈치를 얹고, 그 위에 ③의 양념장을 얹어 물을 자작하게  부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (204, 39, 5, '15분간 끓이다가 국물이 졸아들면 뚜껑을 열고 파와 고추를 넣은 후 참기름을 넣어 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (205, 40, 1, '닭은 깨끗이 씻어 먹기 좋은 크기로 8토막 내 뼈를 발라낸 다음 골고루 칼집을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (206, 40, 2, '넓은 그릇에 다진 파, 마늘, 간장, 고춧가루, 생강즙 등 분량의 양념장 재료를 모두 넣어 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (207, 40, 3, '닭고기에 양념장을 고루 버무려 30분 정도 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (208, 40, 4, '팬에 기름을 두르고 중불에서 너무 바삭해지지 않도록 적당히 익혀낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (209, 41, 1, '다시마는 물에 담가 불려서 정사각형으로 잘라 마른 행주로 닦아둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (210, 41, 2, '표고버섯은 더운물에 불려 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (211, 41, 3, '두부는 곱게 으깨어 물기를 꼭 짜둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (212, 41, 4, '당근과 풋고추도 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (213, 41, 5, '2,3,4를 혼합하고 들깨가루, 깨소금, 소금, 참기름, 후추를 넣어 양념을 하여 볶아 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (214, 41, 6, '1의 다시마에 4의 양념된 두부를 손가락 크기로 만들어 놓고 다시마를 세로로 놓아 김밥처럼 말아서 적당히 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (215, 42, 1, '팥은 찬물에 씻은 후 물에 담가 충분히 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (216, 42, 2, '칼국수면은 손으로 훌훌 털어 뭉침을 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (217, 42, 3, '냄비에 불린 팥을 담고 팥의 1.5배의 물과 소금을 약간 넣고 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (218, 42, 4, '끓기 시작하면 첫물은 따라 버리고 다시 찬물을 붓고 팥이 무를 정도로 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (219, 42, 5, '④의 팥 삶은 것을 체에 걸러 내려서 팥 껍질을 제거하고 팥앙금과 적정량의 물을 부어 끓인다.(물은 식성에 따라 가감한다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (220, 42, 6, '⑤의 팥물에 생면을 넣어 끓이다가 소금으로 간한다. 식성에 따라 설탕을 첨가해도 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (221, 43, 1, '깨끗이 다듬은 콩나물은 냄비에 안치고 소금을 조금 넣은 후 물 1컵을 붓고 삶는다. 7분 정도 지나면 체에 밭쳐 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (222, 43, 2, '물에 불린 표고와 쇠고기는 채썰어 갖은 양념(간장, 설탕, 다진 파, 마늘, 후춧가루, 깨소금) 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (223, 43, 3, '미나리는 다듬어 5cm 길이로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (224, 43, 4, '양파와 당근은 채썰어 소금에 절여놓고 계란은 풀어 지단을 부쳐 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (225, 43, 5, '당근, 미나리를 각각 볶아내고 양념해둔 표고와 쇠고기도 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (226, 43, 6, '삶은 콩나물에 참기름 1/2큰술과 소금으로 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (227, 44, 1, '양배추는 한장씩 뜯어 끓는 소금물에 살짝 데친 다음 찬물에 헹궈 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (228, 44, 2, '밤은 속껍질까지 벗겨 찬물에 잠시 담갔다가 끓는물에 반쯤 익을 정도로 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (229, 44, 3, '쇠고기와 양파는 다져서 그릇에 담고 양념장을 넣어 간이 배도록 버무린다. (양념장 : 간장, 깨소금, 청주, 다진 파, 마늘, 참기름, 후춧가루, 소금)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (230, 44, 4, '삶은 양배추를 넓게 펼쳐 고기를 적당히 담고 밤 한 개를 다진 고기속에 넣어 동그랗게 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (231, 44, 5, '동그랗게 만 양배추말이를 뭉개지지 않을 정도로 실로 묶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (232, 44, 6, '냄비에 양배추말이를 안치고 육수를 자작할 정도로 부어 10분 정도 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (233, 45, 1, '마늘은 단단하고 고른것으로 구입하여 물에 충분히 불렸다가 속 껍질까지 벗겨 물기를 완전히 닦아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (234, 45, 2, '마늘을 그릇에 담고 물 10컵에 소금 1컵을 섞은 소금물을 부어 1주일 정도 숙성시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (235, 45, 3, '분량의 장아찌간장을 팔팔 끓여 식힌 후 삭힌 마늘에 붓고 1주일 정도 놓아둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (236, 45, 4, '마늘이 잘 삭았으면 장물만 따라내어 팔팔 끓인 다음 식혀서 다시 마늘에 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (237, 46, 1, '오징어는 내장을 빼고 껍질을 벗겨 한장으로 편 뒤 가로 세로 사선으로 칼집을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (238, 46, 2, '분량의 재료를 모두 섞어 넓은 그릇에 담고 손질한 오징어를 버무려 15분간 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (239, 46, 3, '팬에 기름을 두르고 고추장양념장에 재운 오징어를 놓고 센불에서 뒤집으면서 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (240, 46, 4, '구운 오징어를 둥글게 말아 한 입에 먹을 수 있도록 1cm 정도로 보기좋게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (241, 46, 5, '상에 낼때는 몸통모양으로 돌려 담아 먹음직스럽게 모양을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (242, 47, 1, '대구살은 포를 떠 넓적하게 썬 다음 소금과 생강즙을 조금 뿌려 밑간이 고루 배도록 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (243, 47, 2, '깨끗이 손질한 양파, 피망, 당근은 먹기 좋은 크기로 썰고 불려 둔 목이버섯은 손으로 잘게 뜯는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (244, 47, 3, ' 통조림 통에서 파인애플 한 조각을 꺼내어 마른 거즈에 잘 닦아낸 다음 8등분하여 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (245, 47, 4, '준비한 생선에 청주, 계란 흰자, 녹말가루를 넣고 섞어 튀김옷을 골고루 입힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (246, 47, 5, '170℃ 정도의 끓는 기름에 튀김옷을 입힌 생선살을 넣고 바삭바삭하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (247, 47, 6, '냄비에 육수를 붓고 끓으면 식초, 설탕, 저민 마늘, 양파, 당근을 넣어 끓이다가 토마토케첩으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (248, 48, 1, '갈비는 뼈가 얇고 4.5cm 정도로 토막 낸 것으로 골라 핏물을 우려 낸 후 깨끗이 씻어 물기를 없앤다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (249, 48, 2, '갈비의 기름을 떼고 갈빗대의 중심으로 앞뒤로 뒤집어 가며 양편의 살을 저미면서 얇게 편다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (250, 48, 3, '손질한 갈비는 배즙과 설탕, 청주를 넣어 1시간 정도 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (251, 48, 4, '냄비에 간장, 육수, 굵은 파, 양파, 마늘, 월계수잎, 생강을 넣고 재료가 무를 때까지 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (252, 48, 5, '충분히 끓여 둔 양념장은 체에 걸러 따라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (253, 48, 6, '걸러 둔 양념장 5큰술에 양파즙, 물엿, 통깨, 후춧가루, 참기름을 넣고 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (254, 49, 1, '돼지고기는 0.5cm 두께로 둥글고 두툼하게 썰어 칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (255, 49, 2, '고기를 접시에 담고 소금과 후춧가루로 밑간하고 밀가루, 계란, 빵가루 순서대로 튀김옷을 입힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (256, 49, 3, '160℃의 기름에 고기를 넣어 앞뒤가 노릇해지도록 튀겨 기름을 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (257, 49, 4, '쇠고기는 잘게 다지고 당근, 샐러리, 마늘은 얇게 저며 썰고 양파는 가늘게 채썰어 볶다가 토마토케첩에 레드와인을 섞어 월계수잎과 육수를 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (258, 49, 5, '다른 냄비에 버터를 녹인 다음 밀가루를 넣어 나무주걱으로 저으면서 갈색이 나도록 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (259, 49, 6, '④의 소스가 끓으면 ⑤의 브라운루를 거품기로 잘 저어 풀어준 후 넣고 약한 불에서 뭉근히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (260, 50, 1, '갈비는 토막내어 기름기를 제거하고 찬물에 30분 정도 담가 핏물을 빼서 준비하고, 냄비에 갈비가 잠길 정도로 물을 부어 대파를 흰 뿌리째 넣고 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (261, 50, 2, '삶은 갈비를 식혀 고루 칼집을 넣어두고 삶아낸 물은 육수로 쓴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (262, 50, 3, '당근과 무는 밤톨 만하게 썰어 가장자리가 둥글게 모양 내고, 밤은 껍질을 깨끗이 벗겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (263, 50, 4, '은행은 달구어진 프라이팬에 기름을 두르고 살짝 볶아낸 후 거즈로 닦아 껍질을 벗겨내고, 표고버섯은 채썰며 계란은 흰자와 노른자를 분리하여 지단을 부쳐 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (264, 50, 5, '배는 강판에 갈아서 준비해두고, 배를 비롯한 분량의 양념 재료를 모두 넣고 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (265, 50, 6, '냄비에 삶아둔 갈비를 안치고 ⑤의 양념장을 1/2 분량만 끼얹은 후 ②에서 나온 육수 1컵을 부어 끓이다가 갈비가 익고 국물이 졸아들면 남은 양념장을 넣고 준비해둔 밤, 무, 당근, 표고버섯을 넣고 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (266, 51, 1, '돼지고기는 목살로 덩어리째 준비해 실로 돌려가며 묶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (267, 51, 2, '냄비에 물을 붓고 된장을 푼 다음 저민 생강을 넣어 끓이다가 돼지고기를 넣어 무르도록 푹 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (268, 51, 3, '다 삶아지면 실을 풀고 꼬치로 찔러보아 피가 묻어나지 않으면 된다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (269, 51, 4, '고기를 베보자기나 거즈로 싼 다음 꾹꾹 눌러준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (270, 51, 5, '고기가 식으면 먹기좋게 0.3cm 두께로 납작하게 저며 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (271, 51, 6, '굴은 묽은 소금물에 살살 흔들어 씻은 다음 티를 골라내고 깨끗한 물에 헹궈 체에 밭친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (272, 52, 1, '홍합은 신선한 것으로 골라 냄비에 넣고 물, 청주, 파, 소금을 넣어 삶아 건진다. 삶은 홍합은 살만 발라서 수염을 자르고 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (273, 52, 2, '냄비에 고추장, 간장, 물엿, 청주, 마늘즙, 생강즙, 깨소금, 참기름을 분량대로 넣어 센 불에서 윤기나게 끓여 양념장을 만들어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (274, 52, 3, '삶은 홍합을 꼬치에 5개식 차례로 한쪽에 꿴다. 크기가 같은 것끼리 꿰야 보기가 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (275, 52, 4, '꼬치에 꿴 홍합의 앞 뒤에 준비한 양념장을 골고루 발라 10분 정도 재워 간이 배게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (276, 52, 5, '양념한 홍합을 250℃로 15분간 예열한 오븐에 5분 정도 굽다가 뒤집어 3분간 더 구운 뒤 파슬리를 뿌려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (277, 53, 1, '쇠고기는 기름기 없는 것으로 얇게 저며 썰고 잘 익은 배추김치는 속을 털어내고 먹기 좋게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (278, 53, 2, '대파는 어슷썰고 무와 두부는 납작하게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (279, 53, 3, '냄비에 쇠고기, 김치, 무를 넣고 김치국물을 조금 넣어 볶다가 반 정도 익으면 물 4컵을 부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (280, 53, 4, '고기가 익으면 청국장을 풀고 끓을 때 다진 마늘을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (281, 53, 5, '③을 뚝배기에 옮겨 담고 한소끔 끓이다가 대파와 두부를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (282, 53, 6, '상에 내기 직전에 소금으로 간을 맞춘 뒤 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (283, 54, 1, '바지락은 소금물에 담가 해감한다. 해감한 조개는 냄비에 물을 붓고 끓여 입이 벌어지면 조개는 건져내고 국물은 체에 걸러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (284, 54, 2, '돼지고기는 기름을 떼서 저며썰고 배추김치는 속을 털어내고 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (285, 54, 3, '양파는 반으로 갈라 채썰고 굵은 파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (286, 54, 4, '냄비에 기름을 두르고 김치, 돼지고기, 양파, 다진 마늘, 고춧가루를 넣어 볶다가 조갯국물을 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (287, 54, 5, '돼지고기와 김치가 익으면 뚝배기에 옮겨 담고 숟가락으로 순두부를 뚝뚝 떠 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (288, 54, 6, '바지락과 굵은 파를 넣고 한소끔 끓으면 소금, 후춧가루를 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (289, 55, 1, '동태는 비늘을 긁고 내장을 제거한 후 5cm 길이로 토막 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (290, 55, 2, '무는 0.5cm 두께로 나박썰고 애호박은 도톰하게 반달썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (291, 55, 3, '청고추, 홍고추, 대파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (292, 55, 4, '두부는 1cm x 4cm x 3cm 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (293, 55, 5, '냄비에 물 또는 육수를 붓고 고추장을 풀어 끓으면 무와 동태를 넣은 뒤 남은 재료를 모두 넣고 끓이면서 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (294, 56, 1, '잘 익은 배추김치는 속을 털고 5cm 길이로 썬다. 너무 신 김치는 물에 한번 헹궈 사용한다. 설탕을 1/2 큰술 넣으면 신 맛이 줄어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (295, 56, 2, '돼지고기는 기름이 약간 섞인 것으로 준비해 한 입 크기로 납작하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (296, 56, 3, '가래떡은 씻어 건져 물기를 빼 놓고 굵은 파는 반으로 갈라 5cm 길이로 쭉쭉 찢어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (297, 56, 4, '분량의 고춧가루에 다진 마늘, 생강, 청주를 넣고 고루 섞어 양념 고춧가루를 만들어 놓은 뒤 30분정도 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (298, 56, 5, '준비한 김치를 먼저 안치고 양념 고춧가루를 고루 끼얹은 다음 납작 썬 돼지고기, 굵은 파, 다진 마늘, 참기름을 넣고 육수 4컵을 부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (299, 56, 6, '센 불에 끓이면서 김치가 어느정도 물러 지면 준비한 가래떡을 함께 넣고 중불에서 서서히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (300, 57, 1, '깨끗이 씻은 콩은 물에 담가 하룻밤 정도 불려서 4배로 불려지면 손으로 비벼 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (301, 57, 2, '통통하게 불린 콩을 믹서에 넣고 물 3컵을 조금씩 부어가며 되직할 정도로 갈아 콩비지를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (302, 57, 3, '살코기로 준비한 돼지고기는 한 입 크기로 얄팍하게 납작썰기하고 배춧잎은 데쳐서 1cm 정도로 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (303, 57, 4, '다진 파, 마늘, 참기름으로 돼지고기와 배춧잎을 양념한 다음 냄비에 기름을 두르고 함께 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (304, 57, 5, '돼지고기가 익으면 콩비지를 넣고 서서히 끓이다가 끓어오르면 소금으로 간을 맞추어 한소끔 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (305, 57, 6, '간장 3큰술에 다진 파, 마늘, 고춧가루, 참기름, 깨소금을 넣고 고루 섞어서 콩비지찌개에 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (306, 58, 1, '잘 마른 황태를 골라 방망이로 두드려 부드러워지면 물에 불렸다가 잘 다듬어 5cm 길이로 자른 뒤 냄비에 물 4컵을 붓고 끓여 북어는 건져서 손으로 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (307, 58, 2, '콩나물은 다듬어 씻고 두부는 4cm 길이로 길게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (308, 58, 3, '홍고추와 굵은 파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (309, 58, 4, '찢어놓은 북어를 그릇에 담아 소금과 참기름을 넣고 양념한 후 계란 2개를 넣어 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (310, 58, 5, '북어국물에 콩나물과 홍고추를 넣고 끓이다가 계란물에 섞은 북어를 한 숟가락씩 넣고 소금, 후춧가루로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (311, 58, 6, '북어가 떠오르면 두부와 파, 다진 마늘을 넣고 살짝 끓인 후 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (312, 59, 1, '유부는 기름을 떼고 어슷하게 반으로 갈라 끓는물에 데쳐 찬물에 헹궈 부드럽게 해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (313, 59, 2, '준비한 우엉, 당근은 깨끗이 씻어 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (314, 59, 3, '분량의 재료를 넣어 만든 조림장을 끓이다가 다진 우엉, 당근, 쇠고기를 넣고 국물이 없어질 때 까지 은근하게 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (315, 59, 4, '채소와 고기가 어느정도 조려지면 마지막에 유부를 뒤집어 넣고 뒤적여 맛을 들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (316, 59, 5, '고슬고슬하게 지은 밥에 조린 우엉, 당근, 고기를 넣어 골고루 잘 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (317, 59, 6, '간이 밴 유부를 왼손에 뒤집어 잡고 오른손으로 밥을 꼭꼭 눌러 유부 안으로 넣은 다음 오므린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (318, 60, 1, '쌀과 물의 양을 1:1 비율로 잡고 다시마 1장과 청주, 소금을 넣어 밥을 짓고, 뜨거울 때 넓은 그릇에 담아 식초, 소금, 설탕을 섞어 만든 배합초를 고루 뿌려가며 섞으면서 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (319, 60, 2, '쇠고기는 곱게 갈아 간장, 설탕, 후춧가루로 간하여 프라이팬에 기름을 두르고 볶아두고, 잔멸치는 기름 두른 뜨거운 프라이팬에 한번 볶은 후 설탕과 간장을 1:2의 비율로 넣고 살짝 조려 물엿을 섞어 맛을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (320, 60, 3, '표고버섯은 미지근한 물에 불려 기둥을 떼고 물기를 꼭 짠 후 채썰어서 간장, 참기름, 설탕, 후춧가루로 양념하여 프라이팬에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (321, 60, 4, '단무지는 곱게 채썰어 물기를 뺀 후 식초, 설탕, 소금을 넣고 버무린 다음 고춧가루로 살짝 물들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (322, 60, 5, '김치는 익은 것으로 하되 식성에 따라 행궈서 사용해도 좋다. 곱게 채썰어 꼭 짜서 깨소금, 참기름으로 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (323, 60, 6, '다섯가지 재료가 완성되면 준비한 밥을 다섯 등분으로 나누어 속재료를 한가지씩 묻혀서 갖가지 모양을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (324, 61, 1, '해파리는 먼저 소금물에서 3시간 정도 우려서 짠맛을 뺀 뒤 냉수에 헹궈 50℃ 정도의 따뜻한 물에 담근다. 해파리가 쪼글쪼글해지면 다시 물을 갈아 담갔다가 소쿠리에 건져 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (325, 61, 2, '넓고 큰 것은 가로 세로 10cm 정도로 잘라 둥글게 말아 가늘게 채썬다. 찬물에 헹궈내어 물기를 꼭 짜서 냉장고에 차게 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (326, 61, 3, '오이는 굵은 소금으로 문질러 씻은 후 가늘게 채썰어 찬물에 10분 정도 담갔다 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (327, 61, 4, '게맛살은 채썬 해파리와 같은 굵기로 결대로 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (328, 61, 5, '곁들일 토마토는 단면으로 약간 도톰하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (329, 61, 6, '소스는 다진 마늘에 분량의 식초, 육수, 설탕, 간장, 소금, 참기름을 넣고 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (330, 62, 1, '배추는 고를 때 속이 꽉차고 묵직한 것으로 골라 큰 것은 4등분 작은 것은 2등분한다. 농도가 15% 정도 된 소금물에 담갔다가 꺼낸 후 굵은소금을 뿌려 5~6시간동안 절여둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (331, 62, 2, '잘 절여진 배추는 여러 개의 양푼에 물을 담아 순서대로 흔들어 씻은 후 물기를 빼고 심을 잘라 정리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (332, 62, 3, '미지근한 물 1컵에 고춧가루를 넣고 잘 섞어 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (333, 62, 4, '무, 배는 4~5cm 길이로 채썰고 미나리, 실파는 4cm 길이로 썬다. 굵은 파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (334, 62, 5, '깨끗이 씻어 껍질을 벗긴 마늘과 생강을 절구에 조금씩 넣어 가면서 곱게 찧는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (335, 62, 6, '불린 고춧가루에 갖은 양념을 넣어 버무리고 준비한 채소를 전부 넣어 속을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (336, 63, 1, '열무와 무는 흐르는 물에 깨끗이 씻어 다듬어 4~5cm 길이로 썬다. 큰 그릇에 열무와 풋배추를 섞이지 않게 넣고 소금을 뿌려 1시간 정도 절인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (337, 63, 2, '실파는 4cm 길이로 썰고 청고추는 어슷썰고 씨를 대충 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (338, 63, 3, '냄비에 10컵의 물을 붓고 밀가루 2큰술을 넣어 중불에서 풀을 쑤어 식혀 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (339, 63, 4, '홍고추는 물에 불려 반으로 가르거나 어슷썰어 씨를 털어내고 분마기에 조금씩 넣어 방망이로 곱게 갈아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (340, 63, 5, '식힌 밀가루풀에 분량의 물을 붓고 곱게 간 홍고추, 다진 마늘, 생강, 청고추를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (341, 63, 6, '국물을 준비한 뒤 싱거우면 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (342, 64, 1, '오이는 굵은 소금으로 문질러 씻어 5cm 길이로 잘라 십자로 칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (343, 64, 2, '오이가 잠길 정도의 물에 굵은 소금을 풀어 소금물을 만들어 오이를 담가 30분 정도 절인 후 마른 행주로 물기를 닦는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (344, 64, 3, '부추는 씻어 가지런히 잡고 잘게 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (345, 64, 4, '넓은 그릇에 분량의 고춧가루를 담고 물을 조금씩 부어가며 되직하게 개어 다진 파, 다진 마늘, 다진 생강과 송송 썬 부추를 넣고 소금과 설탕으로 간을 맞춰 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (346, 64, 5, '오이의 칼집이 벌어지게 한 후 ④의 소를 빠지지 않게 채운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (347, 64, 6, '준비해둔 통에 오이를 길이대로 눕혀 차곡차곡 담고 남은 소를 위에 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (348, 65, 1, '무는 나박썰기하고 배추는 속대만 골라 무와 같은 크기로 썰고 따로 소금에 절인다. 30분 정도 절인 뒤 한 번 뒤집어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (349, 65, 2, '미나리는 씻어 3cm 길이로 자르고 홍고추는 반으로 갈라 씨를 털고 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (350, 65, 3, '굵은 파, 생강, 마늘도 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (351, 65, 4, '큰 그릇에 물 20컵을 붓고 고춧가루 1/3컵을 거즈에 싸서 물에 담가 흔들어 고춧가루 물을 만든 뒤 소금간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (352, 65, 5, '절여진 배추와 무에 채썬 파, 마늘, 생강, 홍고추, 미나리를 섞어 고루 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (353, 65, 6, '잘 버무려진 나박김치를 항아리에 먼저 담고 삼삼하게 간을 한 김칫국을 부어 숙성시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (354, 66, 1, '총각무는 무청이 파랗고 심이 없는 것으로 골라 잔뿌리는 떼고 무청이 달린 부분은 칼로 다듬어 씻어 건진 후 무가 크면 길게 2등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (355, 66, 2, '큰 그릇에 총각무를 켜켜이 담아 소금물을 붓고 표면이 덮이도록 소금을 뿌려 3시간 정도 절인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (356, 66, 3, '대파는 깨끗이 손질해 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (357, 66, 4, '물1컵에 찹쌀가루 3큰술을 풀어 거품기로 저어주면서 중간 불에서 찹쌀풀을 쑤어 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (358, 66, 5, '멸치젓을 냄비에 담고 센불에서 살이 빠지고 뼈만 남을 때까지 끓여 체에 밭쳐 국물을 거르고, 식힌 찹쌀풀에 고춧가루, 대파, 다진 마늘, 다진 생강, 멸치젓국, 새우젓, 설탕을 고루 넣어 양념을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (359, 66, 6, '절여둔 총각무에 양념을 넣어 잘 버무린 다음 2-3개씩 모아 하나로 묶어서 항아리에 차곡차곡 담아 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (360, 67, 1, '무는 깨끗이 씻어 물기를 닦고 1.5cm 두께로 토막내어 가로 세로 1.5cm크기로 고르게 깍뚝썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (361, 67, 2, '실파, 미나리, 갓은 다듬어 씻어 3~4cm 길이로 똑같이 맞추어 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (362, 67, 3, '굴은 삼삼한 소금물에 담가 살살 흔들어 씻어 소쿠리에 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (363, 67, 4, '새우젓은 잡티를 골라 새우젓 건더기만 건져내어 도마 위에 놓고 곱게 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (364, 67, 5, '깍뚝썬 무는 우묵한 그릇에 담고 분량의 고춧가루를 넣어 고루 섞어 빨갛게 물들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (365, 67, 6, '깍두기에 고춧가루물이 골고루 들었을 때 소금을 고루 넣고 간이 배도록 살짝 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (366, 68, 1, '무는 적당히 작은 것으로 골라 수세미로 가볍게 문질러 씻어 잔털을 정리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (367, 68, 2, '넓은 그릇에 소금을 깔고 무를 그 위에 굴려 항아리에 담아 하룻밤 재운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (368, 68, 3, '갓과 살파는 소금에 절여 헹구어 믈기를 뺀 후 실파는 3뿌리씩 갓은 1뿌리씩 묶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (369, 68, 4, '손질한 청고추는 항아리에 차곡차곡 담아 짭짤하게 소금물을 만들어 붓고 한달 정도 그늘에 두어 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (370, 68, 5, '마늘과 생강은 얄팍하게 저며썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (371, 68, 6, '배는 씻어 껍질을 벗기지 말고 반으로 자른다. 깨끗한 주머니에 저며 썬 마늘과 생강, 굵은 파뿌리를 넣고 배를 넣어 잘 아물린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (372, 69, 1, '갓은 겉잎을 잘라내고 큰 그릇에 물을 넉넉히 담고 흔들어 여러 번 헹구어 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (373, 69, 2, '갓을 가지런히 담고 소금을 골고루 뿌려 1시간 정도 절인 뒤 30분 정도 지나면 한번 뒤집는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (374, 69, 3, '마지막 헹굴때 갓이 무르지 않도록 물에 소금을 조금 넣어 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (375, 69, 4, '무는 다듬어 가늘게 채썰어 그릇에 담고 소금을 뿌려 30분 정도 절였다가 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (376, 69, 5, '멸치젓은 달이지 않고 국물만 체에 걸러 사용한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (377, 69, 6, '물 1컵에 찹쌀가루 3큰술을 넣고 중불에서 눌지 않도록 저으면서 되직하게 찹쌀풀을 쑨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (378, 70, 1, '꽃게는 솔로 문질러 씻어 등딱지를 떼고 모래주머니를 제거한다. 집게발은 떼서 끝을 다듬고, 몸통은 4토막으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (379, 70, 2, ' 애호박은 0.5cm 두께로 반달썰고, 무는 2cm x 2cm로 얇게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (380, 70, 3, '풋고추, 홍고추, 대파는 깨끗이 씻어 어슷썰고 느타리버섯은 흔들어 씻어 큰 것은 길이대로 적당히 찢어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (381, 70, 4, '냄비에 물 3컵을 붓고 체에 고추장을 걸러 푼 다음 썰어둔 무를 넣고 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (382, 70, 5, '무가 어느 정도 익으면 준비한 꽃게, 느타리버섯, 고추, 다진 마늘, 생강을 넣고 끓이다가 애호박, 대파, 고춧가루를 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (383, 70, 6, '재료가 다 익어 맛이 어우러지면 위에 떠오르는 거품을 걷어내고 국간장, 소금, 후춧가루로 간을 맞춰 살짝 끓여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (384, 71, 1, '돼지등뼈는 큼직하게 토막내 하룻 밤 정도 찬물에 담갔다가 핏물이 빠지면 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (385, 71, 2, '냄비에 물을 넉넉히 붓고 돼지등뼈를 넣어 끓이다가 도중에 물을 한 번 따라버리고 다시 물을 부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (386, 71, 3, '감자는 껍질을 벗긴 뒤 2등분하여 냉수에 담갔다가 건져 물기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (387, 71, 4, '굵은 파는 길이대로 길게 자르고 마늘, 생강은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (388, 71, 5, '냄비에 물을 넉넉히 붓고 손질해 놓은 감자를 넣어 삶는다. 너무 푹 익지 않았을 때 체에 밭쳐 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (389, 71, 6, '청정원 맛선생을 비롯한 분량의 양념장을 넣어 양념을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (390, 72, 1, '아구는 입끝에 독이 있으므로 입주위를 돌려가며 잘라내고 배를 갈라 내장을 뺀 다음 5cm 길이로 토막내어 열십자로 칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (391, 72, 2, '싱싱한 콩나물을 준비하여 머리와 뿌리를 제거해 준비하고, 미나리 줄기와 고사리는 콩나물과 같은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (392, 72, 3, '싸리버섯은 찢고 깻잎은 길게 2등분해 0.5cm로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (393, 72, 4, '물 4컵에 된장 4큰술을 체에 내려서 풀고 물 6컵을 더 넣어 끓인 후 물이 끓어 오르면 손질한 아구, 콩나물, 고사리를 넣어 한소끔 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (394, 72, 5, '④가 끓으면 싸리버섯, 고추, 대파, 다진 마늘, 고춧가루, 후춧가루를 넣어 양념한 후 한소끔 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (395, 72, 6, '불에서 내리기 직전에 미나리와 깻잎을 넣어 살짝 끓여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (396, 73, 1, '조기는 내장과 지느러미를 떼어내고 비늘을 긁어 손질한 뒤 통째로 씻어서 3~4토막으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (397, 73, 2, '무는 가로 세로 4cm로 나박썰고 양파는 굵게, 생강은 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (398, 73, 3, '굵은 파는 어슷썰고 마늘은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (399, 73, 4, '냄비에 물 4컵을 붓고 간장을 넣어 간한 뒤 고춧가루를 풀어 넣고 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (400, 73, 5, '국물이 팔팔 끓으면 무와 손질한 조기를 넣어 중불에서 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (401, 73, 6, '조기가 어느정도 익으면 굵은 파와 채썬 양파, 다진 마늘, 생강을 넣고 잠깐 더 끓여 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (402, 74, 1, '명란은 붉은 빛을 띠는 싱싱한 것으로 준비해 찬물에 살살 씻어 헹군 다음 체에 밭쳐 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (403, 74, 2, '쇠고기는 기름기 없는 연한 살코기만 골라 가늘게 채썬 후 간장, 다진 파, 다진 마늘, 참기름, 깨소금을 넣고 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (404, 74, 3, '무는 2cm x 2cm 크기로 납작썰고 콩나물은 뿌리를 다듬어 씻고, 대파, 홍고추는 어슷썰며 쑥갓과 대파는 씻어 5cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (405, 74, 4, '분량의 간장에 고춧가루, 다진 마늘, 생강즙, 청주, 소금, 후춧가루를 넣고 골고루 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (406, 74, 5, '뚝배기를 뜨겁게 달구어 밑간한 쇠고기와 납작썬 무를 넣고 볶다가 어느 정도 익으면 물 4컵을 붓고 양념장을 고루 풀어 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (407, 74, 6, '국물에 명란과 콩나물을 넣고 끓이다가 미나리, 쑥갓, 고추, 대파를 넣고 조금 더 끓여 향을 돋군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (408, 75, 1, '우거지는 깨끗이 다듬어 끓는 물에 푹 삶은 뒤 찬물에 담가 냄새를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (409, 75, 2, '쇠고기는 기름기가 많은 것으로 골라 먹기 좋은 크기로 얄팍하게 저며 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (410, 75, 3, '청고추와 홍고추는 꼭지를 딴 뒤 송송 썰어 씨와 함께 다져놓는다. 파는 어슷썰고 마늘과 생강은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (411, 75, 4, '그릇에 물기를 뺀 ①을 넣고 된장 4큰술과 다진 마늘, 식용유 1큰술을 넣어 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (412, 75, 5, '냄비에 기름을 두르고 쇠고기를 넣어 볶다가 무쳐놓은 우거지를 넣고 함께 볶은 후 쌀뜨물을 붓고 약불에서 은근히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (413, 75, 6, '찌개가 끓으면 다진 청고추와 홍고추, 어슷썬 파를 넣고 간장으로 심심하게 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (414, 76, 1, '쇠고기는 샤브샤브용으로 얇게 썰은 것으로 준비하고 접시에 보기 좋게 펼쳐 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (415, 76, 2, '배추는 속대로 준비하여 깨끗이 씻은 뒤 4cm 길이로 썬다. 당근과 무는 한 입 크기로 나박썰며 양파는 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (416, 76, 3, '곤약은 4cm 길이로 납작하게 썰고 어묵과 함께 끓는 물에 데쳐 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (417, 76, 4, '표고버섯은 갓의 중심에 칼집을 넣어 모양을 내고 느타리버섯은 모양대로 준비하며 팽이버섯은 밑둥을 잘라 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (418, 76, 5, '물에 씻어 물기를 뺀 쑥갓과 쇠고기, 배추, 표고버섯, 팽이버섯, 어묵, 곤약 등 준비한 재료를 큰 접시에 돌려 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (419, 76, 6, '물 6컵에 다시마를 넣고 끓으면 다시마는 건져내고 청주, 소금을 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (420, 77, 1, '배추는 연한 잎으로 준비하여 큰 것은 반으로 갈라놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (421, 77, 2, '애호박은 0.3cm 두께로 반달썰고 양파는 채썰며 대파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (422, 77, 3, '느타리버섯은 찬물에 흔들어 씻어 반으로 찢은 다음 물기를 빼둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (423, 77, 4, '쇠고기는 양지를 덩어리째 준비하여 물 4컵을 부어 푹 삶은 다음 얇게 썰어 국물은 기름을 걷어내고 육수로 쓴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (424, 77, 5, '국수는 냄비에 물을 반쯤 부어 소금을 넣고 삶는다. 국수가 끓어오르면 찬물 1컵을 붓고 다시 끓으면 찬물에 헹군 뒤 체에 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (425, 77, 6, '고춧가루에 진간장, 다진 마늘, 후춧가루를 분량대로 넣고 고루 섞은 다음 기름 두른 프라이팬에 넣고 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (426, 78, 1, '낙지는 내장을 떼고 소금으로 문질러 씻은 다음 5cm 길이로 자르고 머리도 같은 크기로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (427, 78, 2, '손질한 낙지를 간장 1큰술에 다진 파와 마늘을 넣고 양념한 뒤 10분 정도 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (428, 78, 3, '새우는 소금물에 씻어 등쪽 두번째 마디를 꼬치로 찔러 내장을 빼고 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (429, 78, 4, '실파는 5cm 길이로 썰고 표고버섯도 5cm 길이로 채썬다. 홍고추는 씨를 털고 반으로 갈라  길이대로 채썰고 쑥갓은 씻어 5cm로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (430, 78, 5, '쇠고기 50g은 채썰고 30g은 다져서 각각 양념한다. 두부는 씻어 으깬 다음 물기를 꼭 짜서 소금, 참기름, 깨소금으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (431, 78, 6, '양념한 두부와 다진 쇠고기를 섞어 완자로 빚은 다음 밀가루와 계란을 입혀 프라이팬에 굴려 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (432, 79, 1, '찬물에 핏물을 뺀 선지를 소금을 조금 넣은 끓는 물에 덩어리째 넣고 살짝 데쳐 숟가락으로 뚝뚝 떼 놓는다. 우거지는 끓는물에 데친 후 5cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (433, 79, 2, '콩나물은 뿌리를 다듬어 씻은 다음 살짝 삶아내고 물기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (434, 79, 3, '분량의 진간장에 다진 파, 마늘, 고춧가루, 참기름, 깨소금을 넣어 고루 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (435, 79, 4, '준비한 우거지, 콩나물은 그릇에 한데 담고 고추장과 다진 파, 마늘을 넣어 간이 배도록 조물조물 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (436, 79, 5, '된장을 걸러 한소끔 끓인 국물에 양념한 우거지, 콩나물을 넣고 다시 팔팔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (437, 79, 6, '우거지와 콩나물이 어느정도 물러져 맛이 우러나면 데쳐 놓은 선지를 넣고 양념장으로 알맞게 간을 맞춘 다음 한소끔 끓인다. 떠오르는 거품을 걷어내고 뜨겁게 끓여 상에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (438, 80, 1, '뚝배기에 한 사람이 먹을 만큼의 밥을 담고 콩나물은 씻어 삶아 밥이 덮일 만큼 수북이 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (439, 80, 2, '①위에 어슷썬 실파와 청고추, 결대로 찢은 장조림을 보기 좋게 둘러 얹고, 그 위에 잘게 썬 김치와 다진 마늘을 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (440, 80, 3, '재료를 넣은 뚝배기에 육수를 자작하게 부어 보글보글 끓이다가 상에 내기 직전에 살짝 구운 김을 비벼 얹고 생계란을 깨뜨려 얹은 다음 깨소금을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (441, 80, 4, '새우젓을 따로 곁들여 식성대로 간을 맞춰 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (442, 81, 1, '양은 속껍질이 나오도록 말끔히 벗겨 흐르는 물에 헹군 뒤 큰 그릇에 담아 소금으로 벅벅 문질러 씻은 후 다시 헹궈낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (443, 81, 2, '무는 큼직하게 토막 낸다. 대파 1/2뿌리는 송송 썰고 남은 1/2뿌리는 길고 큼직하게 썰어 놓는다. 마늘은 통째로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (444, 81, 3, '손질한 양을 냄비에 넣고 물을 충분히 부은 후 길게 썬 파와 통마늘을 넣고 뚜껑을 연채 푹 익도록 끓인다. 20분쯤 끓어 양이 익으면 양지머리를 넣고 덩어리째 한참 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (445, 81, 4, '양과 양지머리가 익으면 토막 낸 무를 넣어 무가 속까지 익을 때까지 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (446, 81, 5, '익은 고기와 무를 건진다. 무와 양지머리는 납작하게 썰고, 양은 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (447, 81, 6, '분량의 양념 재료로 양념을 만든다. 그릇에 양과 양지머리를 함께 넣고 양념한다. 무는 부서지기 쉬우므로 따로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (448, 82, 1, '토막낸 쇠뼈와 사골은 찬물에 1-2시간 담가서 핏물을 빼고 도가니와 양지머리는 덩어리째 씻어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (449, 82, 2, '큰 솥에 쇠뼈와 사골을 넣고 물을 넉넉히 부어 센불에서 끓이다가 국물에 검은색이 돌고 거품이 나면 검은 물을 따라 버리고 다시 물 20컵을 부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (450, 82, 3, '끓는 사골국물에 도가니와 양지머리를 덩어리째 넣고 파1뿌리와 통마늘1통을 넣어 뚜껑을 연 채 끓인다. 팔팔 끓어오르면 불을 줄이고 뭉근히 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (451, 82, 4, '도가니가 푹 물러지면 건져 내 작은 토막으로 썰고 양지머리는 저며썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (452, 82, 5, '끓는 물에 국수를 삶아 찬물에 행군 다음 체에 건져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (453, 82, 6, '물기빠진 국수를 끓는 국에 담가 따뜻하게 데운 다음 뚝배기에 담고 그 위에 고기를 얹어 국물을 붓는다. 상에 낼 때는 소금과 후추가루, 송송 썬 파를 그릇에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (454, 83, 1, '도가니는 적당히 토막 내어 찬물에 담가 핏물을 뺀다. 사태는 기름기 없는 것으로 골라 덩어리째 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (455, 83, 2, '냄비에 물 20컵을 붓고 사태를 넣어 끓이다가 붉은 색이 없어지면 도가니를 넣어 센 불에서 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (456, 83, 3, '끓이다가 국물이 우러나오면 고기를 건져서 한입 크기로 썰고, 남은 뼈는 다시 국물에 넣고 2시간 동안 푹 고아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (457, 83, 4, '썰어 놓은 고기에 다진 마늘 2큰술 반과 분량의 생강, 소금, 후춧가루를 넣어 양념하고 국물은 위에 한지를 덮어 스며들게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (458, 83, 5, '건지를 그릇에 담고 송송 썬 파를 얹은 다음 국물을 끓여 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (459, 83, 6, '진간장에 다진 마늘 반 큰술, 생강, 고춧가루, 참기름, 후춧가루를 넣고 양념장을 만들어 종지에 담아내어 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (460, 84, 1, '쇠꼬리는 4cm로 토막 낸 후 찬물에 담가 핏물을 뺀다. 찬물을 두세 번 갈아서 깨끗이 헹구어 건져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (461, 84, 2, '무는 3cm 길이로 토막 내고, 파 1뿌리는 송송 썰고 1뿌리는 5cm 길이로 썬다. 당면은 찬물에 불렸다가 끓는 물에 데친 후 체에 밭쳐 찬물에 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (462, 84, 3, '냄비에 물을 팔팔 끓여 핏물을 뺀 쇠꼬리를 넣고 데친 다음, 첫물은 따라내고 다시 찬물을 20컵 부어 무와 크게 썬 파, 통마늘을 넣고 국물이 뽀얗게 우러나도록 중불에서 1시간 이상 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (463, 84, 4, '국물이 우러나면 쇠꼬리를 건져 한 입 크기로 썬 다음 분량의 양념 재료로 양념한다. 무도 건져내어 식혀서 얄팍하게 나박썰고 따로 양념한다. 국물은 거즈에 걸러낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (464, 84, 5, '거즈에 밭친 국물을 냄비에 담고 양념한 쇠꼬리와 무를 넣어 한소끔 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (465, 84, 6, '국물이 끓으면 준비한 당면과 송송 썬 파를 넣고 소금으로 간 한 뒤 살짝 끓여 상에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (466, 85, 1, '갈비는 찬물에 담가 핏물을 뺀 뒤 냄비에 넣고 물 10컵을 부어 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (467, 85, 2, '무는 3cm 두께로 토막 내어 갈비와 같이 끓여 푹 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (468, 85, 3, '무와 갈비가 푹 익으면 건져 내어 거즈나 종이로 기름을 걸러낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (469, 85, 4, '삶아낸 갈비와 무를 그릇에 담고 국간장, 다진 파, 다진 마늘, 참기름, 후춧가루로 고루 무쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (470, 85, 5, '기름을 걷어낸 국물에 양념한 갈비와 무를 넣고 국간장으로 부족한 간을 한 다음 한소끔 더 끓여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (471, 85, 6, '그릇에 낼 때는 계란 지단을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (472, 86, 1, '부추는 잎이 짧고 통통한 것으로 골라 가지런히 다듬어 흐르는 물에 씻은 후, 물기를 빼고 반으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (473, 86, 2, '멸치젓에 1/2컵의 물을 부어 끓인 후 고운체에 걸러 맑은 젓국을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (474, 86, 3, '마늘과 생강은 곱게 다지고 홍고추는 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (475, 86, 4, '부추를 그릇에 펴 담고, 멸치젓을 고루 뿌려 절인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (476, 86, 5, '부추가 절여지면 멸치젓국을 따라내고, 고춧가루와 마늘, 생강, 홍고추, 설탕을 섞어 양념을 만들어 부추를 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (477, 86, 6, '부족한 간은 소금으로 한 후 완성한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (478, 87, 1, '실파는 뿌리를 잘라내고 다듬어 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (479, 87, 2, '넓은 그릇에 실파를 고르게 펴고 까나리액젓을 뿌려 절인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (480, 87, 3, '고춧가루에 약간의 물을 부어 불린 후 다진 마늘, 다진 생강, 설탕, 소금, 깨소금을 넣어 고루 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (481, 87, 4, '절인 실파에 양념장을 넣어 고루 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (482, 87, 5, '버무린 실파는 다섯 뿌리씩 모아 잡고 한 데 묶어 항아리에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (483, 88, 1, '배추는 2~4등분하여 소금물에 절인 후 물로 헹구어 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (484, 88, 2, '무는 다듬어 0.3cm 굵기로 채썬다. 밤과 배는 손질하여 가늘게 채썰고 대추는 잘 닦아 씨를 뺀 후 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (485, 88, 3, '표고버섯은 물에 불린 후 기둥을 떼고 채썬다. 생강은 손질하여 채썰고 미나리는 3cm로 자르고 대파는 흰 부분만 3cm 길이로 잘라 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (486, 88, 4, '무, 밤, 미나리, 대파와 다진 마늘, 생강을 섞고 실고추를 넣어 버무린 후 대추, 표고버섯, 잣, 채썬 석이버섯을 넣고 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (487, 88, 5, '절인 배추에 준비한 소를 배춧잎 사이 사이에 넣고 겉에 있는 큰 잎으로 흐트러지지 않게 잘 싸서 항아리에 눌러 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (488, 88, 6, '껍질을 벗긴 배 2개를 강판에 갈아 즙을 낸 뒤 소금물과 함께 섞어 항아리에 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (489, 89, 1, '깻잎은 싱싱한 것으로 골라 씻어낸 후 물기를 완전히 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (490, 89, 2, '파는 잘게 썰고 마늘과 생강은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (491, 89, 3, '진간장에 ②와 고춧가루를 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (492, 89, 4, '깻잎을 한 장씩 펴서 양념장을 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (493, 89, 5, '준비한 항아리에 깻잎을 차곡차곡 담고 누르개로 누른 후 남은 양념장을 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (494, 89, 6, '2~3일 후에 진간장만 따라내어 팔팔 끓인 다음 식혀 항아리에 다시 붓는다. 약 1주일이 지나면 깻잎이 누렇게 익는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (495, 90, 1, '오이는 잘고 연한 재래종으로 준비하여 씻은 후, 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (496, 90, 2, '소금물을 팔팔 끓여 놓은 후 식혀 놓는다. 오이에 식힌 소금물을 붓고 무거운 돌로 눌러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (497, 90, 3, '1주일 정도 지나면 소금물을 따라내어 다시 한 번 끓여 식힌 다음 오이에 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (498, 90, 4, '2주일 정도 지나면 절여진 오이를 꺼내어 소쿠리에 넣어 말린 다음 고추장에 버무려 항아리에 담아 5~6개월 정도 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (499, 90, 5, '먹을 때마다 조금씩 꺼내어 고추장을 훑어내고 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (500, 90, 6, '다진 마늘과 고춧가루, 참기름, 설탕을 함께 섞어 양념을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (501, 91, 1, '콩은 주로 검은 것으로 하나, 얼룩이 있는 밤콩이나 푸른콩을 사용해도 된다. 콩은 물에 담가 충분히 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (502, 91, 2, '냄비에 불린 콩을 넣고 간장과 물 1컵을 부어 중간 불에서 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (503, 91, 3, '조리면서 고루 익도록 자주 뒤적여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (504, 91, 4, '콩이 익어 쪼글쪼글해지고 국물이 졸아들면 불을 줄이고 설탕을 넣고 잘 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (505, 91, 5, '약한 불에서 계속 조려 국물이 거의 졸아들고 콩에윤기가 돌면 불에서 내려 참기름, 깨소금을 뿌려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (506, 92, 1, '쇠고기는 큼직하게 토막 낸 후 기름기와 힘줄을 없앤다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (507, 92, 2, '마늘은 통으로 준비하고 생강은 얇게 저민다. 홍는 어슷하게 썰어 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (508, 92, 3, '계란은 삶아 찬물에 담갔다가 껍질을 벗긴다. 당근은 손질하여 도톰하게 반달모양으로 썰어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (509, 92, 4, '냄비에 쇠고기와 마늘, 생강을 넣고 잠길 정도로 물을 부어 끓이다가 불을 줄여 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (510, 92, 5, '국물의 1/3쯤 졸면 진간장과 설탕을 넣고 약한 불에서 계속 졸이다가 조림간장이 반으로 줄면 준비한 계란, 당근, 홍고추를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (511, 92, 6, '계란은 주걱으로 굴려가며 간이 고루 배게 한다. 상에 낼 때는 깨소금을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (512, 93, 1, '연근은 씻어 위 아래는 잘라내고 껍질을 두껍게 벗겨내 5cm 두께로 통썰기한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (513, 93, 2, '그릇에 진간장, 설탕, 청주와 물 5큰술을 넣고 잘 섞어 조림장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (514, 93, 3, '프라이팬에 기름을 두르고 연근을 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (515, 93, 4, '볶은 연근에 조림장을 붓고 뚜껑을 덮어 약한 불에서 은근하게 조려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (516, 93, 5, '연근이 윤기나게 조려지면 불에서 내리고 통깨를 뿌려 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (517, 94, 1, '실파는 깨끗이 손질한 후 가지런히 모아잡고 길이로 반을 자른다. 뿌리는 칼등으로 두드린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (518, 94, 2, '굴과 홍합, 조갯살은 연한 소금물에 씻어 물기를 뺀 후 굵게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (519, 94, 3, '멥쌀은 불려서 물1컵을 붓고 곱게 갈아 밀가루와 물1컵을 부어 훌훌하게 반죽하고 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (520, 94, 4, '실파는 한줌씩 쥐어 반죽에 담갔다가 기름두른 팬에 가지런히 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (521, 94, 5, '그 위에 해물을 펴서 고루 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (522, 94, 6, '밑이 노릇하게 되면 푼 달걀을 위에 끼얹은 후 뒤집는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (523, 95, 1, '오이는 가는 것으로 골라 소금으로 문질러 씻고 0.3cm 두께로 얄팍하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (524, 95, 2, '파와 마늘은 곱게 다지고 실고추는 3cm 길이로 짧게 끊어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (525, 95, 3, '오이는 소금 2작은술로 절였다가 물에 살짝 헹구어 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (526, 95, 4, '팬을 달구어 참기름을 두르고 절인 오이를 넣어 볶아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (527, 95, 5, '기름이 돌면 다진 파, 마늘, 깨소금, 실고추를 넣고 잠깐 볶아서 넓은 그릇에 펼쳐 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (528, 96, 1, '냉이는 소금을 넣은 끓는 물에 살짝 데친 후 마늘 3쪽 다진 것과 된장을 넣어 손으로 무쳐 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (529, 96, 2, '모시조개는 하룻밤정도 물에 담가두어 해감을 토하게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (530, 96, 3, '냄비에 물 10컵과 모시조개를 넣고 끓이다가 모시조개의 입이 벌어지면 건져내고 국물을 거즈에 걸러 냄비에 담고 된장에 무친 냉이를 넣고 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (531, 96, 4, '찌개가 끓기 시작하면 파와 마늘을 어슷하게 썰어 넣고 간을 맞춘 뒤 마지막으로 모시조개를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (532, 97, 1, '계란을 그릇에 깨뜨려 넣고, 분량의 소금과 후추를 넣어 젓가락으로 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (533, 97, 2, '물 1컵과 다시마 끓인 국물을 걸러내고 차게 식힌다. ①의 계란에 붓고 거품기로 고루 섞는다. 다진 파와 다진 마늘을 넣고 더 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (534, 97, 3, '사각 팬에 기름을 두르고 달구어지면 계란물을 2/3정도 붓고 젓가락으로 저어 꾸덕꾸덕해지면 도톰하게 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (535, 97, 4, '다시 팬에 기름을 두르고 나머지 계란물을 부어 완전히 익기 직전에 접는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (536, 97, 5, '김발에 싸서 두었다가 완전히 식으면 도톰하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (537, 98, 1, '닭은 먹기 좋게 토막 내어 진간장, 다진 마늘, 후춧가루로 밑간을 해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (538, 98, 2, '간이 밴 닭고기에 녹말가루를 묻혀 180°C 의 기름에 노릇하게 튀겨 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (539, 98, 3, '냄비에 분량의 양념 재료를 넣고 물 2큰술을 부어 끓이다가 튀긴 닭을 넣고 윤기 나게 바짝 조린다. 도중에 조림장을 끼얹어 속까지 간이 배게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (540, 98, 4, '닭강정을 접시에 담고 통깨를 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (541, 99, 1, '배추는 한 잎 씩 떼어 씻은 다음 소금 5 큰술을 고루 뿌려 절여놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (542, 99, 2, '새우젓은 건더기만 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (543, 99, 3, '절인 배추는 찬물에 헹구었다가 소쿠리에 건져 물기를 뺀 다음 손으로 쭉쭉 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (544, 99, 4, '다진 파와 마늘, 생강, 고춧가루, 진간장, 새우젓, 설탕, 참기름을 섞어 양념을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (545, 99, 5, '찢은 배추를 양념으로 버무린다. 상에 낼 때 통깨로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (546, 100, 1, '더덕은 껍질을 벗긴 뒤 적당한 길이로 쪼개어 밀대나 칼등으로 두드린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (547, 100, 2, '다듬은 더덕은 물에 담가 아린맛을 뺀 후 건져서 물기를 닦아내고 참기름과 간장을 넣어 주무른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (548, 100, 3, '고추장 2큰술에 양념재료를 모두 넣고 고루 섞어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (549, 100, 4, '달군 석쇠에 ②의 더덕을 가지런히 올리고 타지 않게 살짝 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (550, 100, 5, '양념장을 발라 다시 한번 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (551, 101, 1, '감자는 껍질을 벗겨 한 입 크기로 토막낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (552, 101, 2, '토막낸 감자는 모서리를 다듬은 후 찬물에 담가 끈끈한 성분이 없어지면 물기를 닦는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (553, 101, 3, '멸치는 조림용으로 준비해 머리와 지저분한 것을 떼어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (554, 101, 4, '냄비에 기름을 둘러 감자를 볶다가 물을 자작하게 붓고 분량의 진간장과 물엿, 청주를 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (555, 101, 5, '조림장이 어느 정도 졸아들면 멸치를 넣고 간이 배게 더 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (556, 101, 6, '조림재료에 윤기가 돌면 깨소금을 넣어 살짝 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (557, 102, 1, '마른 오징어는 물에 불려 깨끗이 씻은 다음 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (558, 102, 2, '껍질 벗긴 오징어는 길이로 반 갈라 1cm 폭으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (559, 102, 3, '오징어를 냄비에 넣고 참기름과 물을 부어 천천히 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (560, 102, 4, '국물이 1/3 정도로 졸아들면 진간장, 설탕을 넣고 다진 파와 다진 마늘을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (561, 102, 5, '약한 불에서 은근히 조리다가 국물이 거의 졸면 꼭지 딴 청고추를 넣고 살짝 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (562, 102, 6, '윤기나게 조려지면 깨소금과 후춧가루를 뿌려 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (563, 103, 1, '죽순은 삶아서 딱딱한 바깥쪽을 벗겨 내고 4cm 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (564, 103, 2, '쇠고기는 채썰어 술, 간장 1/2 작은술을 뿌려 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (565, 103, 3, '고기에 간이 배면 녹말가루를 뿌려 버무린 뒤 달군 냄비에 넣고 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (566, 103, 4, '고기를 꺼내고 죽순, 데친 마늘종을 볶다가 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (567, 103, 5, '맛이 배면 고기를 다시 넣고 볶다가 참기름을 넣고 고루 뒤적인 다음 불을 끈다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (568, 104, 1, '애호박은 두께가 고른 것으로 골라 깨끗이 씻은 다음 0.5cm 두께로 통썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (569, 104, 2, '호박에 소금을 뿌려 살짝 절였다가 거즈로 물기를 닦아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (570, 104, 3, '계란은 풀어 소금으로 간을 해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (571, 104, 4, '준비한 호박의 앞뒤로 밀가루를 고루 묻힌 다음 계란물에 적신다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (572, 104, 5, '달구어진 프라이팬에 기름을 둘러 중간불에서 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (573, 104, 6, '분량의 양념 재료를 섞어 양념장을 만들어 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (574, 105, 1, '돼지고기는 한 입 크기로 썰어 간장과 설탕으로 밑간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (575, 105, 2, '표고버섯과 피망, 당근도 고기와 같은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (576, 105, 3, '분량의 간장, 설탕, 식초, 물은 한 볼에 섞어 준비해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (577, 105, 4, '①에 계란흰자를 넣고 버무린 뒤 녹말가루를 묻혀 바삭하게 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (578, 105, 5, '프라이팬에 준비한 야채를 넣고 볶다가 섞어둔 소스 재료를 넣고 끓여 걸쭉해지면 튀긴 돼지고기를 넣고 맛이 고루 배도록 버무려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (579, 106, 1, '메주콩은 5배의 물을 붓고 반나절 정도 담가 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (580, 106, 2, '콩이 완전히 불면 냄비에 ①을 넣고 물을 콩 위로 1cm 정도 올라오게 부어 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (581, 106, 3, '②가 한번 우르르 끓으면 불을 끄고 뜸을 들인다. (콩을 오래 삶으면 냄새가 나므로 살짝 삶는 것이 포인트!)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (582, 106, 4, '체로 콩을 건진 후 찬물을 부어 양손으로 비벼가며 껍질을 벗기고 콩만 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (583, 106, 5, '믹서에 삶은 콩, 통깨, 잣을 넣고 생수를 부어 곱게 간 뒤, 체에 밭쳐 건더기는 걸러내고 국물만 받아 냉장고에 차게 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (584, 106, 6, '볼에 계란과 소금을 적당량 넣고 잘 풀어준 뒤 체에 한번 걸러준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (585, 107, 1, '쇠고기는 연한 부위로 준비해 0.5cm 두께로 도톰하게 저며 앞 뒤로 잔칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (586, 107, 2, '진간장에 배즙을 넣고 나머지 양념 재료를 모두 합해 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (587, 107, 3, '고기를 굽기 30분전에 ②의 양념장을 고루 묻혀 간이 배게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (588, 107, 4, '뜨겁게 달군 석쇠에 올려 앞뒤로 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (589, 108, 1, '부추는 싱싱한 것으로 골라 흐르는 물에 씻어 4cm 길이로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (590, 108, 2, '고추는 송송 썰어 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (591, 108, 3, '그릇에 밀가루를 담고 소금으로 간을 한 뒤 계란과 물을 부어 멍울이 없도록 잘 풀어주고, 썰어 놓은 부추를 넣어 반죽을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (592, 108, 4, '달군 프라이팬에 기름을 두르고 ③의 반죽을 한 국자씩 얹고 위에 청고추와 홍고추를 얹어 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (593, 108, 5, '한 면이 노릇하게 익으면 뒤집어 마저 익힌 다음 먹기 좋은 크기로 썰어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (594, 109, 1, '돼지갈비는 먹기 좋게 토막 내 기름을 떼어내고 병풍모양으로 포를 떠서 잔칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (595, 109, 2, '손질한 갈비에 생강즙과 술을 뿌려 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (596, 109, 3, '진간장 5큰술에 나머지 양념 재료를 합해 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (597, 109, 4, '②의 고기에 ③의 양념장을 넣고 버무려 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (598, 109, 5, '간이 충분히 배면 팬에 기름을 두르고 지지거나 석쇠에 얹어 속까지 잘 익도록 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (599, 109, 6, '구운 돼지갈비와 깨소금, 상추, 깻잎을 함께 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (600, 110, 1, '불린 쌀에 물을 붓고 다시마를 얹어 고슬고슬하게 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (601, 110, 2, '밥이 다 되면 다시마를 꺼내고 촛물과 섞어 비빈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (602, 110, 3, '참치는 너비 3cm 정도로 썰고 오징어는 참치와 같은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (603, 110, 4, '문어는 데쳐서 0.5cm 두께로 썰고 새우는 데쳐 몸통 가운데에 칼집을 넣어 넓적하게 편다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (604, 110, 5, '초밥을 뭉쳐 고추냉이를 바르고 준비한 생선을 붙여서 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (605, 111, 1, '꽁치통조림을 체에 쏟아 국물은 따로 받아놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (606, 111, 2, '무는 3cm 길이로 토막낸 후 반으로 갈라 도톰하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (607, 111, 3, '①의 꽁치국물에 양념을 분량대로 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (608, 111, 4, '깊이가 얕은 냄비에 무를 깔고 꽁치를 가지런히 놓은 다음 양념장을 고루 끼얹어 불에 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (609, 111, 5, '뚜껑을 열어 놓은 채로 가끔 조림국물을 끼얹으면서 무가 무르게 익을 때까지 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (610, 112, 1, '김치는 소를 털어내고 줄기만 길이 8cm, 너비 2cm로 썰어 참기름을 고루 묻힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (611, 112, 2, '쇠고기는 도톰하게 포를 떠 잔칼질하여 김치보다 약간 길게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (612, 112, 3, '표고버섯은 1cm 폭으로 썰고 도라지는 삶아서 반으로 갈라 참기름과 소금으로 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (613, 112, 4, '준비된 재료를 꼬치에 꽂아 밀가루를 묻힌 후 계란물을 묻혀 기름에 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (614, 113, 1, '낙지는 소금으로 문질러 씻은 후 끓는 물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (615, 113, 2, '양파와 고추는 굵게 채썰고 대파는 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (616, 113, 3, '분량의 양념 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (617, 113, 4, '데친 낙지에 ③을 넣고 고루 섞어 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (618, 113, 5, '달군 프라이팬에 야채와 낙지를 넣고 센불에서 재빨리 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (619, 113, 6, '마지막으로 참기름과 깨소금을 뿌려 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (620, 114, 1, '두부는 팔팔 끓는물에 소금을 약간 넣고 속까지 따뜻하게 익도록 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (621, 114, 2, '배추김치는 소를 대강 털고 1cm 폭으로 송송 썬 다음 다진 마늘과 참기름으로 무쳐놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (622, 114, 3, '양념한 김치를 팬에 넣고 숨이 죽도록 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (623, 114, 4, '찬물에 담가둔 깻잎은 담궈 곱게 채를 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (624, 114, 5, '두부를 먹기 좋게 썬 다음 깻잎과 볶은 김치를 접시에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (625, 115, 1, '계란은 1인분에 2개씩해서 1인분씩 만든다. 계란을 잘 풀어서 소금 1/6작은술과 후춧가루를 잘 섞은 후 우유 1큰술을 넣고 잘 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (626, 115, 2, '달군 팬에 식용유를 두르고 ①을 한번에 부어 젓가락으로 재빨리 휘저으면서 넓게 부친다. 안쪽은 반숙상태가 되도록한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (627, 115, 3, '반숙이 되면 불을 약하게 하고 뒤집개로 계란을 먼 쪽에서 가까운 쪽으로 겹치며 모양을 만든다. 이때 버터 1/2큰술을 넣고 반달모양에서 배모양으로 만들고 뒤집어서 노릇하게 부쳐낸다. 기호에 따라 토마토케첩을 살짝 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (628, 116, 1, '양배추는 밑동을 도려내고 끓는 물을 부어 살짝 데친다. 데치는 동안 한번 뒤집어주고 끓으면 뒤집어서 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (629, 116, 2, '양배추를 찬물에 헹구고 잎 4장을 잘 벗겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (630, 116, 3, '다진고기에 계란, 후춧가루, 넛맥,빵가루를 섞고 곱게 다진 양파와 당근을 넣어 섞는다. 끈기가 나도록 잘 이겨 4등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (631, 116, 4, '양배추잎은 심결대로 펴고 소를 뭉쳐 앞쪽에 둔다. 돌돌 말아 소를 싸고 이쑤시개로 꽂아 고정시켜 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (632, 116, 5, '냄비에 양배추말이를 넣고 수프를 가득부어 소근, 후추, 간장을 약간씩 넣고 냄비보다 작은 기름종이를 얹은 다음 뚜껑을 닫아 중불에서 약불로 조절해가며 20분정도 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (633, 116, 6, '다 익으면 건져내 이쑤시개를 빼고 2등분하여 접시에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (634, 117, 1, '두부는 6등분하여 끓는 물에 살짝 데친 뒤 물기를 제거하여 냉장고에 넣어 차게 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (635, 117, 2, '미역은 충분히 불려 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (636, 117, 3, '양파는 곱게 다져서 물에 씻어 매운기를 뺀뒤 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (637, 117, 4, '홍고추는 곱게 다지고 분량의 양념 재료를 섞어 소스를 만들어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (638, 117, 5, '차게 해놓은 두부는 접시에 보기좋게 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (639, 117, 6, '두부 위에 다진 미역을 얹고, 그 위에 다진 양파와 홍고추를 얹고 소스를 끼얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (640, 118, 1, '계란은 풀어서 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (641, 118, 2, '돼지고기와 당근, 양파는 0.7cm 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (642, 118, 3, '말린 표고 버섯은 미지근한 물에 불려 기둥을 떼어내고 0.7cm 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (643, 118, 4, '대파는 굵게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (644, 118, 5, '식용유를 넉넉히 두른 프라이팬에 계란을 넣어 젓가락으로 휘저어 익힌 뒤 꺼낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (645, 118, 6, '식용유를 두른 프라이팬에 돼지고기를 넣어 볶다가 당근, 양파, 표고버섯을 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (646, 119, 1, '배추김치는 속을 털어 1cm 폭으로 송송 썰고 표고버섯은 미지근한 물에 불린 후 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (647, 119, 2, '콩나물은 깨끗이 씻어 건지고, 당근은 껍질을 벗겨 가늘게 채썰고 대파, 홍고추, 청고추는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (648, 119, 3, '햄은 5cm 길이로 가늘게 채썰고, 게맛살은 5cm 길이로 잘라 손으로 가늘게 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (649, 119, 4, '끓는 물에 라면과 당근, 콩나물을 넣고 뚜껑을 덮어 끓이다 한소끔 끓으면 라면수프와 고춧가루를 풀고 햄, 게맛살, 파, 고추, 다진 마늘을 넣고 뜸들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (650, 119, 5, '재료가 다 익으면 그릇에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (651, 120, 1, '계란은 소금으로 간하여 지름 12cm 정도로 얇게 지단을 부친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (652, 120, 2, '미나리는 살짝 데쳐서 물기를 꼭 짜고 치커리는 4cm 길이로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (653, 120, 3, '새우는 껍질과 내장을 제거한 뒤 끓는 물에 데치고, 맛살은 5cm 길이로 잘라 잘게 찢어 물에 담갔다 건져 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (654, 120, 4, '국수는 삶아서 헹군 뒤 사리를 지어 소쿠리에 밭쳐놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (655, 120, 5, '계란 지단을 펴서 치커리, 국수, 게맛살을 놓고 말아 미나리로 묶어주고 위에 새우를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (656, 120, 6, '분량의 양념 재료를 섞어 간장소스를 만들어 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (657, 121, 1, '꽁치는 머리와 꼬리를 떼어내고 두 토막내어 내장을 뺀 뒤 소금을 뿌려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (658, 121, 2, '실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (659, 121, 3, '조림장은 은근한 불에서 걸쭉하게 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (660, 121, 4, '꽁치는 물기를 빼고 석쇠나 그릴에서 앞뒤로 구운 뒤 조림장을 2~3회 발라가며 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (661, 121, 5, '그릇에 담고 실파를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (662, 122, 1, '감자는 0.3cm 두께로 채썰어 물에 담갔다 건져서 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (663, 122, 2, '청고추, 홍고추는 반으로 갈라 씨를 빼고 4cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (664, 122, 3, '프라이팬에 식용유를 두르고 채 썬 감자를 볶는다. 볶을때 소금, 흰후춧가루, 설탕 약간씩을 넣고 달달 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (665, 122, 4, '③번 감자가 다 볶아지면 ②번 고추를 넣고 한번 섞어준 다음 바로 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (666, 123, 1, '가는 파는 다듬어 씻은 뒤 4cm 길이로 썰고 양파는 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (667, 123, 2, '돼지고기는 5cm 길이로 채썰어 청주와 간장에 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (668, 123, 3, '굵은파는 굵게 다지고 마늘, 생강은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (669, 123, 4, '식용유를 두른 프라이팬에 다진 굵은파, 다진 마늘, 생강을 볶은 뒤 간장을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (670, 123, 5, '돼지고기를 볶다가 양파를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (671, 123, 6, '가는파를 넣어 살짝 볶은 뒤 소금, 후춧가루, 참기름을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (672, 124, 1, '도토리묵과 청포묵, 오이, 게맛살을 사방 1.5cm 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (673, 124, 2, '분량의 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (674, 124, 3, '준비한 재료를 그릇에 담고 양념장을 끼얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (675, 125, 1, '고등어는 머리, 꼬리를 떼어내고 내장을 뺀 뒤 양쪽실을 떼어낸다. 한 입 크기로 썰어서 소금, 청주를 뿌리고 15분쯤 재워 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (676, 125, 2, '마른 고추는 2등분하고 생강은 편으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (677, 125, 3, '고등어에 녹말가루를 묻혀 170℃의 식용유에서 노릇노릇하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (678, 125, 4, '냄비에 분량의 양념 재료를 넣고 약간 끈기있게 졸여서 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (679, 125, 5, '그릇에 고등어를 담고 소스를 끼얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (680, 126, 1, '가지는 옆으로 반 갈라 김이 오른 찜통이나 밥솥에 넣고 쪄낸 뒤 젓가락으로 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (681, 126, 2, '마른 새우는 식용유를 두른 프라이팬에 바삭하게 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (682, 126, 3, '분량의 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (683, 126, 4, '가지에 양념장을 넣어 버무린 다음 볶아낸 새우와 함께 그릇에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (684, 127, 1, '냄비에 물, 다시마, 육수를 부어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (685, 127, 2, '부추는 깨끗이 다듬고 씻은 후 6cm 길이로 썰고, 팽이버섯은 밑동을 잘라내고 씻어낸 후 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (686, 127, 3, '당근도 6cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (687, 127, 4, '소스를 만든다. (분마기에 깨와 된장을 넣고 갈다가 설탕, 간장, 술을 조금씩 부어 가면서 갈아낸다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (688, 127, 5, '고기를 10~12cm 길이로 잘라 도마 위에 펴놓고, 준비한 채소를 조금씩 놓고 돌돌 말아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (689, 127, 6, '냄비에 담가놓았던 다시마가 불면 냄비를 불에 올려서 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (690, 128, 1, '양파는 곱게 다져 물기를 빼준 후 달구어진 프라이팬에 기름을 두르고 갈색이 나도록 볶아둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (691, 128, 2, '소갈비는 살을 발라 곱게 다져 분량의 양념 재료를 넣어 양념해 골고루 끈기가 나도록 치댄다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (692, 128, 3, '살을 발라낸 갈비에 밀가루를 바르고 ②를 양면으로 도톰하게 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (693, 128, 4, '석쇠에 호일을 깔고 기름을 발라 고기를 얹은 후 뒤집어가며 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (694, 129, 1, '골뱅이는 체에 밭쳐 물기를 뺀 다음 먹기 좋게 반으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (695, 129, 2, '오이는 두께 0.5cm 통으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (696, 129, 3, '대파와 당근은 두께 0.2cm, 가로x세로 0.2cm X 4cm 로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (697, 129, 4, '양파는 두께 0.5cm로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (698, 129, 5, '샐러리는 두께 0.2cm 통으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (699, 129, 6, '양념 재료를 섞어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (700, 130, 1, '두부는 가로 2cm, 세로 3cm, 두께 0.5cm로 썰어 소금을 뿌려 두었다가 물기를 없앤다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (701, 130, 2, '양념재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (702, 130, 3, '식용유를 두른 프라이팬에 두부를 앞뒤로 노릇노릇하게 부쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (703, 130, 4, '양념장을 걸쭉하게 졸여서 지진 두부에 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (704, 130, 5, '다져놓은 홍고추와 통깨를 각각 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (705, 131, 1, '냉동 쇠고기(우둔살)를 얇게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (706, 131, 2, '양념 재료를 섞어 양념을 만들고, 그릇에 썰어놓은 쇠고기를 담고 양념을 뿌려 잘 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (707, 131, 3, '접시에 상추를 깔고 양념한 쇠고기를 동그랗게 모양내어 담은 뒤 윗부분에 엄지손가락으로 오목하게 홈을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (708, 131, 4, '홈에 날계란 노른자를 얹는다. 노른자 둘레에는 잣가루를 뿌리고 노른자 윗부분을 잣으로 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (709, 131, 5, '배와 오이를 길이 5cm, 폭 4mm로 채썰어 양 끝을 가지런히 모아 칼로 정리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (710, 131, 6, '배와 오이로 주변을 장식하여 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (711, 132, 1, '스파게티는 끓는 물에 소금과 식용유를 약간씩 넣은 뒤 15분 쯤 삶아낸 후, 찬물에 헹궈 체에 밭쳐 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (712, 132, 2, '햄과 베이컨은 길고 얇게 채썰어 놓고 양파는 잘게 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (713, 132, 3, '냄비에 버터를 넣고 중불에서 녹인 다음, 베이컨과 햄을 볶다가 다진 양파를 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (714, 132, 4, '③에 바질을 넣고 살짝 볶다가 와인을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (715, 132, 5, '④에 생크림과 우유를 넣고 양이 반으로 졸면 담가놓은 스파게티 면의 물기를 뺀 뒤, 프라이팬에 크림과 함께 넣고 볶아 소금, 후추로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (716, 133, 1, '닭은 내장을 제거하여 깨끗이 씻고, 찹쌀은 씻어 1시간 정도 물에 불린 후 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (717, 133, 2, '대추는 돌려 깎아 씨를 빼고 수삼은 씻어 잔뿌리를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (718, 133, 3, '닭 속에 찹쌀, 마늘, 대추, 수삼을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (719, 133, 4, '왼쪽 다리 안쪽에 칼집을 넣어 그 사이에 오른쪽 다리를 넣어 꼬아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (720, 133, 5, '생강은 얇게 편썰어 준비한 후 냄비에 닭과 대파, 생강, 물을 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (721, 133, 6, '센불에서 끓인 후 불을 줄여 1시간 정도 푹 고아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (722, 134, 1, '다진 쇠고기는 분량의 양념을 한 뒤 프라이팬에 식용유를 두르고 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (723, 134, 2, '토마토는 8등분하여 두고 당근과 피망을 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (724, 134, 3, '당근과 피망을 쇠고기와 함께 볶다가 밥을 넣고 볶으면서 소금과 후춧가루로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (725, 134, 4, '하트모양 틀에 볶아낸 밥을 60g씩 떠서 채운 뒤 모양을 만들어 떼어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (726, 134, 5, '도시락 밑에 상추를 깔고 밥을 담아 사이사이에 토마토를 놓은 뒤 가운데에 파슬리를 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (727, 135, 1, '유부는 끓는 물에 살짝 데쳐낸 뒤 물기를 짜고 끝을 잘라서 주머니를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (728, 135, 2, '맛살은 다져놓고, 실파는 줄기 10개는 따로 데쳐서 준비하고 나머지는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (729, 135, 3, '계란은 잘 풀어서 실파, 맛살을 넣고 소금, 후춧가루로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (730, 135, 4, '유부 속에 ③을 넣고 살짝 데친 실파로 묶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (731, 135, 5, '내열 용기에 물, 간장, 설탕을 넣고 잘 섞어서 유부가 넘어지지 않게 놓은 뒤 랩을 씌워 전자레인지에서 3분 동안 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (732, 136, 1, '감자는 깨끗이 씻어 껍질째 찜통에서 찐다.(전자레인지에서 익혀도 된다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (733, 136, 2, '후루츠칵테일은 체에 받쳐 물기를 없앤다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (734, 136, 3, '익힌 감자를 반으로 잘라 바깥쪽에서 1cm 쯤을 남겨두고 속을 파낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (735, 136, 4, '파낸 속을 모아서 뜨거울 때 버터, 우유, 설탕, 계핏가루, 다진 파슬리를 넣고 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (736, 136, 5, '④를 다시 감자에 넣어 채운 다음 파슬리로 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (737, 136, 6, '도시락에 치커리를 깔고 감자를 담고, 은박컵에 후루츠칵테일을 담아 케첩과 함께 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (738, 137, 1, '쇠고기는 곱게 다져 양념을 해 잘 버무려두었다 양념이 충분히 배어 들면 식빵 크기가 되도록 모양을 잡는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (739, 137, 2, '프라이팬이나 오븐에 ①의 쇠고기를 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (740, 137, 3, '식빵은 노릇하게 굽고 토마토는 슬라이스해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (741, 137, 4, '양상추는 씻어 식빵 크기로 뜯어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (742, 137, 5, '버터, 겨자, 소금, 후춧가루를 넣어 겨자버터를 만들어서 구워둔 식빵의 한쪽면에 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (743, 137, 6, '식빵 위에 양상추 → 떡갈비(②) → 슬라이스치즈 → 토마토 슬라이스 → 피클을 순서대로 올린 후 겨자버터를 바른 식빵으로 덮는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (744, 138, 1, '쇠고기는 채썰어 분량의 양념에 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (745, 138, 2, '느타리와 표고버섯은 끓는 물에 데쳐 가늘게 찢고 양송이버섯은 껍질을 벗겨 얇게 썰고, 목이버섯은 물에 불려 씻은 후 먹기 좋게 뜯어 놓고 팽이버섯은 밑둥을 잘라낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (746, 138, 3, '양파는 가늘게 채썰고 풋고추, 당근은 길이 4cm, 두께 0.3cm로 채썰고, 미나리는 다듬어 4cm 길이로 준비하고 쇠고기는 야채와 같은 크기로 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (747, 138, 4, '프라이팬에 식용유를 두르고 양파를 볶다가 투명해지면 고기를 넣어 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (748, 138, 5, '당근과 준비된 버섯을 넣고 간장, 설탕, 청주를 넣어 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (749, 138, 6, '⑤에 육수와 녹말가루를 섞어 자작하게 붓고 미나리와 들깨가루를 넣은 후 참기름, 후춧가루로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (750, 139, 1, '닭고기는 기름기를 제거하고 소금과 요리술을 뿌려 두었다가 김 오른 찜통에 찐 뒤 식혀서 가늘게 찢어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (751, 139, 2, '오이는 소금에 문질러 씻어서 길이 4cm, 폭 1cm로 썰고, 배는 껍질을 벗겨 씨를 도려낸 다음 오이와 같은 크기로 썰어 설탕물에 담갔다가 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (752, 139, 3, '양상추는 손으로 뜯어서 물에 담그고 수삼은 칼등으로 긁어 잔털과 껍질을 벗긴 후 씨를 도려낸 다음 오이와 같은 크기로 썰어 설탕물에 담갔다가 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (753, 139, 4, '대추는 돌려 깎아 씨를 발라내어 3등분하고 밤은 얇게 저며 썰어서 찬물에 씻어 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (754, 139, 5, '분량의 재료를 섞어 머스터드 마요네즈를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (755, 139, 6, '재료와 머스터드 마요네즈를 버무려 완성한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (756, 140, 1, '오징어는 다리를 잡아당겨 내장을 빼낸 뒤, 몸통 속에 손을 넣어 찌꺼기를 긁어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (757, 140, 2, '손에 소금을 묻혀서 껍질을 벗기고 깨끗이 씻은 뒤 끓는 물에 소금을 넣고 삶아 건져서 5cm 두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (758, 140, 3, '새우는 꼬치로 등 쪽의 내장을 빼고 엷은 소금물로 씻은 뒤 끓는 물에 소금을 약간 넣고 삶아서 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (759, 140, 4, '컬리플라워는 작게 송이를 떼어 엷은 소금물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (760, 140, 5, '접시에 컬리플라워와 오징어, 새우를 얹고 머스터드 마요네즈를 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (761, 141, 1, '사과는 깨끗이 씻어서 길이로 반을 자른 후 씨를 도려낸다. 길이 4cm, 너비 1cm로 썰어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (762, 141, 2, '자몽은 아래 위를 조금씩 잘라내고 껍질과 과육 사이사이에 칼집을 넣은 뒤 곡선을 따라 껍질을 벗겨서 한 쪽씩 뗀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (763, 141, 3, '샐러리는 섬유질을 벗기고 반 갈라서 길이 4cm 로 썰고, 오이는 씻어서 4cm 길이로 토막 내 썬 다음 길이로 4등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (764, 141, 4, '딸기는 흐르는 물에 씻은 후 4등분한다. 오렌지도 껍질을 제거해 과육을 분리해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (765, 141, 5, '접시에 과일과 오이, 샐러리를 담고 오렌지 마요네즈를 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (766, 142, 1, '훈제연어를 얇게 포 뜨고, 앤다이브는 한 잎씩 떼어 씻은 후 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (767, 142, 2, '트레비스는 손으로 뜯어서 찬물에 담갔다 건져 물기를 빼고, 양파는 얇게 링 모양으로 썰어서 찬물에 담가 매운맛을 없앤 뒤 건져서 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (768, 142, 3, '다진 양파와 올리브오일을 섞어 드레싱을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (769, 142, 4, '접시에 훈제연어와 앤다이브, 트레비스를 담고 양파를 얹은 뒤 케이퍼를 뿌리고 양파드레싱을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (770, 143, 1, '금치의 연한 잎으로 준비하여 깨끗이 씻어서 물기를 빼고, 양송이 버섯은 껍질을 벗겨서 얇게 저며썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (771, 143, 2, ' 달군 팬에 베이컨을 구워 종이타월로 기름기를 닦아 내고 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (772, 143, 3, '시에 시금치 깔고 그위에 양송이버섯과 베이컨을 얹은 뒤 가루치즈를 뿌리고 레몬 드레싱을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (773, 144, 1, '감자, 당근은 껍질을 벗겨서 큼직하게 썰어 모서리를 다듬은 뒤 물에 씻어 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (774, 144, 2, '양파는 껍질을 벗겨서 씻은 후 반 잘라 큼직하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (775, 144, 3, '샐러리는 섬유질을 벗기고 3cm 길이로 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (776, 144, 4, '양송이는 기둥 끝을 약간 잘라내고 껍질을 벗겨 길이로 반 가른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (777, 144, 5, '큼직하고 깊이가 있는 프라이팬에 버터를 두르고 감자, 당근, 양파를 넣고 볶다가 물을 부어서 끓인다. 끓기 시작하면 거품을 걷어내고 월계수잎을 넣은 후 감자가 반쯤 익을 때까지 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (778, 144, 6, ' 감자가 반쯤 익으면 샐러리와 양송이버섯, 홀토마토를 넣고 끓인다. 중간중간 거품을 걷고 불을 줄여서 채소가 푹 무를 때가지 끓여 소금과 후춧가루로 간을 해서 수프 그릇에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (779, 145, 1, '햄은 0.5cm 두께로 썰어놓고 프랑크소시지는 반으로 가르고 베이컨은 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (780, 145, 2, '김치는 속을 털어낸 뒤 4cm 길이로 썰고 두부는 0.5cm 두께로 썬다. 청고추, 홍고추는 어슷썰고 깻잎은 1cm 길이로 썰고 스파게티면은 삶아놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (781, 145, 3, '분량의 양념 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (782, 145, 4, '전골냄비에 재료를 보기 좋게 돌려 담은 뒤 양념장을 넣어 섞고 끓으면 스파게티면을 넣고 푹 끓여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (783, 146, 1, '곱창 겉에 붙어 있는 기름을 떼어 낸 뒤 굵은 소금으로 세게 주무른 뒤 속을 훑어 내리며 물로 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (784, 146, 2, '양은 굵은 소금과 밀가루로 문질러 씻은 뒤 끓는 물에 넣었다 꺼내어 검은 껍질과 안쪽의 얇은 막을 벗겨 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (785, 146, 3, '대파는 10cm 길이로 썰고 생강은 껍질을 벗긴 후 얇게 저민다. 냄비에 물을 넣고 양과 곱창을 끓이다가, 곱창과 양을 건져놓고 육수는 식혀 기름기를 걷어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (786, 146, 4, '당근, 양배추, 양파는 채썬다. 표고버섯은 미지근한 물에 불린 뒤 기둥을 떼어내고 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (787, 146, 5, '미나리는 다듬어 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (788, 146, 6, '곱창과 양은 한 입 크기로 썰어 놓는다. 분량의 양념을 섞어 양념장을 만든다. 함께 버무려 전골냄비 가운데에 담고 썰어 놓은 야채를 돌려 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (789, 147, 1, '아귀는 내장을 빼고 깨끗이 씻어 뼈째 토막 낸 뒤 가로 6cm, 세로 6cm 크기로 썰어 다시 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (790, 147, 2, '미더덕은 소금물에 씻은 뒤 꼬치로 몸통을 찔러 물을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (791, 147, 3, '콩나물은 머리와 꼬리를 떼로 미나리는 5cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (792, 147, 4, '대파는 두께 0.4cm로 어슷하게 썰고, 깻잎은 두께 0.7cm로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (793, 147, 5, '찹쌀가루와 물을 섞어 따로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (794, 147, 6, '분량의 양념장 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (795, 148, 1, '볼에 강력분을 넣고 드라이이스트, 계란, 설탕, 소금을 넣고 미지근한 물을 부어 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (796, 148, 2, '반죽이 어느 정도 뭉쳐졌으면 평평한 곳으로 옮겨 손바닥으로 힘주어 반죽한 다음 어느 정도 매끄러워지면 실온에 두었던 버터와 우유를 조금씩 넣어 가며 치댄다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (797, 148, 3, '반죽이 얇게 펴질 정도까지 충분이 치댄 다음 검은 깨를 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (798, 148, 4, '반죽을 둥글려 윗면을 매끄럽게 한 뒤 볼에 넣는다. 젖은 천을 덮은 후 따뜻한 곳에 두어 1시간 동안 1차 발효를 시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (799, 148, 5, '반죽을 꺼내어 두 덩이로 나누어 둥글린 다음, 이번에는 마른 천을 덮어 10분 정도 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (800, 148, 6, '반죽을 밀대로 펴서 22cm X 15cm 로 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (801, 149, 1, '삼겹살은 8cm 길이로 썰어낸 뒤 칼등으로 자근자근 두드린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (802, 149, 2, '①에 소금, 생강즙, 후춧가루로 밑간을 하되 이때는 고기두께가 0.2cm 정도가 되게 한다. 고기두께가 얇으면 얇을수록 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (803, 149, 3, '붉은 고추는 반으로 갈라서 씨를 빼고 8cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (804, 149, 4, '무순은 깨끗이 씻어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (805, 149, 5, '찹쌀가루를 체에 밭친 후, ②의 고기를 양면에 묻혀 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (806, 149, 6, '⑤에 붉은 고추와 무순을 넣고 돌돌 말아낸 다음 꼬치로 고정시킨다. 고기가 얇으면 꼬치가 없어도 된다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (807, 150, 1, '배추는 속대로 준비하여 씻어서 칼로 길이로썬다. 무는 넙적넙적하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (808, 150, 2, '쇠고기는 채썰어 양념에 버무린다. 굵은파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (809, 150, 3, '냄비에 식용유를 두르고 양념한 쇠고기를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (810, 150, 4, '물을 넣고 된장과 고추장을 체에 걸러 푼다. 국물이 끓으면 배추와 무를 넣고 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (811, 150, 5, '굵은 파를 넣고 마늘을 넣어 끓여 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (812, 151, 1, '멥쌀은 씻어서 충분히 불린 다음 물기를 빼고 소금을 넣어 반만 빻고, 나머지는 쑥을 넣어 곱게 빻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (813, 151, 2, '대추는 물 10컵을 넣고 물이 없어질 때까지 끓인 뒤 체에 걸러 걸쭉한 대추고를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (814, 151, 3, '①의 흰 멥쌀가루의 반은 뜨거운 물을 넣어 익반죽을 하고 나머지는 대추고를 넣어 반죽한다. 쑥을 넣은 멥쌀가루도 익반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (815, 151, 4, '풋콩은 깨끗이 씻어 소금을 뿌리고 깨는 곱게 빻아 꿀을 넣어 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (816, 151, 5, '녹두는 따뜻한 물에 불려 껍질을 벗긴 뒤 푹 쪄서소금을 넣고 믹서에 곱게 갈아 체에 내린다. 냄비에 녹두 고물과 설탕을 넣고 약한 불에서  볶은 뒤 계핏가루를 넣고 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (817, 151, 6, '반죽을 조금씩 떼어 각각의 소를 넣고 빚어, 솔잎을 깔아 준비한 찜통에서 30분간 찐다. 쪄낸 송편을 찬물에 헹궈 참기름을 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (818, 152, 1, '녹두는 따뜻한 물에 충분히 불린 후 손으로 비벼 껍질을 벗기고 물기를 뺀 뒤 믹서에 물을 넣고 곱게 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (819, 152, 2, '돼지고기는 다진다. 분량의 양념 재료를 섞어 양념을 만든 후 돼지고기를 넣어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (820, 152, 3, '느타리버섯은 끓는 물에 데쳐 물기를 짜서 손으로 찢어 두고, 석이버섯은 따뜻한 물에 불려 비벼 씻은 다음 곱게 채썬다. 대파는 어슷하게 썰어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (821, 152, 4, '갈아놓은 녹두를 소금, 후춧가루로 간을 해서 프라이팬에 식용유를 넉넉히 두르고 한 숟가락씩 얹어 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (822, 152, 5, '느타리버섯을 전 가운데에 얹고 녹두반죽을 조금만 떠서 버섯 둘레에 얹고 돼지고기, 숙주, 석이버섯, 다진 파, 실고추를 전 위에 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (823, 152, 6, '전의 앞뒤를 노릇하게 지져낸다. 그릇에 담고 초간장을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (824, 153, 1, '쇠고기는 힘줄 없는 부위를 사용한다. 길이 6cm, 너비 1cm, 두께 0.4cm로 자른 다음, 칼집을 넣는다. 분량의 양념 재료로 양념장을 만들어 1/2 분량에 재웠다가 프라이팬에 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (825, 153, 2, '마른 표고버섯은 따뜻한 물에 불린 뒤 기둥을 떼어낸다. 표고버섯, 도라지, 당근, 오이는 너비 1cm, 길이 5cm, 두께 0.4cm로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (826, 153, 3, '표고버섯은 남은 양념장에 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (827, 153, 4, '도라지는 소금에 비벼 씻은 다음 헹구어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (828, 153, 5, '프라이팬에 식용유를 두르고 표고버섯, 도라지, 당근, 오이를 각각 볶아내면서 소금과 참기름으로 간을 해준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (829, 153, 6, '산적꼬치에 준비된 재료를 색 맞추어 꽂은 다음 잣가루를 뿌려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (830, 154, 1, '토란은 껍질을 벗기고 큰 것은 2~4등분한다. 쌀뜨물에 소금을 약간 넣고 끓으면 토란을 넣고 살짝 삶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (831, 154, 2, '쇠고기는 끓는 물에 덩어리째 넣고 삶다가 다시마, 무를 넣어 푹 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (832, 154, 3, '어느정도 국물이 우러나면 건더기를 건지고 쇠고기, 무는 0.5cm 두께로 썰어 다진 마늘, 후춧가루, 참기름으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (833, 154, 4, '사태국물을 다시 끓이다가 양념한 쇠고기와 무를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (834, 154, 5, '④에 토란, 다시마를 넣고 국간장으로 간을 맞춘 다음 한소끔 끓여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (835, 154, 6, '마지막에 어슷 썬 파를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (836, 155, 1, '쇠고기는 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (837, 155, 2, '목이버섯은 미온수에 불린 후 찬물로 헹구어 물기를 짜서 적당한 크기로 뜯고, 느타리버섯은 끓는 물에 데친 후 찬물에 헹구어 물기를 짠 후 손으로 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (838, 155, 3, '실파는 4cm 길이로 썰고 홍고추는 씨를 털어 4cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (839, 155, 4, '분량의 양념 재료를 섞어 양념장을 만들고 양념장을 나누어 쇠고기, 느타리버섯, 목이버섯을 각각 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (840, 155, 5, '전골냄비에 준비한 재료를 돌려 담고 물 3컵을 붓고 끓여 국간장과 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (841, 156, 1, '고등어는 머리, 꼬리를 떼어내고 3cm 크기로 어슷썰어 내장을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (842, 156, 2, '무는 2cm 두께로 썰어 큰 것은 4등분, 작은 것은 2등분하여 끓는 물에 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (843, 156, 3, '파는 3cm 길이로 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (844, 156, 4, '냄비에 고등어와 무를 분량의 양념 재료로 만든 양념장의 2/3를 얹어 뚜껑을 덮고 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (845, 156, 5, '끓어 오르면 은근한 불에서 양념장을 숟가락으로 끼얹어 주며 조리다가 나머지 양념장을 끼얹어 센불에서 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (846, 156, 6, '고등어와 무를 그릇에 담고 파를 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (847, 157, 1, '끓는 물에 식용유 1큰술과 당면을 넣고 삶다가 투명하게 삶아지면 소쿠리에 밭쳐서 물에 한 번 헹궈 간장 2큰술과 참기름 1큰술에 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (848, 157, 2, '쇠고기는 기름기를 떼어내고 결 방향대로 곱게 채썰어 핏물을 뺀 다음 분량의 양념 재료로 쇠고기 양념을 만들어 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (849, 157, 3, '느타리버섯은 끓는 물에 소금을 약간 넣고 데쳐서 찬물에 헹군 뒤 물기를 꼭 짜서 가늘게 찢어 소금과 참기름에 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (850, 157, 4, '오이는 소금으로 문질러 씻은 뒤 4cm 길이로 돌려 깎아서 가늘게 채썰어 소금에 살짝 절이고 당근은 4cm 길이로 곱게 채썰고, 양파도 곱게 채썬다. 배는 껍질을 벗겨 씨를 도려낸 다음 4cm 길이로 채썰어서 색이 변하지 않도록 설탕물에 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (851, 157, 5, '프라이팬에 느타리버섯, 오이, 당근, 쇠고기 순으로 소금과 후춧가루로 간하여 재빨리 볶아서 넓은 쟁반에 펴서 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (852, 157, 6, '큼직한 볼에 당면과 ⑤를 넣고 무쳐서 소금으로 간하고, 채썰어놓은 배와 통깨를 넣고 살살 무쳐서 접시에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (853, 158, 1, '양파와 파, 마늘은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (854, 158, 2, '된장 3큰술과 고추장 1큰술에 ①과 고춧가루를 넣고 잘 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (855, 158, 3, '먹기 직전에 참기름을 넣고 잘 섞어 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (856, 159, 1, '배추김치는 속을 털어내고 국물을 짠 후 2cm 두께로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (857, 159, 2, '돼지고기를 채썰어 간장 1작은술, 청주 1큰술, 후춧가루 약간을  넣고 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (858, 159, 3, '죽순, 양송이버섯은 모양대로 얇게 썰고 피망도 채썬다. 마른 고추를 어슷썰어 씨를 털어내고 대파, 마늘, 생강은 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (859, 159, 4, '프라이팬에 식용유를 두르고 충분히 달궈지면 마른 고추를  볶다가  대파, 마늘, 생강을 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (860, 159, 5, '④에 돼지고기를 넣어 센불에서 볶은 후 김치, 죽순, 양송이버섯을 넣어 같이 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (861, 159, 6, '⑤에  피망을 넣어 볶고 소금, 후춧가루로 간하고 참기름으로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (862, 160, 1, '닭고기는 깨끗이 씻어 앞뒤로 칼집을 넣어주고 소금, 후춧가루, 청주로 밑간을 해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (863, 160, 2, '분량의 양념 재료를 섞어 양념장을 만들고 ①에 버무려 30분 가량 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (864, 160, 3, '녹말가루와 튀김가루를 섞어 재워둔 닭고기에 묻히고 160℃의 기름에서 바삭하게 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (865, 161, 1, '오징어는 몸통과 다리를 분리하고 몸통은 소금을 뿌렸다가 껍질을 벗기고 다리는 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (866, 161, 2, '표고버섯은 밑동을 제거하고 두부는 면보에 싸서 물기를 짠다. 당면은 끓는 물에 데쳐 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (867, 161, 3, '표고버섯, 두부, 당면, 쇠고기, 당근, 피망은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (868, 161, 4, '간장, 식초, 깨소금을 섞어 초간장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (869, 161, 5, '깻잎과 양상추는 곱게 채썬 다음 샐러리 잎과 함께 얼음물에 담가놓고 오븐은 200℃로 예열해놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (870, 161, 6, '다져놓은 오징어다리, 표고버섯, 두부, 당면, 쇠고기, 당근, 피망을 용기에 담아 양념 재료를 차례로 넣고 골고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (871, 162, 1, '표고버섯은 따뜻한 물에 불려 밑동을 떼어내서 채썰고 죽순도 깨끗이 씻어 빗살 모양으로 얇게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (872, 162, 2, '새우살은 끓는 물에 소금을 약간 넣어 데쳐낸다. 새우를 삶아낸 물 1과 1/2컵을 남겨서 소스용 재료로 사용한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (873, 162, 3, '달군 팬에 채썬 표고버섯, 죽순, 새우살, 완두콩을 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (874, 162, 4, '식용유에 누룽지를 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (875, 162, 5, '튀겨낸 누룽지에 ③을 넣고 홍고추, 다진 마늘을 넣고 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (876, 162, 6, '팬에 분량의 소스 재료를 넣어 끓인 후 녹말물을 풀어 넣어 걸쭉한 소스를 만든 다음 ⑤에 붓고 살짝 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (877, 163, 1, '가래떡은 3cm 길이로 썰어서 끓는 물에 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (878, 163, 2, '파프리카는 떡 크기에 맞춰 썰어 살짝 데치고, 브로콜리는 작은 줄기로 떼어내 끓는 소금물에 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (879, 163, 3, '분량의 양념 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (880, 163, 4, '꼬치에 떡, 파프리카, 브로콜리 순으로 꽂는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (881, 163, 5, '④에 양념장을 발라 떡이 고루 익을 때까지 그릴에 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (882, 164, 1, '양파, 당근은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (883, 164, 2, '실파는 송송썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (884, 164, 3, '두부는 곱게 으깬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (885, 164, 4, '찬밥, 양파, 당근, 실파, 두부를 섞고 콩가루와 계란을 넣은 뒤 소금과 후춧가루로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (886, 164, 5, '프라이팬에 기름을 두르고 둥글게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (887, 165, 1, '쌀은 씻어서 30분 정도 불린 후 소쿠리에 건져 물기를 빼고, 팥은 씻어서 냄비에 담고 팥이 잠길 정도로 물을 부어 끓인 뒤 살짝 끓어오르면 물을 버린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (888, 165, 2, '①에 다시 물 8컵을 부어 1시간 정도 푹 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (889, 165, 3, '②에 불린 쌀을 넣고 주걱으로 한 번씩 저어가며 쌀알이 완전히 퍼질 때까지 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (890, 165, 4, '찹쌀과 쌀가루를 섞어서 가루 낸 것에 찬물 3/4컵을 부어 반죽해서 지름 1~2cm 정도의 새알심을 동그랗게 빚는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (891, 165, 5, '끓는 소금물에 ④를 넣고 새알심이 익어서 위로 떠오르면 찬물에 식혀 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (892, 165, 6, '센불에서 죽이 끓을 때 새알심을 넣고 소금으로 간을 맞춘 뒤 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (893, 166, 1, '냉장고에 보관해둔 버터를 주사위 모양으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (894, 166, 2, '체에 밀가루와 소금을 넣고 쳐서 큰 볼에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (895, 166, 3, '②에 버터 썬 것을 넣고 섞은 후 버터에 밀가루를 입힌다. 버터를 손으로 한 개씩 으깬 다음 양손으로 비벼서 보슬보슬한 가루를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (896, 166, 4, '찬물을 넣고 손가락으로 원을 그리면서 섞은 후 대충 덩어리로 뭉쳐서 비닐백에 담아 밀대로 5mm 정도 두께로 밀어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (897, 166, 5, '④를 냉장고에 1~2시간 정도 넣어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (898, 166, 6, '아몬드 크림을 만든다. 실온에 두었던 버터를 볼에 담고 거품기로 저어서 크림 상태로 만든다. 여기에 설탕을 넣고 힘있게 섞은 후 계란을 넣고 잘 섞어준 뒤 아몬드 가루를 넣고 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (899, 167, 1, '고구마 줄기는 미지근한 물에 한시간 정도 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (900, 167, 2, '끓는 물에 고구마 줄기를 20분 정도 삶아낸 뒤 찬물에 헹구어 물을 갈아주며 하루 정도 물에 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (901, 167, 3, '분량의 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (902, 167, 4, '고구마 줄기는 물기를 짜고 5cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (903, 167, 5, '④에 양념장을 넣어 간이 배도록 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (904, 167, 6, '프라이팬에 들기름을 두르고 ⑤를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (905, 168, 1, '어묵은 가운데 1/3 정도를 파낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (906, 168, 2, '닭고기 안심은 곱게 다져 소금, 후춧가루를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (907, 168, 3, '말린 표고버섯은 미지근한 물에 불려 기둥을 떼어내고 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (908, 168, 4, '오이는 돌려 깎아 채썬 뒤 소금에 살짝 절이고 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (909, 168, 5, '닭고기살에 표고버섯, 오이를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (910, 168, 6, '어묵 가운데 녹말가루를 뿌리고 ⑤를 채운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (911, 169, 1, '쇠고기는 찬물에 담가 핏물을 없애고 양지머리와 함께 한번 끓여 첫 물을 버리고, 다시 푹 고아서 육수를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (912, 169, 2, '계란은 노른자와 흰자를 나누어 지단을 부친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (913, 169, 3, '쇠고기는 채썰거나 다져서 양념 재료로 양념하여 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (914, 169, 4, '가래떡을 어슷썰어 물에 씻어 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (915, 169, 5, '①의 육수에 간장과 소금으로 간을 맞추고  채썬 파와 다진 마늘을 넣어 팔팔 끓으면 떡을 헤쳐서 넣고, 떡이 익어서 떠오를 때까지 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (916, 169, 6, '대접이나 그릇에 떡국을 담고 위에 고기와 지단을 고명으로 얹고 후춧가루를 약간 뿌려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (917, 170, 1, '멥쌀은 깨끗이 씻어서 6시간 정도 충분히 불린 후 건져 소금을 넣고 가루로 빻아서 곱게 체에 친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (918, 170, 2, '멥쌀가루에 물을 뿌린 다음 버무려서 찜통이나 시루에 넣고 충분히 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (919, 170, 3, '쪄낸 떡을 둘로 나누어 반은 그대로 절구에서 찰지게 될 때까지 치고, 나머지는 데친 쑥을 섞어서 쑥색이 고루 들 때까지 찧는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (920, 170, 4, '③의 떡을 큰 도마에 놓고 참기름을 발라가며 일정한 크기로 떼어낸 다음 둥근 모양으로 빚고 떡살로 눌러 문양을 낸 후 참기름을 발라 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (921, 171, 1, '생강은 껍질을 벗겨 씻은 뒤 얇게 저민다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (922, 171, 2, '통계피는 씻어서 잘게 부순다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (923, 171, 3, '곶감은 가루를 털고 젖은 수건으로 표면을 깨끗이 닦은 뒤 꼭지와 밑동을 살짝 자른 다음 옆면을 칼로 잘라 씨를 발라낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (924, 171, 4, '호두는 통째로 미지근한 설탕물에 잠시 담갔다가 꺼내어 꼬치로 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (925, 171, 5, '곶감을 넓게 편 다음 호두를 넣고 김발로 꼭꼭 말아 곶감쌈을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (926, 171, 6, '생강은 찬물 10컵을 붓고 향이 우러나도록 약한 불에서 30~40분간 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (927, 172, 1, '닭고기는 토막 내어 소금, 후춧가루, 정종에 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (928, 172, 2, '홍고추, 청고추, 대파, 생강, 마늘은 굵게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (929, 172, 3, '소스 재료를 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (930, 172, 4, '닭고기에 계란을 넣고 감자전분을 넣어 무치듯 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (931, 172, 5, '170℃ 기름에 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (932, 172, 6, '프라이팬에 기름을 넣고 대파, 마늘, 생강, 홍고추, 청고추를 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (933, 173, 1, '멥쌀과 찹쌀은 불린 다음 소금 1큰술을 넣고 곱게 빻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (934, 173, 2, '①에 물을 10큰술 정도 넣어 손으로 비비면서 다시 한 번 체에 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (935, 173, 3, '팥은 물을 붓고 푹 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (936, 173, 4, '충분히 물러졌으면 남은 물을 따라내고 잠깐 불에 올려 뜸을 들인 후 소금 1큰술과 함께 절구에 넣고 팥알이 부서지도록 대강 찧는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (937, 173, 5, '시루에 면보를 깔고 ④의 팥고물을 평평하게 깐 다음 ②의 쌀가루를 2cm 두께로 번갈아 안쳐 김 오른 솥 위에 시루째 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (938, 173, 6, '김이 새지 않도록 솥과 시루 사이에 밀가루 반죽을 붙인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (939, 174, 1, '냄비에 물, 다시마, 무를 넣어 끓여 국물을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (940, 174, 2, '두부는 물기를 짠 후 곱게 으깨고 실파를 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (941, 174, 3, '②에 다진 쇠고기, 소금, 후춧가루를 넣어 골고루 섞은 후 2㎝ 크기의 완자를 빚는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (942, 174, 4, '①의 무를 건져 얄팍하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (943, 174, 5, '①의 국물에 국간장, 소금, 후춧가루로 간을 한 후 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (944, 174, 6, '⑤가 끓어오르면 완자와 ④의 무를 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (945, 175, 1, '두부는 물기를 짠 후 곱게 으깨어 다진쇠고기와 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (946, 175, 2, '①에 소금, 후춧가루, 깨소금, 참기름을 넣어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (947, 175, 3, '②를 치대어 직경 3㎝ 크기의 완자를 빚는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (948, 175, 4, '밤은 껍질을 까서 물에 담가놓고 무, 당근은 밤 크기로 썰어 끓는 물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (949, 175, 5, '분량의 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (950, 175, 6, '냄비에 양념장을 넣어 끓어오르면 완자를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (951, 176, 1, '오징어는 껍질을 벗기고 칼집을 넣어 썰고 5cm x 1cm 크기로 썰고 낙지는 7~8cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (952, 176, 2, '새우는 껍질을 벗기고 등 쪽의 내장을 꺼낸다. 조갯살도 잘 씻어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (953, 176, 3, '양파, 청피망, 홍피망, 당근은  채썰어 식용유를 두른 팬에 재빨리 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (954, 176, 3, '끓는 물에 해물을 넣어 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (955, 176, 5, '당면은 삶은 후 물기를 빼고 썰어 간장, 설탕, 참기름으로 무쳐놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (956, 176, 6, '식용유를 두른 팬에 양념한 당면을 볶고 채소와 해산물을 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (957, 177, 1, '물에 다시마와 말린표고버섯을 넣고 2~3시간 정도 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (958, 177, 2, '계란은 황백으로 나눠 지단을 부쳐 채썰고 대파는 어슷썰기 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (959, 177, 3, '냄비에 ①의 물과 다시마, 표고버섯을 넣고 끓기 시작하면 다시마를 건져 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (960, 177, 4, '③에 간장과 소금으로 간을 맞춘 후 떡국 떡을 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (961, 177, 5, '④에 다진 마늘과 대파를 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (962, 177, 6, '⑤를 그릇에 담고 지단을 올려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (963, 178, 1, '찹쌀은 씻어 30분 이상 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (964, 178, 2, '팥은 한 번 삶아 물을 버리고 다시 물을 넣고 팥알이 터지지 않게 삶은 뒤 팥물은 받아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (965, 178, 3, '밤은 껍질을 까서 먹기 좋은 크기로 잘라 놓고 대추도 씨를 발라 적당한 크기로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (966, 178, 4, '준비한 재료를 모두 넣고 1과 1/3컵의 팥물에 소금 1/2작은술을 넣어 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (967, 179, 1, '쌀은 씻어 건져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (968, 179, 2, '콩나물은 통통한 것으로 다듬어 씻어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (969, 179, 3, '돼지고기는 살코기로 준비해 4~5㎝ 길이로 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (970, 179, 4, '무는 깨끗이 씻어서 폭 0.5㎝ 길이 4~5㎝ 길이로 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (971, 179, 5, '취나물은 끓는 물에 소금을 조금 넣고 데쳐서 찬물에 1시간 정도 담가 쓴맛을 우려낸다. 여러번 헹군 뒤 물기를 꼭 짜서 4㎝ 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (972, 179, 6, '돼지고기에 양념을 해서 프라이팬에 볶아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (973, 180, 1, '모둠 채소는 흐르는 물에 깨끗이 씻어 소쿠리에 물기를 빼 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (974, 180, 2, '케일은 끓는 물에 데쳐 찬물에 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (975, 180, 3, '밥에 소금과 참기름으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (976, 180, 4, '케일 및 쌈야채에 ③을 먹기 좋은 크기로 한 덩이씩 싼 뒤 흑임자를 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (977, 180, 5, '준비한 양념 재료를 섞어 쌈장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (978, 180, 6, '그릇에 채소와 케일 쌈밥을 담고 쌈장을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (979, 181, 1, '말린 표고버섯은 물에 불려 굵게 채썰고 기둥은 결대로 찢는다.(불린 물은 ②에 섞어 육수로 사용한다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (980, 181, 2, '멸치는 내장과 머리를 발라낸 후 냄비에 물 2컵과 ①의 표고버섯 불린 물을 붓고 된장을 풀어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (981, 181, 3, '두부는 사방 1.5cm로 깍둑썰어 준비하고 달래는 길이로 3등분하며 대파, 청양고추는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (982, 181, 4, '바지락은 연한 소금물에 담가 해감시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (983, 181, 5, '②가 끓으면 두부와 해감 시킨 바지락을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (984, 181, 6, '⑤에 나머지 재료인 달래, 표고버섯, 대파, 청양고추를 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (985, 182, 1, '마른 표고버섯은 따뜻한 물에 불려 밑둥을 없애고 물기를 꼭 짠 다음 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (986, 182, 2, '쑥의 질긴 부분을 없앤 후 끓는 물에 데쳐 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (987, 182, 3, '느타리버섯은 끓는 물에 데쳐 물기를 없애고 손으로 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (988, 182, 4, '팬에 식용유를 두르고 다진 마늘을 볶다가 버섯재료와 쑥을 넣어 센불에 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (989, 182, 5, '간장, 설탕, 소금, 후춧가루로 간을 해 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (990, 183, 1, '닭가슴살은 옆으로 갈라 넓게 편 후 소금, 후춧가루를 살짝 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (991, 183, 2, '명란젓은 껍질을 벗기고 마요네즈를 넣어 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (992, 183, 3, '닭가슴살에 녹말가루를 뿌리고 김을 깐 뒤 ②를 놓고 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (993, 183, 4, '③에 밀가루, 계란, 빵가루를 입혀 160℃의 기름에서 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (994, 183, 5, '당근과 대파는 가늘게 채썰어 그릇에 담고 튀긴 닭고기를 썰어 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (995, 184, 1, '바지락은 연한 소금물에 담가 해감시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (996, 184, 2, '봄동은 깨끗이 다듬어 씻은 후 물기를 빼고 먹기 좋은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (997, 184, 3, '홍고추는 굵게 다지고 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (998, 184, 4, '달구어진 팬에 식용유를 두르고 마늘을 볶다가 물을 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (999, 184, 5, '④가 끓으면 바지락, 청주를 넣고 뚜껑을 덮어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1000, 184, 6, '봄동과 굴소스를 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1001, 185, 1, '김치는 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1002, 185, 2, '감자는 삶아서 체에 내리고 두부는 물기를 짠 뒤 으깬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1003, 185, 3, '당근,피망은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1004, 185, 4, '준비된 재료에 밀가루, 계란, 다진마늘, 소금, 후춧가루를 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1005, 185, 5, '프라이팬에 식용유를 두르고 반죽을 한 숟가락씩 떠서 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1006, 186, 1, '옥수수는 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1007, 186, 2, '찬밥에 옥수수, 소금, 계란을 넣고 잘 섞은 후 녹말가루를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1008, 186, 3, '지름 2㎝ 정도로 완자를 빚어(사진1) 170℃의 식용유에서 노릇노릇하게 튀겨 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1009, 186, 4, '식용유 2큰술에 설탕 1/2컵을 넣고 물을 1큰술 정도 넣어 설탕이 녹을 때까지 끓여 시럽을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1010, 186, 5, '④의 시럽에 튀겨 놓은 맛탕을 버무려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1011, 187, 1, '양배추는 두꺼운 부분을 도려내고 가로 5㎝ 길이로 채썰고, 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1012, 187, 2, '모시조개는 소금물에 담가 해감을 토하게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1013, 187, 3, '냄비에 물을 붓고 다시마와 멸치를 넣어 끓으면 불을 끄고 체에 면보를 깔아 건더기를 걸러내 다시물을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1014, 187, 4, '다시물에 된장을 풀어 다시 한번 끓인 다음 양배추, 모시조개를 넣고 다진 마늘을 넣어 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1015, 187, 5, '그릇에 담아 송송 썬 실파를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1016, 188, 1, '오징어는 내장을 제거하고 껍질을 벗긴 뒤 0.5㎝ 두께의 링 모양으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1017, 188, 2, '쇠고기는 0.2㎝ 두께로 채썬 뒤 간장, 후춧가루로 밑간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1018, 188, 3, '풋고추, 홍고추, 대파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1019, 188, 4, '냄비에 물을 붓고 ②의 쇠고기를 넣어 끓으면 양념 재료를 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1020, 188, 5, '④에 오징어, 풋고추, 홍고추, 대파를 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1021, 189, 1, '오이는 통통하고 길이가 짧은 것으로 골라 깨끗하게 씻어 건진 다음 소금을 뿌려 3시간 정도 절인 뒤 물기를 닦아 준비하는데, 피클용 오이가 없으면 일반 오이를 먹기 좋은 크기로 썰어서 절인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1022, 189, 2, '홍고추는 꼭지를 잘라 깨끗이 씻어 물기를 제거해두고, 생강은 편썰고 당근, 양파는 큼직하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1023, 189, 3, '소독한 병에 절인 오이와 홍고추, 당근, 양파를 차곡차곡 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1024, 189, 4, '분량의 물과 설탕, 식초, 소금, 정향, 통후추, 월계수잎, 통계피, 생강편을 섞어 팔팔 끓여 바로 ③의 병에 3/5 정도 채워지도록  붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1025, 189, 5, '김이 오른 찜통에 피클 병을 넣고 병 뚜껑은 살짝 열고 찜통 속에 5분 정도 두었다가 찜통 안에서 병 뚜껑을 꼭 닫은 후 꺼내어 실온에 두고 숙성시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1026, 190, 1, '멥쌀과 찹쌀은 깨끗이 씻어 30분간 불린 다음 체에 밭쳐 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1027, 190, 2, '밤은 껍질을 벗겨 적당한 크기로 자르고 은행은 식용유를 두르고 볶아 껍질을 벗긴다. 대추는 씨를 빼고 3~4등분한다. 송이버섯은 먹기 좋은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1028, 190, 3, '돌솥에 참기름을 바르고 ①의 쌀을 볶다가 물을 쌀 위로 1cm 정도 올라오게 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1029, 190, 4, '③에 밤, 은행, 대추, 잣을 넣고 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1030, 190, 5, '뜸들일 때 버섯을 밥 위에 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1031, 191, 1, '시금치는 끓는 물에 소금을 약간 넣어 살짝 데쳐낸 뒤 찬물에 헹궈 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1032, 191, 2, '냄비에 말린새우, 물을 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1033, 191, 3, '된장을 체에 걸러 푼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1034, 191, 4, '끓으면 시금치, 다진 마늘을 넣어 끓인 뒤 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1035, 192, 1, '김치는 물기를 살짝 짜서 씻고, 깻잎도 깨끗이 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1036, 192, 2, '양배추와 근대 잎은 끓는 물에 데쳐 내거나 찜통에 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1037, 192, 3, '쇠고기는 분량의 양념을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1038, 192, 4, '프라이팬에 식용유를 두르고 쇠고기를 볶다가 밥을 넣어 볶은 후 소금, 후춧가루로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1039, 192, 5, '김치, 깻잎, 양배추, 근대 잎에 ④를 넣어 각각 말아 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1040, 193, 1, '팥에 물을 부어 한소끔 끓으면 물을 버리고, 다시 물을 부어 팥을 삶은 후, 삶은 팥과 팥 삶은 물을 따로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1041, 193, 2, '밤과 대추는 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1042, 193, 3, '불려 놓은 찹쌀과 멥쌀, 삶은 팥, 밤, 대추, 소금, 팥 삶은 물을 한꺼번에 넣고 찜통에서 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1043, 193, 4, '③의 쪄낸 찰밥을 랩에 적당량 넣고 동그랗게 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1044, 194, 1, '다시마는 마른 행주로 닦은 후 냄비 바닥에 깔아 쌀을 넣어 고슬하게 밥을 짓고, 다시마를 빼낸 후 분량의 배합초를 넣어 버무려 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1045, 194, 2, '볼에 계란을 풀고 소금간을 한 뒤 프라이팬에 식용유를 두르고 계란 지단을 부친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1046, 194, 3, '지단이 식으면 가로 x 세로가 20cm x 20cm 정도가 되도록 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1047, 194, 4, '표고버섯을 불린 다음 얇게 채썰고, 오이는 돌려깎기한 후 껍질 부분만 채를 썰고, 당근은 곱게 채썰어 각각 볶은 후 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1048, 194, 5, '미나리는 잎을 떼고 끓는 소금물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1049, 194, 6, '계란 지단 위에 밥을 펴고 볶아 둔 표고버섯, 오이, 당근을 올리고 김밥 말듯이 말아준 후 데친 미나리로 한입 크기 마다 묶어서 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1050, 195, 1, '어묵은 먹기 좋은 크기로 썰어 끓는 물에 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1051, 195, 2, '대파와 홍고추는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1052, 195, 3, '다시물에 간장, 맛술, 청주를 넣어 육수를 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1053, 195, 4, '육수가 끓으면 어묵, 대파, 홍고추를 넣고 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1054, 196, 1, '모시조개는 연한 소금물에 담가 해감 시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1055, 196, 2, '죽순은 2㎝ 길이의 빗살 모양으로 썰고 대파는 어슷썬다. 레몬은 노란 껍질 부분을 모양 내어 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1056, 196, 3, '찬물에 ①의 조개를 넣고 한소끔 끓으면 면보에 거르고 조개는 따로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1057, 196, 4, '조개 국물만 냄비에 넣고 끓으면 삶은 조개와 죽순을 넣고 소금으로 간을 한 뒤 썰어놓은 대파와 레몬 껍질을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1058, 197, 1, '두부는 1㎝ 깍둑썰기하여 끓는 물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1059, 197, 2, '미역은 물에 불려 끓는 물에 데친 뒤 잘게 자르고, 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1060, 197, 3, '맛국물이 끓으면 된장을 풀고 한소끔 끓으면 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1061, 197, 4, '그릇에 두부, 미역, 실파를 넣고 ③의 국물을 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1062, 198, 1, '호박, 당근, 죽순은 잘게 다져 끓는 물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1063, 198, 2, '새우와 달걀흰자를 믹서기에 넣고 갈아 걸쭉하게 만든 다음 생크림, 소금, 호박, 당근, 죽순, 콘프레이크와 통후추를 빻아 넣어 잘 섞어 만두소를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1064, 198, 3, '만두피에 만두소를 넣고 다른 만두피로 덮어 만두를 동그랗게 만든 다음 쿠키 커터로 찍어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1065, 198, 4, '찜통에 만두를 쪄서 초간장을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1066, 199, 1, '쌀은 씻어서 물에 담가 충분히 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1067, 199, 2, '잣은 고깔을 떼고 마른 면보에 싸서 문질러 닦고 6개만 고명용으로 따로 남겨놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1068, 199, 3, '믹서에 불린 쌀과 물 2컵을 부어서 곱게 갈아 놓고, 잣도 물 1/3컵을 넣고 곱게 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1069, 199, 4, '대추는 깨끗이 씻어서 돌려 깎은 후 껍질을 방망이로 살짝 밀어 동그랗게 말아 얇게 썰어 대추꽃을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1070, 199, 5, '냄비에 갈아놓은 쌀과 물 1컵을 부은 뒤 불에 올려, 바닥이 눌지 않도록 저어가며 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1071, 199, 6, '쌀이 끓어서 퍼지기 시작하고 어느 정도 익으면 잣 갈아놓은 것을 넣고 이 때부터는 아주 약한 불에서 알맞은 농도가 될 때까지 끓이고, 먹기 직전에 간을 해서 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1072, 200, 1, '청고추는 맵지 않은 것으로 골라 씻어 밀폐 용기에 담은 다음  소금물을 붓고 고추가 떠오르지 않게 대발이나 접시를 얹어  2주일 정도 노랗게 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1073, 200, 2, '삭힌 고추를 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1074, 200, 3, '간장, 설탕, 채썬 생강을 넣어 장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1075, 200, 4, '밀폐 용기에 삭힌 고추를 담고 ③의 장을 부은 다음 뚜껑을 덮는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1076, 200, 5, '④를 2~3일 정도 두었다가 간장만 냄비에 따라내어 팔팔  끓인  다음 식힌다. ⑤를 고추에 다시 부어 3일간 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1077, 201, 1, '생선살과 새우살은 물기를 뺀 후 곱게 다진다.(또는 믹서기를 이용해 곱게 갈아 준다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1078, 201, 2, '김치는 물기를 짜고 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1079, 201, 3, '풋고추, 홍고추는 반으로 갈라 씨를 빼고 0.1㎝ x 0.1cm 크기로 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1080, 201, 4, '①, ②, ③의 준비한 재료에 계란, 밀가루, 녹말, 소금, 후춧가루를 넣어 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1081, 201, 5, '④를 깻잎 위에 깔고 말아 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1082, 201, 6, '170℃의 식용유에서 노릇노릇하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1083, 202, 1, '양지머리는 덩어리째 30분 정도 찬물에 담가 핏물을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1084, 202, 2, '무는 큼직하게 토막 낸다. 굵은 파는 10㎝길이로 썰고 마늘은 통으로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1085, 202, 3, '무는 납작하게 썰고 가는 파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1086, 202, 4, '냄비에 양지머리,굵은 파,마늘,물을 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1087, 202, 5, '국물이 끓으면 무를 넣고 불을 줄여 끓이면서 떠오르는 거품은 걷어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1088, 202, 6, '쇠고기와 무가 익으면 건져 쇠고기는 결대로 찢어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1089, 203, 1, '마늘은 얇게 편으로 썰고, 무는 1.5cm x 1.5cm 크기로 나박썰기 하고, 두부도 같은 크기로 썰어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1090, 203, 2, '명란은 길이로 칼집을 낸 뒤 3~4등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1091, 203, 3, '홍고추와 대파는 어슷 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1092, 203, 4, '뚝배기에 무, 마늘, 물을 넣고 끓이다가 무가 익으면 불을 줄이고 두부를 먼저 넣은 뒤 명란을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1093, 203, 5, '중간불에서 가열하다 명란의 속이 하얗게 익으면 불을 끄고 뚜껑을 덮은 채 그대로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1094, 203, 6, '먹기 직전에 고추와 간장, 파, 소금을 넣고 한소끔 더 끓여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1095, 204, 1, '양지머리는 찬물에 30분 정도 담가 핏물을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1096, 204, 2, '양은 굵은 소금을 넣어 주물러 씻은 다음 끓는 물에 살짝 데쳐 칼이나 숟가락으로 표면의 검은 부분을 긁어 내고 안쪽의 얇은 막을 떼어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1097, 204, 3, '곱창은 굵은 소금으로 주물러 씻고 겉의 기름기를 떼어 낸 다음 속은 흐르는 물로 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1098, 204, 4, '냄비에 물을 부어 끓으면 양지머리, 양, 곱창을 넣어 센 불에서 끓이다가 은근한 불에서 2시간 정도 끓여 건진다. 끓이는 도중에 떠오르는 기름과 거품은 걷어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1099, 204, 5, '대파와 고사리는 7㎝ 길이로 썰고, 대파는 끓는 물에 데쳐 찬물에 헹구어 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1100, 204, 6, '양지머리는 결대로 찢고 양파 곱창은 먹기 좋은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1101, 205, 1, '스파게티면을 끓는 물에 소금을 약간 넣고 삶아 오일에 살짝 버무려둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1102, 205, 2, '호박은 채썰고 양송이버섯은 모양대로 썰고 김치는 잘게 썰어 소금, 후춧가루로 간을 해서 각각 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1103, 205, 3, '식용유를 두른 프라이팬에 분량의 다진 마늘, 다진 양파를 볶다가 양파가 투명해지면 고추장, 토마토케첩, 설탕을 넣고 볶다가 물을 넣고 은근히 졸여 고추장 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1104, 205, 4, '소스가 끓으면 다진 호두를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1105, 205, 5, '소스에 스파게티 국수를 버무린 다음 그릇에 담고 볶은 채소를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1106, 206, 1, '무는 납작하게 썰어서 냄비에 넣고 물 5컵 정도를 부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1107, 206, 2, '게는 등딱지를 떼고 내장을 빼낸 뒤 다리의 끝부분을 잘라내고 먹기 좋은 크기로 토막을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1108, 206, 3, '새우는 등 쪽의 내장을 꼬치로 빼고 엷은 소금물에 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1109, 206, 4, '오징어는 몸통을 잡고 다리를 잡아당겨 내장을 빼낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1110, 206, 5, '양파는 채썰고, 고추와 대파는 어슷하게 썰고 고추는 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1111, 206, 6, '미나리는 잎을 떼고 다듬어 씻어서 4㎝ 길이로 썰고, 쑥갓도 연한 부분만 씻어서 4㎝ 길이로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1112, 207, 1, '양상추는 끓는 물에 살짝 데쳐서 찬물에 헹군 후 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1113, 207, 2, '말린표고버섯은 물에 불려 물기를 제거한 뒤 얇게 채썰어 마늘, 설탕, 간장, 참기름, 후춧가루를 넣어 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1114, 207, 3, '고구마는 표고버섯과 같은 길이로 가늘게 채썰고, 부추도 길이를 맞추어 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1115, 207, 4, '물기를 제거한 양상추는 안쪽에 밀가루를 살짝 묻힌 뒤 ②와 ③을 적당량 넣어 돌돌 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1116, 207, 5, '찹쌀가루, 밀가루, 얼음물, 계란, 소금을 넣어 튀김옷을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1117, 207, 6, '④에 ⑤의 튀김옷을 살짝 입혀 170℃의 온도에서 튀겨 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1118, 208, 1, '닭은 내장을 빼고 깨끗하게 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1119, 208, 2, '대파는 10㎝ 길이로 썰고 생강은 편으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1120, 208, 3, '냄비에 닭, 대파, 마늘, 생강, 물을 넣고 1시간 정도 끓인다. 끓이는 도중에 떠오르는 기름과 거품은 걷어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1121, 208, 4, '국물은 식혀서 위에 뜬 기름을 걷어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1122, 208, 5, '닭은 껍질을 벗기고 손으로 먹기 좋게 찢어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1123, 208, 6, '도라지는 얇게 채썰고 미나리는 잎을 떼어내고 3㎝ 길이로 썰고 끓는 물에 데쳐 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1124, 209, 1, '삼겹살에 소금, 후춧가루, 생강즙을 뿌린 뒤 무명실로 단단하게 감는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1125, 209, 2, '김오른 찜통에 ①을 넣고 40분 동안 쪄낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1126, 209, 3, '②가 식으면 0.6㎝두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1127, 209, 4, '깻잎은 줄기를 제거하고 길이로 반 가른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1128, 209, 5, '깻잎의 앞, 뒤쪽에 각각 밀가루를 묻힌 뒤 안쪽에 삼겹살 썬 것을 넣고 감싼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1129, 209, 6, '⑤에 계란을 입혀서 식용유를 두른 프라이팬에서 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1130, 210, 1, '냄비에 우유를 붓고 약한 불에서 가열해 80℃정도가 되면 식초를 조금씩 넣으면서  천천히 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1131, 210, 2, '냄비 속의 우유가 약간 노란색을 띠면서 투명하게 응고되면 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1132, 210, 3, '②를 면보에 걸러 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1133, 210, 4, '③을 틀 속에 넣어 눌러 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1134, 210, 5, '완성된 우유두부를 썰어 접시에 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1135, 210, 6, '분량의 양념 재료를 섞어 만든 양념장을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1136, 211, 1, '닭은 중간크기의 것으로 골라서 깨끗이 씻은 후 뼈는 남기고 살을 발라 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1137, 211, 2, '닭고기살에 잔칼집을 넣어 고기가 덜 오그라지게 한 다음 소금, 후추를 약간 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1138, 211, 3, '프라이팬에 식용유를 두르고 뜨거워지면 닭살을 넣고 앞뒤로 눌러서 살짝 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1139, 211, 4, '냄비에 간장, 설탕, 청주, 생강즙, 다시물을 넣어 끓여 약간 걸쭉하게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1140, 211, 5, '④의 냄비에 지져서 기름을 뺀 닭을 집어넣고 은근히 윤기나게 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1141, 211, 6, '닭이 어느정도 익어가면 대파도 4㎝ 길이로 썰어 살짝 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1142, 212, 1, '밀가루와 녹차 분말을 섞어(사진1) 체에 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1143, 212, 2, '①에 소금을 넣어 잘 섞은 후 물을 조금씩 넣으며 반죽한 뒤(사진2) 비닐에 넣어 실온에 30분간 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1144, 212, 3, '호박은 반달썰기 하고 감자는 호박과 같은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1145, 212, 4, '분량의 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1146, 212, 5, '냄비에 육수를 6컵 붓고 감자, 소금을 넣어 끓어오르면 수제비 반죽을 떼어 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1147, 212, 6, '다시 끓어오르면 호박을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1148, 213, 1, '삼겹살은 5㎝ 길이로 자른 뒤 소금, 후춧가루로 밑간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1149, 213, 2, '치즈는 다지고 표고버섯, 양파, 피망은 채썰고 마늘은 편썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1150, 213, 3, '프라이팬에 올리브유를 두르고 마늘을 볶아 향을 낸 뒤 삼겹살을 넣어 노릇하게 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1151, 213, 4, '③에 표고버섯, 양파, 피망을 얹고 위에 구운 마늘과 치즈를 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1152, 213, 5, '④를 200℃의 오븐에 넣어 10분간 노릇하게 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1153, 213, 6, '접시에 ⑤를 담고 분량의 양념 재료를 섞은 소스를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1154, 214, 1, 'LA갈비로 얇게 썰은 것을 다시 길이로 2~3등분 한 뒤 칼집을 넣어 찬물에 한 번 살짝 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1155, 214, 2, '손질한 갈비는 배즙이나 청주에 재워 연하게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1156, 214, 3, '간장에 설탕, 다진 파, 다진 마늘, 후춧가루, 깨소금, 참기름을 넣어 고루 섞어서 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1157, 214, 4, '넓고 낙낙한 그릇에 연하게 재운 갈비를 담고 양념장의 2/3만 덜어 무쳐서 간이 배도록 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1158, 214, 5, '가래떡은 6㎝ 길이로 썰어 굵으면 4등분, 중간 것은 2등분, 떡볶이용 가는떡은 그대로 사용한다.(굳은 것은 끓는 물에 삶아 찬물에 헹구면 부드러워진다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1159, 214, 6, '흰 떡은 간장과 참기름에 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1160, 215, 1, '갑오징어는 가운데 뼈를 빼고 깨끗이 손질하여 가로, 세로로 어슷하게 잔 칼집을 넣은 후 끓는 물에 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1161, 215, 2, '데쳐놓은 오징어는 가로 1.5㎝ 세로 6~7㎝ 정도로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1162, 215, 3, '피망은 꼭지를 떼고 반으로 갈라 속을 털어낸 후 오징어 길이에 맞추어 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1163, 215, 4, '고추장에 설탕, 다진마늘, 다진파, 생강즙, 깨소금, 참기름을 넣어 골고루 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1164, 215, 5, '꼬치에 갑오징어, 피망을 보기 좋게 섞어서 꽂는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1165, 216, 1, '두부는 곱게 으깨어 물기를 짜놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1166, 216, 2, '피망, 당근은 굵게 다져서 프라이팬에 기름을 두르고 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1167, 216, 3, '①에 ②의 야채를 넣고 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1168, 216, 4, '계란에 우유를 넣고 소금과 후춧가루로 간한 다음 팬에 기름을 두르고 따뜻해지면 계란을 둘러 반정도 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1169, 216, 5, '계란 가운데에 ③을 얹고 반을 접어 가장자리로 계란을 밀며 반달모양으로 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1170, 216, 6, '접시에 담아 토마토케첩을 곁들여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1171, 217, 1, '사과는 껍질을 벗겨 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1172, 217, 2, '파이렉스 그릇에 사과를 담고 뚜껑을 덮어 레인지 강에서 8분 정도 익힌 다음 믹서에 갈아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1173, 217, 3, '파이렉스 그릇에 ②의 사과즙을 담고 설탕을 뿌려 레인지 강에서 10분쯤 가열한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1174, 217, 4, '딸기는 꼭지를 떼고 씻어 물기를 없앤 후 잘게 썬 다음 파이렉스 그릇에 담고 설탕을 뿌려 고루 섞어 레인지 강에서 13분정도 가열한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1175, 217, 5, '포도는 알알이 떼어 씻은 후 물기를 닦아 파이렉스 그릇에 담아 레인지 강에서 8분 가열한 후 체에 걸러 설탕을 넣고 다시 레인지 강에서 13분 정도 가열한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1176, 218, 1, '사골은 토막내어 찬물에 1시간 정도 담그고, 양지머리도 덩어리째 찬물에 30분정도 담가 핏물을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1177, 218, 2, '무는 큼직하게 토막을 낸다. 대파는 10㎝ 길이로 썰고 마늘과 생강은 통으로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1178, 218, 3, '냄비에 물을 부어 끓으면 사골을 넣어 끓어오르면 처음 끓인 물은 따라 버린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1179, 218, 4, '냄비에 사골과 물을 넣어 센불에서 끓인 뒤 양지머리, 대파, 마늘, 생강을 넣어 끓인다. 1시간 정도 끓인 후 무를 넣고 푹 끓인다. 끓이는 도중에 떠오르는 기름과 거품을 걷어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1180, 218, 5, '양지머리가 푹 익고 무가 익었으면 대파와 함께 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1181, 218, 6, '뽀얀 국물이 우러날 때까지 푹 끓인 뒤 마늘과 생강을 건지고 체에 밭쳐 찌꺼기를 거른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1182, 219, 1, '소금물에 감자를 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1183, 219, 2, '삶은 감자는 껍질을 벗겨 큰 볼에 담고 으깬 다음 밀가루, 계란노른자, 소금, 후춧가루, 넛맥을 넣어 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1184, 219, 3, '반죽을 조금씩 떼어 손으로 비벼 짧은 국수가락 같은 모양을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1185, 219, 4, '③을 끓는 물에 넣어 잠깐 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1186, 219, 5, '버터나 마가린을 두른 프라이팬에 삶은 감자국수를 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1187, 219, 6, '⑤에 다진 양파와 베이컨을 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1188, 220, 1, '냄비에 물을 붓고 멸치, 다시마를 넣어 끓기 시작하면 다시마는 건져내고 20분정도 멸치 물을 우려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1189, 220, 2, '김치는 속을 털어낸 후 물기를 제거한 후 한 입 크기로 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1190, 220, 3, '감자, 양파, 청고추, 대파는 적당히 먹기 좋게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1191, 220, 4, '밀가루에 계란, 식용유, 소금, 물을 넣고 수제비 반죽을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1192, 220, 5, '①번 장국에 김치를 넣고 끓인 후 채소를 넣고 다시 끓어오르면 수제비를 떠서 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1193, 220, 6, '⑤번이 어느 정도 끓기 시작하면 간장으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1194, 221, 1, '오곡곡물(보리,팥,조 등)은 박박 문질러 씻은 후 물에 담가 오랫동안 불렸다가 푹 퍼지도록 뜸을 들여 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1195, 221, 2, '상추, 깻잎, 치커리 등 여러 가지 쌈을 골고루 준비해 흐르는 물에 깨끗이 씻은 후 냉장고에 넣어 싱싱하게 준비해놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1196, 221, 3, '냄비에 기름을 넣고 다진 멸치와 청고추, 홍고추, 양파, 대파, 마늘을 넣어 볶다가 된장, 고추장을 넣어 잘 섞으면서 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1197, 221, 4, '쌈장이 바글바글 끓으면 물을 자작하게 붓고 끓이다가 깨소금과 참기름을 넣어 골고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1198, 221, 5, '준비해 둔 쌈과 쌈장, 오곡밥을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1199, 222, 1, '오징어는 다리를 떼어 내고 껍질을 벗긴 다음 칼집을 주고 끓는 물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1200, 222, 2, '초고추장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1201, 222, 3, '데쳐 낸 오징어와 초고추장을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1202, 223, 1, '양파와 당근, 호박과 대파는 모두 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1203, 223, 2, '돼지고기는 채썰어 다진 마늘, 다진 생강, 간장, 후춧가루로 조물락 버무려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1204, 223, 3, '오징어는 딱딱한 면을 제거한 뒤 깨끗이 씻고,안쪽 면에 솔방울 모양이 되도록 어슷하게 칼집을 넣어가며 저며 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1205, 223, 4, '조갯살은 소금물에 흔들어 씻어 지저분한 것들을 제거하고, 홍합은 내장 옆의 털들을 제거한 뒤 끊는 물에 넣어 살짝 데쳐 사용해야 국물이 맑다. 홍합 삶은 물은 거즈에 걸러 육수로 사용한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1206, 223, 5, '목이버섯은 물에 담가 불린 뒤 먹기 좋게 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1207, 223, 6, '오목한 팬에 돼지고기를 볶다가 채썬 야채와 고춧가루를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1208, 224, 1, '배춧잎은 파릇하고 깨끗한 것을 골라 소금물에 절인다. 배추 한장을 손으로 꺾어보아 부드럽게 휘어질 정도로 절여졌으면 물에 씻어 물기를 짜 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1209, 224, 2, '부추, 양파, 당근은 잘게 다지고 고추, 양파, 표고버섯은 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1210, 224, 3, '간 고기와 채소를 섞고 갖가지 양념으로 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1211, 224, 4, '절인 배춧잎에 양념한 고기와 채소를 넣어 밖으로 새어나오지 않도록 돌돌 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1212, 224, 5, '김이 오른 찜통에 제육배추말이를 넣어 10분 정도 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1213, 224, 6, '프라이팬을 뜨겁게 달군 뒤 기름을 두르고 채썬 채소를 넣어 볶은 다음 끓는 육수를 붓는다. 한소끔 끓으면 양념을 넣어 간을 내고, 물녹말로 농도를 맞춘 후 참기름을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1214, 225, 1, '코다리는 머리, 꼬리를 떼어내고 옆으로 갈라 편 후 뼈를 발라내고 반으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1215, 225, 2, '실파는 송송썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1216, 225, 3, '양념장을 만들어 코다리를 재워 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1217, 225, 4, '냄비에 양념 코다리와 물을 약간 넣고 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1218, 225, 5, '접시에 담고 송송 썬 실파를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1219, 226, 1, '참치, 광어는 한 쪽이 20g 정도되게 어슷하게 포를 떠 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1220, 226, 2, '새우는 중하로 한 마리는 머리를 떼고 한 마리는 머리, 꼬리를 남겨두고 창자를 등쪽에서 꼬챙이로 빼낸 다음 껍질을 벗기고 배쪽으로 포를 떠서 칼등으로 판판하게 손질 한 다음 끓는 물에 소금을 약간 넣어 삶아 식초물에 잠깐 담갔다 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1221, 226, 3, '갑오징어는 껍질을 벗겨 하나가 20g 정도 되게 포를 떠 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1222, 226, 4, '계란은 계란말이를 부쳐 김발로 말아 1cm 두께로 가로 2cm, 세로 5cm 정도로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1223, 226, 5, '피조개, 전복도 깨끗이 손질하여 포를 떠서 식초물에 잠시 담갔다가 건지고 연어, 청어알을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1224, 226, 6, '무순을 다듬고, 생강은 얇게 썰어 끓는 물에 데쳐 식초물에 담갔다가 여러 겹을 겹쳐 모양을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1225, 227, 1, '두부는 깨끗한 헝겊에 싸 둘러 짜면서 물기를 완전히 빼고 으깨어 놓는다. 숙주는 삶아 다지고, 김치는 속을 털어내고 물기를 짠 다음 잘게 썬다. 돼지고기에 두부, 숙주, 김치를 넣고 갖은 양념으로 간하여 만두소를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1226, 227, 2, '만두피에 만두소를 알맞게 얹어 한식 만두 모양으로 빚어 찜통에 한 번 쪄낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1227, 227, 3, '채소와 버섯은 손질하여 같은 길이로 짧게 썰거나 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1228, 227, 4, '고춧가루에 국간장, 다진 파, 다진 마늘, 생강, 후춧가루를 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1229, 227, 5, '전골냄비에 버섯과 채소를 보기 좋게 돌려 담고 육수를 붓고 양념장을 골고루 끼얹은 다음 한소끔 끓으면 만두를 넣어 끓이면서 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1230, 228, 1, '새우는 막새우를 준비하여 잘 씻어 물기를 빼고 잘게 썬다. 오징어는 배를 갈라 내장을 손질하고 껍질을 벗겨내어 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1231, 228, 2, '양파와 당근은 손질하여 오징어와 비슷한 크기로 입자 있게 잘게 썰고, 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1232, 228, 3, '팬에 기름을 두르고 새우, 오징어, 양파, 당근, 실파를 넣고 소금, 후춧가루로 간하여 중불에서 약간 익을 정도로 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1233, 228, 4, '고슬고슬하게 지어진 밥을 준비하여 볶은 재료를 넣고 소금, 깨소금, 참기름으로 간을 맞춘 다음 밀가루를 잘 섞고 계란을 깨뜨려 넣어 끈기가 나게 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1234, 228, 5, '팬에 기름을 넉넉히 두르고 뜨겁게 달구어지면 해물밥을 한 스푼씩 떠넣어 둥글납작하게 완자 모양을 만들어 앞 뒤로 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1235, 228, 6, '간장에 식초, 다진 파, 마늘, 통깨, 송송 썬 실파를 넣어 초간장을 만들어 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1236, 229, 1, '다진 쇠고기와 으깬 두부를 섞어 소금, 후춧가루로 간을 하고 잘 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1237, 229, 2, '모양을 만들어 프라이팬에 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1238, 229, 3, '장식으로 파슬리를 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1239, 230, 1, '0.5cm 두께로 썬 두부는 소금을 약간 뿌리고 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1240, 230, 2, '슬라이스 치즈는 가로, 세로 각각 2cm 크기로 썰고 방울토마토는 0.2cm 두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1241, 230, 3, '치커리는 두부 크기에 맞게 찢어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1242, 230, 4, '식용유를 두른 프라이팬에 두부를 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1243, 230, 5, '두부에 마요네즈를 살짝 바르고 슬라이스 치즈와 방울토마토를 놓고 치커리을 얹은 후 케첩을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1244, 231, 1, '계란은 소금을 넣어 12분 간 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1245, 231, 2, '삶은 계란을 찬물에 담가 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1246, 231, 3, '물 2컵에 홍차 티백 4개를 담가 우려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1247, 231, 4, '양파는 6등분하고 마늘은 편으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1248, 231, 5, '냄비 ③을 넣고 양념을 넣은 뒤 계란, 양파, 마늘을 넣어 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1249, 232, 1, '가지는 길이 5cm 정도로 토막내어 반으로 잘라 소금물에 절여 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1250, 232, 2, '물기를 제거한 가지에 밀가루를 솔솔 뿌려둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1251, 232, 3, '냄비에 식용유를 두르고 다진 돼지고기를 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1252, 232, 4, '잘게 썬 멸치, 양파, 청고추, 홍고추, 파, 마늘을 ③과 같이 볶다가 된장, 물을 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1253, 232, 5, '④가 끓기 시작하면 ①의 가지를 넣고 뚜껑을 덮어 간이 충분히 배도록 오래 찐 후 마지막에 참기름을 넣어 향을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1254, 233, 1, '닭고기는 부드러운 안심 부위를 선택하여 얇게 포를 뜬 다음 소금, 후춧가루 뿌려 밑간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1255, 233, 2, '콘플레이크는 잘게 부셔서 빵가루와 섞어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1256, 233, 3, '닭고기에 카레를 골고루 묻힌 다음 계란옷을 입히고 빵가루와 섞은 콘플레이크를 묻혀 바삭하게 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1257, 233, 4, '고운 고춧가루 1/4작은술, 다진 마늘, 다진 양파 약간, 토마토케첩 1큰술, 간장 1/2큰술, 식초 1큰술, 올리브오일 2큰술을 잘 섞어 드레싱을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1258, 233, 5, '먹기 직전에 드레싱을 뿌려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1259, 234, 1, '식용유를 두른 프라이팬에 다진 파, 마늘, 생강을 넣어 양념맛이 기름에 배도록 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1260, 234, 2, '나머지 식용유를 부어 기름이 뜨거워지면 굵은 고춧가루를 넣고 불을 끈뒤 5~10분 동안 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1261, 234, 3, '기름에 고추의 매운맛과 붉은색이 배어들면 체에 거즈를 깔고 밑에 물기 없는 그릇을 준비한 뒤 기름을 부어 거른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1262, 235, 1, '생태는 비늘을 긁어내고 지느러미와 내장을 제거한 뒤 깨끗이 씻어 토막낸다. 머리 부분도 버리지 말고 함께 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1263, 235, 2, '바지락 은 모래가 많기 때문에 소금물에 담가 해감시킨 후 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1264, 235, 3, '무, 두부, 호박은 납작 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1265, 235, 4, '팽이버섯은 끝부분만을 자른 뒤 흐르는 물에 씻어 적당하게 갈라두고 미나리는 다듬어 소금물에 담가 지저분한 것들을 제거한 뒤 깨끗이 씻어 6~7cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1266, 235, 5, '청고추, 홍고추는 어슷썰어 씨를 털어낸다. 대파는 다듬어 큼직하게 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1267, 235, 6, '고춧가루에 양파즙, 무즙, 조미술, 간장, 다진 마늘, 다진 생강, 후춧가루를 넣어 찌개 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1268, 236, 1, '두부는 가로 2cm, 세로 3cm, 두께 0.5cm로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1269, 236, 2, '굴은 굵은 소금물에 흔들어 씻어 건지고 양송이버섯은 0.5cm 두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1270, 236, 3, '가는 파는 3cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1271, 236, 4, '청고추, 홍고추는 반으로 갈라 채썰고 마늘은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1272, 236, 5, '냄비에 물을 넣고 끓이다가 소금을 약간 넣은 뒤 두부와 굴을 넣어 계속 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1273, 236, 6, '⑤에 양송이버섯, 가는 파, 청고추, 홍고추를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1274, 237, 1, '두부는 모양이 으깨지지 않도록 씻어 도톰하고 납작하게 썰어 준비하고 잘 익은 김치는 속을 털어내고 송송 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1275, 237, 2, '느타리버섯은 적당한 굵기로 잘라놓고 생표고 팽이버섯도 먹기 좋게 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1276, 237, 3, '돼지고기는 잘게 썰고 양파는 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1277, 237, 4, '청고추, 대파는 어슷하게 썰어놓고 마늘, 생강은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1278, 237, 5, '냄비에 참기름을 두르고 돼지고기, 김치, 양파를 달달 볶다가 물을 넣어 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1279, 237, 6, '고춧가루 1큰술, 국간장 1큰술, 다진 양파, 다진 마늘, 깨소금, 어슷썬 청고추를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1280, 238, 1, '김치는 속을 털어내고 씻어서 물기를 짠 다음 일부분만 잘라 설탕 1큰술, 참기름으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1281, 238, 2, '당근, 오이, 단무지는 잘게 다지고 당근만 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1282, 238, 3, '쇠고기는 양념하여 프라이팬에 기름을 두르고 볶다가 밥과 ②의 재료를 넣고 살짝 볶아 밥을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1283, 238, 4, '김치잎을 펴서 볶아둔 밥을 놓고 흐트러지지 않게 오므려 싼 다음 미나리 데친 것으로 가운데를 묶어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1284, 239, 1, '홍합은 싱싱한 것으로 준비해 껍질에 붙어있는 지저분한 수염을 떼어내고 연한 소금물에 담가 해감시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1285, 239, 2, '대파는 어슷썰고 홍고추도 어슷썰어 물에 헹구어 씨를 뺀다. 실파는 3~4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1286, 239, 3, '냄비에 분량의 물과 홍합을 넣고 한소끔 끓으면 다진 마늘, 대파, 홍고추, 실파를 넣고 끓이면서 떠오르는 거품을 걷어내고 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1287, 240, 1, '돼지갈비는 기름기를 떼어내고 손질하여 칼집을 충분히 내 찬물에 담가 핏물을 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1288, 240, 2, '홍고추는 배를 갈라 씨를 털어내어 어슷썰고 감자와 당근, 무를 밤톨 크기로 썰어 가장자리를 둥글려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1289, 240, 3, '큰 그릇에 분량의 양념 재료를 섞어 양념장을 만들어 ①의 갈비에 재워놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1290, 240, 4, '프라이팬에 기름을 넉넉히 두르고 홍고추를 볶아 고추기름을 낸 후 재운 갈비를 앞뒤로 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1291, 240, 5, '④에 감자, 무, 당근을 넣고 뒤적이면서 돼지갈비가 잠길 정도로 물을 부어, 뚜껑을 덮고 끓이다가 갈비가 익을 무렵 고추장과 고춧가루를 넣고 윤기나게 조려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1292, 241, 1, '가래떡은 단단할 때 어슷썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1293, 241, 2, '소시지는 가로, 세로  칼집을 넣어서 준비하고 양파, 피망은 넓적하게 썰고 대파는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1294, 241, 3, '물이 끓으면 떡을 넣고 고추장과 양념 재료를 넣어 자작하게 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1295, 241, 4, '③에 ②의 준비한 야채를 넣고 숨이 죽을 정도로만 조려 상에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1296, 242, 1, '돼지고기는 삼겹살 부위로 선택하여 3∼4mm 두께로 썰어 맛술, 생강즙에 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1297, 242, 2, '약한 불에 고추장, 고춧가루, 간장, 설탕, 마늘, 깨소금, 후춧가루, 참기름을 혼합하여 양념장을 만들어 준비된 고기에 양념장을 버무려 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1298, 242, 3, '양념된 고기는 달군 돌판에 잘 뒤집어 가며 굽고 돌판이 없으면 석쇠에 호일을 깔고 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1299, 242, 4, '그릇에 된장, 고추장, 다진 파, 다진 마늘, 깨소금, 식초, 설탕, 참기름 등을 혼합하여 쌈장을 만들어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1300, 242, 5, '대파는 줄기 부분만 쪽을 떼어 곱게 채썰어 냉수에 잠깐 담근 후 물기를 빼고 고춧가루로 물을 들여 참기름과 통깨를 넣고 살살 무쳐서 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1301, 243, 1, '배추는 속이 찬 것을 골라 2등분 또는 4등분하여 소금물에 절였다가 깨끗이 씻어 큼직하게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1302, 243, 2, '밤은 껍질을 벗겨 편썰어 준비하고 갓, 미나리, 실파는 4~5㎝길이로 썰며 배는 납작하게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1303, 243, 3, '고춧가루에 물을 2큰술 넣고 섞어 불려서 액체육젓, 다진 마늘, 다진 생강, 설탕, 통깨를 섞어 고추양념을 만들어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1304, 243, 4, '고추양념에 배추, 밤, 갓, 미나리, 실파, 배를 넣고 소금으로 간을 해 고루 버무려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1305, 244, 1, '배추는 2등분하여 소금물(소금 5컵, 물 25컵)에 15시간 정도 절인 다음 헹구어 물기를 뺀 후 줄기 쪽은 3cm로 썰어서 소로 쓰고 넓은 잎은 보로 이용한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1306, 244, 2, '무는 절여서 납작하게 썰고 배도 같은 크기로 도톰하게 썬다. 갓, 미나리, 실파는 3cm 길이로 썰며 대추는 씨를 뺀 후 채썰고 석이는 곱게 채썰고 밤은 껍질을 벗겨 동글납작하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1307, 244, 3, '굴은 소금물에 씻어 건지고 낙지도 손질하여 3cm 길이로 썬다. 은행은 볶아 껍질을 벗겨 놓고 잣은 깨끗이 손질해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1308, 244, 4, '썰어놓은 배추와 무에 분량의 고춧가루를 분마기에 함께 넣고 곱게 불려서 다진 마늘, 생강을 넣고 액체육젓을 부어 고추양념을 만들어 고루 버무린 후 나머지 양념을 섞고 마지막에 해물과 배를 넣어 버무려서 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1309, 244, 5, '공기에 배춧잎 3~4장을 고르게 펴놓고 버무린 소를 소복하게 담고 밤, 대추, 석이, 잣, 은행 등의 고명을 약간씩 남겨서 김치 위에 보기좋게 얹은 다음 펼쳐진 배춧잎을 한장씩 차례로 덮어 꼭꼭 아물린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1310, 244, 6, '보쌈을 항아리에 차곡차곡 담고 물에 맑은 젓국을 타서 보쌈이 잠기도록 자작하게 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1311, 245, 1, '사골은 찬물에 담가 핏물을 뺀 후 냄비에 물을 붓고 끓이다가 첫 물은 따라버리고 다시 물을 부어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1312, 245, 2, '사태는 덩어리째 핏물을 뺀 후 ①에 같이 넣고 푹 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1313, 245, 3, '우거지는 끓는 물에 소금을 넣고 푹 삶은 후 찬물에 담가 냄새를 우려낸 뒤 물기를 꼭 짜고 5cm 폭으로 썰고 고기는 건져서 얄팍하게 저민다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1314, 245, 4, '③에 된장, 마늘을 넣어 골고루 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1315, 245, 5, '뽀얗게 우러난 사골 국물을 4컵 정도 냄비에 넣고 ④를 넣어 한소끔 끓으면 어슷하게 파, 고추를 넣고 소금으로 간을 맞춘 다음 고춧가루를 조금 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1316, 246, 1, '양파, 당근은 곱게 다지고 청, 홍고추는 씨를 제거한 후 곱게 다지고 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1317, 246, 2, '참치는 체에 밭쳐 기름을 제거한 후 곱게 으깨어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1318, 246, 3, '유리볼에 찬밥과 다진 양파, 당근, 청, 홍고추, 실파와 분량의 계란물, 밀가루, 소금, 후춧가루를 넣어 재료들이 잘 섞이도록 반죽을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1319, 246, 4, '후라이팬에 기름을 두르고 뜨거워지면 ③을 한 수저씩 떠 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1320, 246, 5, '④를 둥글게 모양을 만들어 앞 뒤로 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1321, 247, 1, '팥은 쭉정이를 골라 내고 깨끗하게 씻어서 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1322, 247, 2, '냄비에 팥의 4배 정도의 물을 붓고 한소끔 끓이다가 팥물을 따라내 버린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1323, 247, 3, '다시 냄비에 물을 팥의 4배 정도 넣고 끓이면서 소금을 약간 넣고 설탕량의 2/3 정도를 넣 고 팥알이 충분히 삶아질 수 있도록 약한 불에 은근히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1324, 247, 4, '인절미를 네모나게 썰어 약간 굳힌 다음 석쇠에 놓고 살짝 부풀정도로 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1325, 247, 5, '나머지 설탕을 넣어서 팥알이 충분히 설탕맛이 배고 국물이 농축되면 그릇에 담고 구워 놓은 떡과 대추 고명을 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1326, 248, 1, '쇠고기는 불고기용으로 얇게 썰어 분량의 쇠고기양념 재료를 넣고 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1327, 248, 2, '무는 4cm 길이로 납작하게 썰고 표고버섯은 어슷썬다. 팽이버섯은 밑둥을 자르고 미나리는 깨끗이 씻어 준비한다. 대파와 고추는 어슷썰고, 양파는 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1328, 248, 3, '낙지는 먹통과 내장을 제거한 뒤 굵은 소금을 뿌려 거품이 날 정도로 주물러 깨끗이 씻어 물기를 빼고 5cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1329, 248, 4, '전골냄비에 재료들을 보기 좋게 담고 물 3컵을 부어 끓이고 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1330, 249, 1, '중간 크기의 잔멸치를 준비해 채에 담아 흔들어 부스러기와 티를 없앤다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1331, 249, 2, '분량의 된장에 맛술을 섞어 고운 체에 밭쳐 거른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1332, 249, 3, '프라이팬에 기름을 두르고 뜨거워지면 ①의 멸치를 넣고 약한 불에서 나무젓가락이나 주걱으로 가볍게 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1333, 249, 4, '③에 분량의 간장, 설탕, 다진 마늘과 준비한 된장을 넣고 고루 양념이 섞이도록 저어가며 바삭하게 볶은 다음 깨소금을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1334, 249, 5, '상추를 깨끗이 씻어 그 위에 밥을 놓고 볶은 멸치를 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1335, 250, 1, '다진 쇠고기는 간장 1큰술, 설탕 1/2작은술, 마늘 1작은술, 후춧가루, 참기름에 양념하여 프라이팬에 기름을 두르고 볶아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1336, 250, 2, '콩나물은 꼬리를 떼고 깨끗이 다듬어 끓는 소금물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1337, 250, 3, '당근은 껍질을 벗겨 곱게 채썰고 미나리도 같은 길이로 잘라 뜨거운 프라이팬에 기름을 두르고 소금 간을 하며 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1338, 250, 4, '유부는 끓는 물에 데쳐 기름을 빼고 채썰어 소금, 참기름에 무쳐 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1339, 250, 5, '고슬하게 지은 밥을 그릇에 적당히 담아, 볶아 놓은 모든 재료를 색 맞추어 올리고 분량의 양념 재료를 넣어 양념장을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1340, 251, 1, '바게트를 1.5cm 두께로 어슷하게 썰어 프라이팬에 노릇하게 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1341, 251, 2, '달군 프라이팬에 베이컨을 채썰어 먼저 볶고 김치, 당근, 양송이, 피망도 채썰어 함께 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1342, 251, 3, '바게트 위에 ②를 얹고 모짜렐라치즈를 얹어 180℃의 오븐에 넣고 7~10분간 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1343, 251, 4, '접시에 담기 전 파슬리가루를 솔솔 뿌려 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1344, 252, 1, '오이, 양배추는 가늘게 채썰고 깻잎과 상추는 흐르는 물에 씻은 후 손으로 먹기 좋은 크기고 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1345, 252, 2, '청고추는 어슷하게 썰어 찬물에 담가 씨를 뺀 후 김치는 송송 썰고 깨소금, 참기름을 넣고 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1346, 252, 3, '고추장 2큰술, 식초 2큰술, 설탕 1큰술, 깨소금, 참기름을 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1347, 252, 4, '끓는 물에 소금을 약간 넣고 국수를 넣어 쫄깃하게 삶은 후 찬물에 열기를 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1348, 252, 5, '그릇에 채소들을 보기좋게 담은 후 국수를 사리로 틀어 같이 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1349, 252, 6, '초고추장 소스를 뿌려 비벼먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1350, 253, 1, '오징어는 소금을 발라가며 껍질을 손쉽게 벗긴 후 깨끗이 씻어 끓는 물에 살짝 데쳐 한입 크기로 길쭉하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1351, 253, 2, '당근과 양파는 깨끗이 씻어 네모지게 잘게 썰고, 표고버섯은 밑동을 칼끝으로 떼어낸 후 작게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1352, 253, 3, '옥수수는 삶아서 칼끝으로 떼어내거나 통조림을 사용한다. 완두콩은 데쳐서 사용하거나 통조림으로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1353, 253, 4, '오목한 그릇에 물과 계란을 골고루 푼 다음 녹말가루를 넣어 튀김옷을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1354, 253, 5, '데쳐놓은 오징어는 물기를 없앤 뒤 밀가루를 살짝 묻혀 털어내고, 튀김옷을 입혀 180℃ 정도의 달궈진 기름에서 튀긴다. 식으면 다시 한 번 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1355, 253, 6, '프라이팬에 기름을 두르고 잘게 썬 당근, 양파, 표고버섯, 옥수수, 완두콩을 넣어 볶다가 육수를 넣는다. 육수가 보글보글 끓으면 간장과 설탕, 소금, 식초, 조미술을 넣어 맛을 낸 뒤, 물녹말을 조금씩 넣어 농도를 맞추고 참기름을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1356, 254, 1, '콩나물은 꼬리를 제거하고 깨끗이 씻은 후 소금을 넣은 끓는 물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1357, 254, 2, '쇠고기는 잘게 썰어 간장, 다진 마늘, 다진 파, 깨소금, 후춧가루, 참기름으로 약하게 양념해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1358, 254, 3, '양파와 당근은 납작하게 썰고 느타리버섯은 손으로 ？어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1359, 254, 4, '멸치를 넣어 장국을 만든다.(멸치의 비린맛을 없애려면 기름기 없는 후라이팬에서 살짝 볶아 국물을 우려낸다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1360, 254, 5, '냄비에 ④의 멸칫국물을 붓고 고기와 당근, 양파를 넣고 끓인 다음 콩나물과 느타리버섯을 넣고 끓이면서 피쉬소스나 간장으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1361, 254, 6, '⑤가 어느정도 끓기 시작하면 소금, 후춧가루로 간을 맞춘 후 물녹말로 농도를 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1362, 255, 1, '다시물을 끓여 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1363, 255, 2, '다시마는 표면에 묻은 분가루를 손질하여 ①의 국물에 넣고 같이 끓인 다음 체에 밭쳐 걸러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1364, 255, 3, '무, 당근은 큼직하게 썰어 살짝 삶고 계란도 반완숙하여 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1365, 255, 4, '어묵은 먹기좋은 크기로 썰어 끓는물에 살짝 한 번 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1366, 255, 5, '꼬지에 ④의 어묵을 끼운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1367, 255, 6, '겨자는 따끈한 물로 잘 개어 끓는 냄비 뚜껑 위에 엎어 놓고 10분 정도 발효시켜서 다시 한 번 젓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1368, 256, 1, '닭다리는 깨끗이 씻어 뼈를 중심으로 양옆에 칼집을 넣으면서 뼈를 발라낸 후 껍질을 벗겨 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1369, 256, 2, '잘게 썬 닭고기에 간장, 조미술, 녹말가루를 넣어 간이 배도록 조물조물 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1370, 256, 3, '마늘, 생강은 납작하게 저며 썬 후 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1371, 256, 4, '마른 고추는 어슷하게 썰어 씨를 털어내고 실파는 깨끗이 씻어 4cm 길이로 썬다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1372, 256, 5, '프라이팬에 기름을 두르고 뜨겁게 달군 후 양념한 닭고기를 넣어 달라붙지 않게 젓가락으로 저어가며 튀기듯이 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1373, 256, 6, '간장에 식초, 조미술, 설탕, 굴소스, 토마토케첩을 넣어 골고루 저어 굴소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1374, 257, 1, '쌀은 미리 씻어 충분히 불려 물기를 빼 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1375, 257, 2, '밤과 고구마는 먹기 좋게 썰고 은행은 기름에 살짝 볶아 껍질을 벗긴다. 대추는 씨를 기점으로 돌려깎기 하여 잘게 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1376, 257, 3, '냄비에 참기름을 살짝 두르고 위의 재료를 볶다가 쌀을 넣어 투명해질 때까지 같이 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1377, 257, 4, '③에 다시마 국물을 자작하게 넣어 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1378, 257, 5, '간장에 다시마 국물을 넣고 다진 파, 다진 마늘, 깨소금, 참기름을 고루 섞어 양념장을 만들어 채소밥에 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1379, 258, 1, '도라지는 잘게 찢어 소금물에 삶아 내고 씻어 물기를 짜 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1380, 258, 2, '고사리는 억센 부분을 잘라내고 다듬어 6~7cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1381, 258, 3, '시금치는 끓는 소금물에 데쳐 찬물에 헹구어 물기를 짜놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1382, 258, 4, '콩나물은 깨끗이 씻어 끓는물에 데쳐 물기를 짜놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1383, 258, 5, '도라지, 시금치, 콩나물은 소금, 깨소금, 참기름, 다진 파, 마늘, 들깻가루로 양념하여 조물조물 묻힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1384, 258, 6, '후라이팬에 기름을 살짝 두르고 고사리를 볶다가 간장, 물, 깨소금, 다진 파, 다진 마늘 등을 넣어 촉촉하게 양념하면서 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1385, 259, 1, '흰떡은 6cm 길이, 0.5cm 폭으로 채썰어 참기름과 간장을 섞어 만든 유장으로 골고루 버무려 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1386, 259, 2, '쇠고기는 불고기용으로 준비하여 채썰어 고기 양념을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1387, 259, 3, '양파, 피망, 당근도 손질하여 흰 떡과 같은 길이로 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1388, 259, 4, '팬에 기름을 넉넉히 두르고 뜨겁게 달군 다음 양념한 고기를 넣어 볶다가 채소와 김치를 넣고 함께 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1389, 259, 5, '고기와 채소가 거의 익으면 흰 떡을 넣어 고루 섞으면서 볶다가 참기름을 두르고 통깨를 훌훌 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1390, 260, 1, '두부는 1cm 정도의 두께로 썰어 깨끗한 행주로 물기를 제거한 후 소금으로 약하게 간하여 프라이팬에 노릇하게 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1391, 260, 2, '지져낸 두부는 열기를 식혀 냉장고에 차게 보관한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1392, 260, 3, '쇠고기는 편육용으로 삶아 식힌다. (삶을 때는 쇠고기의 냄새를 없애기 위해 마늘, 통후추 등을 소량 넣어 준다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1393, 260, 4, '양배추, 오이, 당근, 쇠고기, 두부는 길이 5cm, 넓이 2cm 정도의 막대모양으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1394, 260, 5, '땅콩가루 1큰술, 식초 1큰술, 간장 1/2큰술, 갠겨자 1/2큰술, 황설탕 1/2큰술, 소금, 참기름약간을 믹서기에 넣고 곱게 갈아 땅콩겨자소스를 만들어 차게 보관해 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1395, 260, 6, '접시에 위의 재료를 보기좋게 담은 후 땅콩겨자소스를 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1396, 261, 1, '밀가루에 소금, 참기름을 넣어 고루 비벼 체에 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1397, 261, 2, '①의 밀가루에 분량의 조청, 생강즙, 청주를 넣어서 반죽을 질지 않게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1398, 261, 3, '반죽을 밀대로 0.5cm 정도로 밀고 원형 틀로 찍어 모양을 잡는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1399, 261, 4, '분량의 물과 설탕을 냄비에 넣고 반으로 줄 때까지  졸여 집청시럽을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1400, 261, 5, '130~140℃의 기름에서 천천히 색이 나도록 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1401, 261, 6, '약과가 튀겨지면 집청시럽에 담갔다가 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1402, 262, 1, '참치는 기름을 뺀 후 실파, 깨소금, 후춧가루를 넣고 양념하고 밥은 맛소금을 넣어 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1403, 262, 2, '계란은 완숙하여 노른자만 체에 내려놓고 파슬리는 곱게 다져 물기를 꼭 짜고 당근은 살짝 삶아 곱게 다진 후 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1404, 262, 3, '랩을 사방 10cm 정도로 잘라 한 입 크기로 밥을 놓고 속에 참치를 넣어 부드럽게 뭉친 다음 계란노른자, 파슬리가루, 당근가루, 흰 깨를 나누어 묻히고 적당히 김띠를 두른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1405, 262, 4, '깨끗한 거즈에 밥을 펴서 중심에 게맛살과 참치를 놓고 오이를 채썰어 얹어 김밥모양으로 돌돌 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1406, 262, 5, '④를 칼에 물을 묻혀가며 1.5cm 폭으로 썰어 남은 파슬리, 당근, 깨, 노른자가루를 보기 좋게 묻혀 ③과 같이 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1407, 263, 1, '미역은 물에 담가 충분히 불리고, 손으로 비벼 깨끗이 씻어 물기를 뺀 후 먹기 좋은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1408, 263, 2, '유부는 납작하게, 두부는 1cm x 1cm로 깍둑썰고, 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1409, 263, 3, '냄비에 멸치, 다시마, 다시물을 붓고 된장을 풀고 미림을 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1410, 263, 4, '국물이 한소끔 끓으면 준비해둔 미역, 유부, 두부를 넣어 끓이다가 다 끓으면 송송 썬 실파를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1411, 264, 1, '밀가루에 물과 소금을 넣고 시금치즙, 당근즙, 각각 넣어 잘 치대어 삼색으로 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1412, 264, 2, '호박은 납작하게 반달썰기하고, 감자는 껍질을 벗겨 먹기 좋은 크기로 썬다. 대파는 어슷하게 썰고, 양파는 굵직하게 반달썰기 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1413, 264, 3, '멸치와 다시마를 넣고 끓이다가 국물이 어느정도 우러나면 맑게 걸러낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1414, 264, 4, '맛국물에 감자, 호박, 양파를 넣고 끓이다가 반죽을 손으로 얇게 떠서 넣고 팔팔 끓인다. 한소끔 끓으면 청정원 맛선생과 국간장, 다진 마늘, 대파를 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1415, 265, 1, '고들빼기는 다듬어서 소금물에 절인 다음 무거운 것으로 눌러 일주일 정도 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1416, 265, 2, '일주일 정도 삭힌 고들빼기는 물에 쓴 맛을 우려내어 체에 건져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1417, 265, 3, '밴댕이 젓등의 젓갈류를 준비하여 다져놓고 삭힌 고추는 걸러놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1418, 265, 4, '갓, 실파등은 길이 10cm 길이로 썰어 젓국에 절여 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1419, 265, 5, '젓국에 고춧가루와 설탕을 함께 개어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1420, 265, 6, '고들빼기, 고추, 갓 등의 양채를 양념에 넣어 고루 버무린 다음 항아리에 차곡차곡 담은 후 우거지를 덮어 소금을 뿌리고 무거운 것으로 누른 다음 익으면 먹도록 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1421, 266, 1, '달래는 깨끗이 손질하여 3cm 길이로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1422, 266, 2, '굴은 껍질을 제거하고 소금물에 살살 비벼 씻은 후 깨끗한 물에 헹구어 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1423, 266, 3, '청, 홍고추는 얇고 둥글게 썰어 물에 씻어 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1424, 266, 4, '큰 볼에 밀가루 1컵, 계란 1개, 물 2/3컵을 넣어 밀가루가 응어리지지 않게 잘 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1425, 266, 5, '4번 반죽에 손질한 달래, 굴, 고추, 실파 등을 넣어 잘 섞은 후 소금으로 간을 심심하게 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1426, 266, 6, '팬에 기름을 넉넉히 두르고 ⑤의 재료를 한숟가락씩 펴서 평평하게 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1427, 267, 1, '달래는 뿌리부분을 자르지 말고 깨끗이 다듬어 흐르는 물에서 가볍게 씻은 후 소쿠리에 건져 물기를 없앤 다음 5cm 길이로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1428, 267, 2, '마늘, 생강은 곱게 다져 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1429, 267, 3, '고춧가루는 미지근한 물에 설탕을 조금 넣고 개어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1430, 267, 4, '개어놓은 고춧가루와 다진 마늘, 생강을 분량의 진간장에 조금 넣고 ①의 달래를 가볍게 버무려 통깨를 뿌린 후 그릇에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1431, 268, 1, '두릅은 연한 것으로 겉껍질을 벗겨 내고 깨끗이 손질하여 끓는 소금물에 데쳐 낸 다음 찬물에 헹구어 꼭 짜 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1432, 268, 2, '파와 마늘은 곱게 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1433, 268, 3, '된장에 다진 파, 마늘, 참기름, 깨소금 등을 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1434, 268, 4, '큰 볼에 데쳐낸 두릅과 양념장을 조물조물 무쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1435, 269, 1, '숙주나물은 숙주의 머리, 뿌리를 제거한 후 끓는 물에 소금을 약간 넣고 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1436, 269, 2, '미나리는 불순물을 깨끗이 제거하기 위해 소금과 식초를 넣은 물에 잠시 담갔다가 살짝 데친 후 물기를 꽉 짜준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1437, 269, 3, '파와 마늘은 곱게 다지고 홍고추는 링모양으로 썬 후 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1438, 269, 4, '위의 숙주와 미나리, 다진 파, 마늘을 섞어 소금, 깨소금, 참기름 등을 넣어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1439, 270, 1, '참나물은 깨끗이 다듬어 끓는 물에 삶아 찬물에 헹구어 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1440, 270, 2, '고추장에 다진 파, 다진 마늘, 깨소금, 참기름을 넣어 양념 고추장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1441, 270, 3, '삶아 놓은 참나물에 ②의 양념장을 넣어 골고루 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1442, 271, 1, '오징어는 깨끗이 씻은 후 껍질을 벗기고 안쪽에 다이아몬드 모양으로 칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1443, 271, 2, '칼집 낸 오징어에 꼬치를 꽂아 칼집모양이 밖으로 보일 수 있도록 돌돌 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1444, 271, 3, '마늘, 대파, 생강은 곱게 다져서 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1445, 271, 4, '고추장 1큰술, 고춧가루 1/2큰술, 간장 1작은술, 물엿 1큰술, 양파즙, 생강즙, 다진 파, 다진 마늘, 깨소금, 후춧가루, 참기름을 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1446, 271, 5, '꼬치에 꽂은 오징어를 살짝 익히고 나서 ④번 양념장을 골고루 발라가며 타지 않게 굽는다. 구울 때 수분이 적으면 청주를 조금씩 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1447, 272, 1, '닭고기는 깨끗이 손질하여 적당한 크기로 토막내어 마늘, 생강 저민 것을 넣고 푹 삶은 다음 고기는 건져 살코기만 잘게 찢고, 뼈는 다시 넣어 끓여 육수를 낸 후 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1448, 272, 2, '콩나물은 꼬리를 떼고 소금물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1449, 272, 3, '오이는 채썰어 소금에 절였다가 물기를 꼭 짜고, 김치는 속을 털어내고 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1450, 272, 4, '고춧가루에 간장, 다진 파, 다진 마늘, 깨소금, 참기름을 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1451, 272, 5, '닭 살, 콩나물, 오이, 김치를 양념장으로 골고루 버무려 밥 위에 소담히 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1452, 273, 1, '김치는 얇게 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1453, 273, 2, '두부는 끓는물에 데쳐 삼각모양으로 0.8cm 두께로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1454, 273, 3, '돼지고기는 가제에 싸서 끓는물에 대파, 생강, 소금을 넣고 삶은 후 알맞게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1455, 273, 4, '접시에 돼지고기를 깔고, 두부를 돌려 담은 후 김치를 얹어 보기좋게 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1456, 273, 5, '쌈채소와 쌈장을 곁들이면 더욱 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1457, 274, 1, '호박은 5mm 두께로 반달모양으로 썰고 두부는 8mm 두께로 네모지게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1458, 274, 2, '청고추, 파는 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1459, 274, 3, '냄비에 기름을 두르고 호박을 넣어 볶다가 새우젓을 넣고 잠시 더 볶아 물을 잘박하게 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1460, 274, 4, '③이 한소끔 끓으면 두부, 청고추, 파, 다진 마늘을 넣고 끓인 뒤 소금으로 간을 맞추고 참기름을 친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1461, 275, 1, '해파리는 먼저 옅은 소금물에 10분간 담그고 끓는물에 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1462, 275, 2, '물에 데친 해파리는 식초, 설탕물에 5분 정도 담근 후 물기를 제거해 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1463, 275, 3, '쭈꾸미는 끓는물에 살짝 데쳐 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1464, 275, 4, '새우도 내장과 껍질을 제거한 후 살짝 데쳐 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1465, 275, 5, '겨자 2큰술, 머스터드 1/2큰술, 마늘, 식초, 간장, 소금, 참기름을 섞어 해파리 겨자소스를 만든다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1466, 275, 7, '오이, 당근, 피망, 계란 지단은 5cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1467, 276, 1, '닭고기는 뼈를 발라 내고 힘줄 부분을 칼끝으로 끊고 방망이로 두드려 연하게 한 다음 소금, 후춧가루를 뿌려 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1468, 276, 2, '당근은 사방 1cm 넓이로 사각지게 썰어 살짝 데체고 완두콩은 소금물에 삶아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1469, 276, 3, '닭고기 살쪽에 밀가루를 약간 뿌리고 중심에 당근, 완두콩, 옥수수 알을 돌돌 말아서 무명실로 촘촘하게 묶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1470, 276, 4, '둥근 팬에 식용유를 두르고 뜨거워지면 닭고기를 넣어 굴려가며 갈색나게 굽는다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1471, 276, 5, '버터를 충분히 넣어 속까지 익으면 도톰하게 썰어 접시에 담고 분량의 양념재료로 만든 토마토케첩소스를 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1472, 277, 1, '아스파라거스는 4cm 길이로 썰어서 끓는 물에 소금을 넣고 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1473, 277, 2, '새우는 꼬지로 등의 내장을 빼고 소금물에 삶아 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1474, 277, 3, '샐러리는 섬유질을 벗겨내고 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1475, 277, 4, '프라이팬에 기름을 두르고 뜨거워지면 미림, 샐러리와 아스파라거스를 볶다가 손질한 새우를 넣고 살짝 볶아 소금, 후춧가루로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1476, 278, 1, '밀가루에 물과 소금을 넣고 반죽하여 치댄 다음 비닐에 싸서 약 20분 정도 두었다가 둥글게 얇게 만두피를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1477, 278, 2, '배추는 깨끗이 씻어 아주 곱게 다져 소금을 뿌리고 잠시 절였다가 물기를 꼭 짜내고 부추도 깨끗이 씻어 물기를 빼고 잘게 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1478, 278, 3, '오목한 그릇에 갈아놓은 쇠고기를 넣고 간장, 식용유, 물을 넣어 잘 섞은 다음 배추, 부추를 넣고 다진 파, 다진 마늘, 소금, 후춧가루로 양념하여 잘 버무려 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1479, 278, 4, '얇게 민 만두피에 만두소를 조금 얹어 안쪽으로 오무려 붙여 예쁜 꽃만두가 되게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1480, 278, 5, '꽃만두는 끓는 물에 넣어 3분 정도 끓여 익으면 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1481, 278, 6, '냄비에 육수를 부어 팔팔 끓으면 익혀낸 꽃만두를 넣고 다진 파와 다진 마늘, 소금, 후춧가루로 간을 맞춘 다음 한소끔 끓으면 팽이버섯과 쑥갓을 넣고 살짝 익혀 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1482, 279, 1, '돼지고기는 등심으로 얇게 저며 썰어 간장, 조미술, 후춧가루로 양념한 다음 녹말가루로 살살 버무려 잠시 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1483, 279, 2, '마늘, 생강은 납작납작하게 편으로 썰고, 대파와 청고추는 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1484, 279, 3, '표고버섯은 미지근한 물에 불려 씻어 뒷부분의 기둥을 잘라 내고 저며 썬다. 양파는 약간 굵직하게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1485, 279, 4, '속이 깊은 팬에 기름을 두르고 청고추, 대파, 마늘, 생강을 넣어 살살 볶다가 돼지고기를 넣어 함께 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1486, 279, 5, '육수나 표고버섯 불린 물을 부어 끓이면서 소금과 굴소스, 후춧가루로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1487, 279, 6, '자작하게 끓으면 물녹말을 넣어 걸쭉하게 농도를 낸 후 참기름을 살짝 두른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1488, 280, 1, '풋마늘은 껍질을 벗겨 깨끗이 씻은 후 길이 10cm 정도로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1489, 280, 2, '갑오징어는 뼈와 먹물을 빼고 껍질을 벗긴 후 깨끗이 씻는다. 갑오징어에 칼집을 약간 넣고 넓이 2cm, 길이는 풋마늘보다 약간 길게 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1490, 280, 3, '오징어와 풋마늘을 섞어 꼬지에 꿰어 밀가루를 묻힌다. 밀가루와 물을 1:1의 비율로 넣어 반죽을 해놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1491, 280, 4, '3번의 꼬지에 반죽을 발라가며 구운 다음 양념장을 끼얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1492, 281, 1, '상추는 한입 크기로 찢고 오이, 대파는 납작하게 어슷썰어 간장, 식초, 고춧가루, 설탕, 참기름을 넣어 살짝 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1493, 281, 2, '도라지는 잘게 찢어 소금에 주물러 쓴 맛을 뺀 후 파, 마늘, 깨소금, 참기름으로 양념하여 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1494, 281, 3, '고사리는 연한 줄기만 다듬어 간장, 파, 마늘, 깨소금으로 양념하여 볶고 당근은 채썰어 기름에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1495, 281, 4, '쇠고기 다진 것은 분량의 재료로 양념한 후 고추장과 같이 기름에 살짝 볶아 약고추장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1496, 281, 5, '비빔밥 그릇에 밥을 담고 ①, ②, ③을 보기좋게 얹어 약고추장을 곁들여 비벼 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1497, 282, 1, '두부는 멍울이 지지 않도록 체에 내려 소금 약간과 조미술을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1498, 282, 2, '유리볼에 계란과 다시물을 섞고 소금, 조미술, 간장으로 간을 한 후 고운 체에 걸러 알끈 등을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1499, 282, 3, '생표고버섯은 기둥을 채썰고 모시조개는 소금물에 담가 해감 시킨 후 살짝 데치고 새우는 내장을 제거한 후 삶아 껍질을 벗긴다. 은행도 끓는물에 데쳐 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1500, 282, 4, '②에 체에 내린 두부를 넣어 서로 잘 섞이도록 저어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1501, 282, 5, '찜그릇에 속재료를 넣고 계란물을 부어 은박지로 뚜껑을 만들어 덮고 김 오른 찜통에 13분 정도 쪄 부드럽게 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1502, 283, 1, '양상추는 한 잎씩 떼어 흐르는 찬물에 씻은 후 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1503, 283, 2, '물기를 뺀 양상추를 한입 크기로 잘라 놓는다. 이때 손으로 자연스럽게 찢어주는 것이 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1504, 283, 3, '오이피클은 동그란 단면을 살려 약 3mm 두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1505, 283, 4, '훈제연어는 양상추보다 약간 작은 정도로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1506, 283, 5, '체리는 반으로 자르고 무순도 흐르는 물에 헹구어 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1507, 283, 6, '한입 크기로 잘라놓은 양상추 위에 땅콩 버터를 약간 바르고 그 위에 훈제 연어를 올린 후 피클, 무순, 체리의 순서로 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1508, 284, 1, '브로콜리는 반으로 나누어 한 쪽은 소금과 기름을 넣어 삶아내고 나머지 반쪽은 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1509, 284, 2, '삶은 브로콜리는 잘게 썰고 살짝 데친 브로콜리는 작은 송이로 떼어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1510, 284, 3, '프라이팬에 버터를 두르고 삶아 놓은 브로콜리를 넣어 볶다가 밀가루를 넣어 다시 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1511, 284, 4, '③에 생크림과 우유를 넣어 걸쭉하게 만들어준 다음 믹서에 갈아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1512, 284, 5, '④를 다시 냄비에 담고 뭉근히 끓여 소금, 후춧가루로 간을 하고 데친 브로콜리를 넣어 살짝 끓인 후 접시에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1513, 285, 1, '오징어는 껍질을 벗겨 잘 씻은 후 끓는 물에 살짝 데쳐 사방 3cm 정도 크기의 정사각형으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1514, 285, 2, '새우도 상온에서 해동해 물에 잘 흔들어 씻은 후 끓는물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1515, 285, 3, '가래떡은 동그란 단면을 살려 1.5cm 두께로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1516, 285, 4, '피망도 오징어와 비슷한 크기로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1517, 285, 5, '꼬치에 오징어, 새우, 가래떡, 피망을 끼워 넣고 양념장을 발라가며 그릴에 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1518, 286, 1, '피망, 햄, 양파는 모두 옥수수알 크기 정도로 깍둑썰기 하고 옥수수는 체에 밭쳐 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1519, 286, 2, '기름을 두른 팬에 옥수수와 햄, 당근을 넣고 소금, 후춧가루로 간을 하면서 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1520, 286, 3, '피망과 양파를 넣어 한번 더 볶아낸 후 밥을 넣어 살살 펴주면서 볶는다. 이 때 기름이 떨어지지 않도록 보충해 가며 볶아주는 것이 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1521, 286, 4, '소금, 후춧가루로 마무리 간을 한 후 접시에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1522, 287, 1, '꽁치 통조림은 체에 밭쳐 물기를 거둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1523, 287, 2, '김치는 속을 잘 털어서 적당한 크기로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1524, 287, 3, '냄비에 김치와 꽁치를 켜켜이 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1525, 287, 4, '고춧가루, 설탕, 간장, 마늘, 대파, 물 등을 섞어 양념장을 만든 뒤 김치와 꽁치에 뿌려 은근한 불에 조려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1526, 287, 5, '참기름을 둘러 마무리한 뒤 상에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1527, 288, 1, '돈까스 고기는 소금과 후춧가루, 생강즙으로 밑간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1528, 288, 2, '피자치즈는 넓적하고 얇게 썰어 준비하고 피망은 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1529, 288, 3, '돈까스 고기 위에 피자치즈를 얹고 피망 다진 것을 얹은 다음 다시 피자치즈를 얹고 고기를 얹어 꼭꼭 누른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1530, 288, 4, '고기에 밀가루를 잘 묻힌 뒤 계란물에 담갔다가 빵가루를 꼼꼼하게 입힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1531, 288, 5, '170℃로 달궈진 기름에 넣고 잘 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1532, 289, 1, '분량의 양념 재료를 섞어 쇠고기 양념장과 떡볶이 양념장을 각각 만들어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1533, 289, 2, '떡은 뜨거운 물에 10분 정도 불린 후 쇠고기 양념장을 약간 덜어 밑간을 해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1534, 289, 3, '양파, 당근, 피망, 표고버섯은 채썰고 쇠고기와 표고버섯은 쇠고기 양념장에 버무려 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1535, 289, 4, '프라이팬에 기름을 두르고 채썬 쇠고기와 표고버섯, 떡을 넣고 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1536, 289, 5, '쇠고기가 익으면 야채를 넣고 ①의 떡볶이를 넣고 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1537, 289, 6, '참기름을 넣고 마무리해서 접시에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1538, 290, 1, '춘권피는 바구니 모양을 만들어 기름에 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1539, 290, 2, '무순은 깨끗이 씻어 물기를 거두고 깻잎은 잘 씻어 얇게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1540, 290, 3, '토마토는 껍질을 벗겨 잘게 썰고 양파도 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1541, 290, 4, '쇠고기는 불고기양념을 해서 재워놓고 분량의 양념재료를 섞어 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1542, 290, 5, '튀긴 춘권피 바구니에 쇠고기와 여러가지 토핑을 얹어 양념을 뿌려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1543, 291, 1, '밥은 고슬고슬하게 지어 소금, 참기름, 다진 파로 아주 약하게 간을 해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1544, 291, 2, '신김치는 잎으로 준비해 물에 한번 헹군 다음 설탕, 소금, 조미료, 참기름 등으로 조물조물 양념해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1545, 291, 3, '밥을 한 입 크기로 쥔 다음 꼭꼭 뭉친 뒤 양념한 김치잎으로 잘 싸준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1546, 291, 4, '밥 대신 만두 속이나 다진 고기등을 넣어서 쪄 먹어도 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1547, 292, 1, '찬물에 멸치를 넣고 끓여 멸칫국물을 우려내고 국간장이나 소금으로 간을 해 멸치장국을 만들어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1548, 292, 2, '쇠고기는 불고기양념을 해서 재워 두었다가 팬에 국물없이 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1549, 292, 3, '호박, 당근도 채썰어 소금 간을 해서 볶아주고 계란은 지단을 부쳐 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1550, 292, 4, '분량의 양념재료를 섞어 매콤한 양념장을 만들어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1551, 292, 5, '소면은 물에 삶아 건져서 찬물에 헹군 다음 1인분씩 사리지어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1552, 292, 6, '국수대접에 사리지은 소면을 담고 멸치장국을 부어 준비한 고명을 골고루 얹고 양념장을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1553, 293, 1, '감자는 껍질을 벗겨 조각으로 잘라 소금을 넣고 삶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1554, 293, 2, '양파와 베이컨은 두껍게 채 썰어 팬에 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1555, 293, 3, '인스턴트 크림수프는 우유를 넣고 걸죽하게 끓여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1556, 293, 4, '삶은 감자와 볶은 양파, 베이컨을 섞어 내열용기에 담고 크림스프를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1557, 293, 5, '여기에 모짜렐라 치즈를 얹어 180℃의 오븐에 구워 치즈가 녹아 노릇해지면 꺼낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1558, 294, 1, '코다리는 잘 씻어 한 입 크기로 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1559, 294, 2, '양파는 사방 3cm로 썰고 청고추와 홍고추는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1560, 294, 3, '파도 어슷썰고 마늘은 편으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1561, 294, 4, '분량의 양념재료를 모두 섞어 물을 약간 넣고 조림장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1562, 294, 5, '팬에 코다리만 넣고 조림장을 넣어 약간 조려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1563, 294, 6, '어느정도 간이 들었으면 양파와 청고추, 홍고추를 넣고 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1564, 295, 1, '데리야끼소스에 닭가슴살을 5시간쯤 재운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1565, 295, 2, '프라이팬을 달궈 닭과 파인애플을 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1566, 295, 3, '브로콜리, 컬리플라워, 당근은 한 입 크기로 썰어 끓는 물에 데친 후 찬물에 헹궈놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1567, 295, 4, '청피망, 홍피망을 1cm 정도로 작게 썰고 숙주는 다듬는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1568, 295, 5, '달군 프라이팬에 ④을 넣고 볶다가 계란 푼 것을 넣고 계속 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1569, 295, 6, '밥을 넣고 밥이 뭉치지 않도록 볶다가 간장으로 간을 한 후 참기름으로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1570, 296, 1, '프라이팬에 기름을 두르고 다진 마늘을 볶다가 양파, 양배추를 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1571, 296, 2, '양배추에 기름이 돌면 분량의 양념과 쫄면 혹은 더운 물에 불린 당면을 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1572, 296, 3, '마지막으로 순대와 깻잎을 넣고 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1573, 297, 1, '닭은 뼈가 없는 안심살 혹은 가슴살로 준비하여 먹기 좋은 크기로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1574, 297, 2, '분량의 양념재료를 섞어서 닭을 재워 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1575, 297, 3, '양배추는 단단한 심을 제거하고 한 입 크기로 썰고 고구마도 얄팍하게 저미고 양파는 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1576, 297, 4, '두꺼운 철판이나 넓은 프라이팬에 기름을 두른 후 각종 채소와 양념에 재운 닭을 넣고 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1577, 297, 5, '닭이 거의 익으면 마지막으로 깻잎을 넣고 볶아낸 후 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1578, 298, 1, '베이컨은 렌지에 10초간 돌려 살짝 익혀 기름기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1579, 298, 2, '피망은 얇게 채썰어 5cm로 잘라놓고, 흰떡, 샐러리와 맛살도 5cm 정도로 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1580, 298, 3, '베이컨에 준비한 재료를 놓고 돌돌 말아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1581, 298, 4, '팽이버섯과 채썬 피망은 함께 베이컨에 말아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1582, 298, 5, '꼬치에 종류별로 끼운 후 그릴에 노릇하게 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1583, 298, 6, '머스터드, 마요네즈, 간장, 레몬즙을 섞어 소스를 만든후 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1584, 299, 1, '열무김치는 물기를 짠 뒤 5cm로 썰어 참기름에 무쳐 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1585, 299, 2, '다진 쇠고기는 양념장에 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1586, 299, 3, '팬에 키친타올로 기름기를 거의 없을 정도로만 바르고 쇠고기를 달달 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1587, 299, 4, '고추장 양념재료를 모두 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1588, 299, 5, '밥을 담고 밥 위에 열무김치를 올려담은 후 양념장을 곁들여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1589, 300, 1, '다시마를 0.5cm로 썰어 30분 정도 냉수에 담갔다가 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1590, 300, 2, '청고추, 당근은 5cm 정도로 채썰고 맛살도 가늘게 찢어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1591, 300, 3, '팬에 기름을 두르고 마늘을 볶다가 다시마, 당근, 맛살을 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1592, 300, 4, '청고추채를 넣고 생강즙, 청주, 소금, 후춧가루, 참기름으로 양념을 한 뒤, 접시에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1593, 301, 1, '물에 불린 당면은 끓는 물에 삶아 헹군 뒤 10cm 정도로 잘라 햇살담은간장, 설탕, 참기름에 무쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1594, 301, 2, '양파, 당근, 실파는 6cm 길이로 채썰고  목이버섯, 느타리버섯은 가늘게 찢어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1595, 301, 3, '쇠고기도 채썰고 분량의 양념장에 무쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1596, 301, 4, '달궈진 팬에 기름을 두르고 각각의 채소와 버섯, 고기를 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1597, 301, 5, '넓은 팬에 당면을 살짝 볶다가 나머지 재료들을 모두 넣고 모두 섞듯이 볶아 바로 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1598, 301, 6, '참기름을 두르고 넓은 접시에 밥을 담은 후 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1599, 302, 1, '볼에 분량의 밀가루와 설탕, 소금, 이스트, 계란, 물, 쑥가루를 넣고 고루 섞어 한 덩어리로 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1600, 302, 2, '①에 부드럽게 녹인 버터를 조금씩 섞어가면 반죽을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1601, 302, 3, '적당한 크기로 나눠  길게 막대모양으로 민 후, 둥글게 한번 꽈주고 틀에 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1602, 302, 4, '180℃의 오븐에서 20분 간 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1603, 303, 1, '여린 깻잎보다는 약간 억센 깻잎을 골라 흐르는 물에 깨끗이 씻어 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1604, 303, 2, '항아리에 된장과 함께 깻잎을 켜켜이 담은 다음 망을 씌운후 뚜껑을 덮어 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1605, 303, 3, '한달 정도 삭힌 후 꺼내어 먹는다. (찜통에 살짝 쪄서 먹어도 좋다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1606, 304, 1, '쌀국수는 끓는 물에 삶아서 찬물에 헹궈 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1607, 304, 2, '쇠고기는 분량의 양념에 재웠다가 간이 어느정도 배면 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1608, 304, 3, '양파는 채썰어서 소금, 설탕, 레몬즙에 절여둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1609, 304, 4, '새우는 내장을 빼내고 레몬과 소금을 넣은 끓는 물에 데친 후, 색이 변하면 꺼내어 식혀 껍질을 벗겨둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1610, 304, 5, '숙주는 다듬어 약한 소금물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1611, 304, 6, '상추는 씻어서 물기를 뺀뒤 1cm 폭으로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1612, 305, 1, '아욱은 찬물에 소금으로 바락바락 문질러 씻고, 억센 줄기를 떼어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1613, 305, 2, '쌀뜨물에 멸치를 서너마리 넣고 물을 끓이다 된장을 풀고 한소끔 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1614, 305, 3, '멸치를 건져내고 모시조개를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1615, 305, 4, '조개국물이 우러나고 국물이 한번 팔팔 끓으면 조개는 건져내고 아욱을 넣고 은근하게 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1616, 305, 5, '아욱이 부드럽게 익으면 조개를 넣고 대파, 마늘을 넣고, 두부도 작게 썰어 넣어도 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1617, 306, 1, '달구어진 팬에 기름을 두르고 마늘을 넣어 볶다가 고추장을 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1618, 306, 2, '물과 고춧가루, 설탕을 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1619, 306, 3, '②에 어묵을 넣어 끓이다가 라면을 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1620, 306, 4, '라면이 어느정도 반쯤 익었을때 말랑한 떡과 대파를 넣고 자작자작하게 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1621, 307, 1, '고등어는 손질하여 5~6cm로 토막낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1622, 307, 2, '배추김치의 속을 어느정도 털어내고 냄비 위에 편편하게 깐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1623, 307, 3, '분량의 양념을 섞어 만든 양념장을 배추 위에 조금 붓고 고등어를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1624, 307, 4, '고등어 위에 양념장을 골고루 끼얹고 물을 부어 센불에서 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1625, 307, 5, '양념장이 거의 없어 지게 될 무렵, 찬물을 조금 더 부어 자작하게 조린 후 접시에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1626, 308, 1, '쇠고기는 채썰어 녹말가루와 계란에 무쳐 뜨거운 팬에 재빨리 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1627, 308, 2, '새우살도 녹말가루에 버무린 후 뜨거운 팬에 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1628, 308, 3, '불린 해삼은 반을 갈라 손질하고 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1629, 308, 4, '표고버섯도 손질하여 채썰고 죽순도 채를 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1630, 308, 5, '부추는 4~5cm 정도로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1631, 308, 6, '완두콩은 소금물에 파랗게 삶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1632, 309, 1, '바지락은 껍질째 박박 문질러 잘 씻고 찬물에 한시간 정도 더 담가두어 완전히 해감시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1633, 309, 2, '마늘은 다지고 대파와 마른고추는 송송 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1634, 309, 3, '팬에 기름을 두르고 마늘을 볶다가 두반장과 설탕을 넣어 기름에 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1635, 309, 4, '③이 기름에 잘 어우러지면 바지락을 넣고 뒤적거리듯 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1636, 309, 5, '조개가 입이 벌어지면 대파와 마른고추를 넣고 한번 더 볶아낸 후, 접시에 예쁘게 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1637, 310, 1, '가지는 반으로 가른 후 어슷하게 저며 썰고, 대파는 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1638, 310, 2, '고추는 물에 헹궈 씨를 뺀 후 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1639, 310, 3, '쇠고기는 기름이 적당하게 섞인 등심으로 준비해 얇게 저며 썬 후 청주, 후춧가루로 밑간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1640, 310, 4, '달군 프라이팬에 기름을 두르고 ②의 쇠고기를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1641, 310, 5, '③의 쇠고기가 알맞게 익으면 기름을 넉넉하게 넣고 ①의 가지와 고추를 넣어 간장, 다진 파, 다진 마늘, 소금, 후춧가루로 간하여 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1642, 310, 6, '깨소금, 참기름으로 마무리한 뒤 접시에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1643, 311, 1, '팽이버섯은 뿌리 부분을 자른 뒤 작은 덩어리로 나누고, 느타리버섯은 끓는 물에 데쳐 물기를 꼭 짠 다음 먹기 좋은 크기로 쭉쭉 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1644, 311, 2, '양송이버섯은 깨끗이 씻어 밑동 끝의 지저분한 부분을 잘라내고 껍질을 벗긴 다음 모양을 살려 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1645, 311, 3, '호박과 두부는 도톰하게 썰고, 굵은 파와 고추는 어슷하게 썰어서 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1646, 311, 4, '뚝배기에 쌀뜨물을 부어 끓으면 청국장을 푼 다음 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1647, 311, 5, '국물이 보글보글 끓으면 손질한 양송이버섯, 느타리버섯, 두부, 호박, 홍고추를 넣고 다시 한번 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1648, 311, 6, '청국장이 한소끔 끓으면 굵은 파, 청고추, 다진 마늘을 넣고 살짝 끓인 후 마지막에 팽이버섯을 넣고 불에서 내려 상에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1649, 312, 1, '부추는 깨끗이 다듬은 후 잘 씻어 3~4cm 길이로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1650, 312, 2, '청고추는 반을 갈라 씨를 뺀 후 어슷하게 썰어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1651, 312, 3, '오징어는 내장을 빼고 껍질을 벗겨낸 후 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1652, 312, 4, '오목한 볼에 밀가루, 찹쌀가루, 계란, 물을 넣어 반죽해 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1653, 312, 5, '준비한 밀가루 반죽에 부추, 청고추, 오징어를 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1654, 312, 6, '기름 두른 팬에 반죽을 한 국자씩 떠 넣고 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1655, 313, 1, '얇게 썬 채끝살은 하나씩 데쳐서 냉수에 담가 기름을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1656, 313, 2, '①을 맛술, 간장, 설탕에 넣어 하루정도 잰다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1657, 313, 3, '양배추와 당근은 곱게 채썰고 무순은 깨끗이 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1658, 313, 4, '②의 고기에 ③을 채우고 한입 크기로 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1659, 313, 5, '와사비초장을 함께 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1660, 314, 1, '닭안심은 곱게 다지거나 커터기로 갈아둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1661, 314, 2, '표고버섯은 물에 불려 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1662, 314, 3, '느타리버섯은 렌지에 살짝 가열하고 곱게 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1663, 314, 4, '팽이버섯은 2cm로 썰고 당근과 고추는 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1664, 314, 5, '준비된 재료에 분량의 양념을 섞어 잘 치대어 네모지게 모양을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1665, 314, 6, '랩을 씌워 렌지 또는 찜통에 5분간 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1666, 315, 1, '쇠고기는 홍두깨살로 준비해 도톰하게 저미고 칼등으로 두들겨 연하게 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1667, 315, 2, '손질한 쇠고기는 3cm x 10cm 정도로 썰어 분량의 양념에 무쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1668, 315, 3, '새송이는 0.5cm 두께로 도톰하게 썰어 기름장에 무쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1669, 315, 4, '꼬치에 송이와 쇠고기를 번갈아 손바닥으로 받치면서 넉넉하게 꿴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1670, 315, 5, '팬에 기름을 조금만 두르고 산적을 지져낸 후 접시에 담아 잣가루를 뿌려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1671, 316, 1, '돼지고기는 곱게 다지고 부추는 깨끗이 씻어 다듬은 뒤 물기를 빼 잘게 썰고 굵은 파와 생강, 마늘은 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1672, 316, 1, '볼에 밀가루와 소금을 넣고 찬물을 부어가며 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1673, 316, 2, '돼지고기에  부추와 남은 재료, 분량의 양념을 섞은 뒤 고루 섞이도록 잘 젓고 준비된 만두피에 만두소를 한 숟가락 넣고 양끝을 모아 올리듯이 싼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1674, 316, 3, '냄비에 물을 넉넉히 부어 팔팔 끓으면 만두를 넣고 뚜껑을 닫고 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1675, 316, 4, '물이 끓어오르면 찬물을 반컵 정도 붓고 다시 끓이고 만두가 동동 떠오르면 체로 건져내어 오목한 그릇에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1676, 317, 1, '쌀국수는 찬물에 담가 부드럽게 만든 후 끓는물에 삶아 찬물에 헹궈 물기를 빼둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1677, 317, 2, '돼지고기는 채썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1678, 317, 3, '숙주나물은 꼬리부분을 다듬고 청고추, 홍고추는 얇게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1679, 317, 4, '파는 채썰고 마늘도 얇게 채썬고 생강은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1680, 317, 5, '팬에 기름을 두르고 ④를 넣고 달달 볶아 향을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1681, 317, 6, '돼지고기를 넣어 볶다가 굴소스와 분량의 양념을 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1682, 318, 1, '끓는물에 올리브오일을 조금 넣고 준비한 파스타-파파델리를 넣고 삶은 뒤 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1683, 318, 2, '달군 팬에 기름을 두른 뒤 ①의 면을 넣고 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1684, 318, 3, '생표고버섯은 기둥을 떼낸 후 채썰고, 느타리버섯은 손으로 2~3등분하여 찢는다. 양송이도 굵게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1685, 318, 4, '달군 팬에 올리브오일을 두른 뒤 준비한 각종 버섯을 넣고 볶다가 ②의 면을 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1686, 318, 5, '맨 마지막에 간장, 화이트와인, 다진 마늘을 넣고 살짝 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1687, 318, 6, '그릇에 보기 좋게 담은 뒤 고수 잎을 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1688, 319, 1, '물을 넉넉하게 끓인 후 닭봉을 넣고 대파는 흰 뿌리 부분만 잘라 얇게 저며 썬 마늘, 생강과 함께 넣고 끓인다. 끓일 때 생기는 거품은 걷어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1689, 319, 2, '닭봉이 익으면 건져 식힌 후 살을 잘게 뜯고 국물은 고운 망에 걸러 닭개장 육수로 사용한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1690, 319, 3, '고사리는 단단한 줄기는 잘라낸 후 5㎝ 길이로 썰고 도라지는 가늘게 찢어 소금을 넣고 주물러 쓴 맛을 뺀 다음 끓는물에 넣어 살짝 데쳐 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1691, 319, 4, '대파는 파란 부분을 5㎝ 길이로 잘라 끓는 물에 소금을 넣고 살짝 데쳐 찬물에 헹구어 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1692, 319, 5, '고춧가루에 닭육수를 넣어 불린 후 분량의 마늘, 생강, 참기름, 후춧가루, 소금을 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1693, 319, 6, '넓은 볼에 닭살, 고사리, 대파를 넣고 준비된 양념을 무쳐 끓는 닭육수에 넣고 끓인다. 중불에서 은근하게 끓여야 국물 맛이 개운하다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1694, 320, 1, '김치는 1cm 크기로 썰고 햄, 양파, 감자도 1cm 정도의 크기로 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1695, 320, 2, '프라이팬에 기름을 두르고 김치와 감자를 볶다가 햄과 양파를 넣고 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1696, 320, 3, '양파가 어느 정도 익으면 밥을 넣고 펴듯이 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1697, 320, 4, '소금과 후춧가루로 간을 맞추고 계란프라이를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1698, 321, 1, '오징어채는 4cm 로 잘라 잔손질을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1699, 321, 2, '팬에 분량의 양념을 넣어 끓인다. 거품이 날때까지 팔팔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1700, 321, 3, '조림장이 끓으면 불을 줄인 후 오징어를 넣고 뭉치지 않게 고루 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1701, 321, 4, '참기름을 두른 후 접시에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1702, 322, 1, '바지락은 옅은 소금물에 담가 해감을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1703, 322, 2, '냄비에 물을 넣고 바지락을 넣어 국물을 낸다. 바지락 입이 벌어지면 바지락은 건져내고 국물은 면보에 걸러둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1704, 322, 3, '②의 조갯국물에 청장, 맛술, 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1705, 322, 4, '양파는 채썰고 애호박도 채를 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1706, 322, 5, '③의 국물에 ④의 채소를 넣고 끓이다가 준비한 국수를 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1707, 322, 6, '국수가 어느정도 익어가면 건져놓은 조개와 다진 마늘을 넣어 끓여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1708, 323, 1, '도라지는 껍질을 벗겨 가늘게 찢고 소금으로 주물러 쓴 맛을 없애준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1709, 323, 2, '오이는 반을 갈라 어슷 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1710, 323, 3, '오징어는 껍질을 벗긴 후 4cm 길이로 가늘게 채썰어 끓는 물에 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1711, 323, 4, '분량의 양념 재료를 잘 섞어 양념장을 만든후 손질한 재료를 모두 넣어 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1712, 323, 5, '마지막에 참기름 한 방울과 깨소금을 넣어 무쳐서 상에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1713, 324, 1, '애호박을 1cm 두께로 도톰하고 둥글게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1714, 324, 2, '팬에 기름을 두르고 뜨거워지면 호박을 얹어 노릇하게 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1715, 324, 3, '홍고추는 씨를 빼고 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1716, 324, 4, '새우젓은 면보에 꼭 짜 건더기는 버리고 국물만 쓴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1717, 324, 5, '볼에 국간장과 새우젓국, 다진 고추와 나머지 양념을 모두 넣어 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1718, 324, 6, '지져낸 호박을 양념과 고루 섞어 무쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1719, 325, 1, '찹쌀을 2시간 정도 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1720, 325, 2, '양송이버섯은 껍질을 벗기고 모양을 살려서 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1721, 325, 3, '호박, 당근, 실파, 깻잎은 깨끗이 씻은 후 3㎝ 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1722, 325, 4, '불린 찹쌀은 방망이로 쌀알이 1/2정도가 되도록 부순다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1723, 325, 5, '냄비에 참기름을 두르고 ④를 볶다가 물을 붓고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1724, 325, 6, '쌀알이 퍼지면 썰어 놓은 채소를 넣고 한번 더 끓여 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1725, 326, 1, '미역은 물에 불려서 깨끗이 씻어 먹기 좋은 크기로 손으로 찢어 참기름에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1726, 326, 2, '홍합은 속털을 말끔히 손질하고 씻어서 3∼4등분하여 소금을 뿌렸다가 참기름에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1727, 326, 3, '볶을 때 생긴 국물에 끓는물 3컵을 붓고 미역을 넣어 서너 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1728, 326, 4, '국물이 뽀얗고 미역이 파랗게 되었을 때 간장, 후춧가루로 간을 하고 ②의 홍합을 넣어 한소끔 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1729, 327, 1, '오징어는 내장을 빼고 껍질을 벗긴 후 동그랗게 썰고, 양파와 피망은 채썰고 실파는 4cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1730, 327, 2, '양념 재료를 혼합하여 양념장을 만들어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1731, 327, 3, '프라이팬에 기름을 두르고 양파를 볶다가 오징어를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1732, 327, 4, '오징어가 불투명하게 익어가면 양념장을 넣어 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1733, 327, 5, '국물이  끓으면  피망을 넣어 볶다가 채썬 파를 넣고 뒤적여 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1734, 327, 6, '접시에 밥을 소복이 담고 오징어 볶음을 얹어 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1735, 328, 1, '완두와 옥수수는  체에 밭쳐 물기를 빼둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1736, 328, 2, '당근과 감자는 완두크기로 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1737, 328, 3, '껍질콩도 비슷한 크기로 썰어 끓는 물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1738, 328, 4, '팬에 기름을 두르고 다진 마늘을 넣어 볶다가 감자, 당근 , 완두, 옥수수 순으로 넣어 센불에서 재빨리 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1739, 328, 5, '간장과 굴소스를 넣어 볶다가 소금, 후춧가루로 간을 맞춘 뒤 참기름 한방울을 둘러 마무리 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1740, 329, 1, '대구는 내장과 지느러미를 손질하여 5cm 정도의 크기로 토막내어 소금물에 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1741, 329, 2, '콩나물은 꼬리를 제거하고 무는 두껍지 않게 은행잎모양으로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1742, 329, 3, '홍고추, 청고추는 어슷하게 썰어 물에 한번 씻고 미나리는 5cm 정도의 길이로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1743, 329, 4, '냄비에 다시마 우려낸물을 담고 무와 콩나물을 넣어 시원한 맛을 우려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1744, 329, 5, '고춧가루 2큰술, 고추장 1큰술, 소금, 후춧가루, 다진 마늘, 다진 생강, 새우젓을 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1745, 329, 6, '④의 국물에 대구를 넣어 한소끔 끓인 후 양념장을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1746, 330, 1, '쇠고기를 얇게 저민 불고기 감으로 준비하여 다진 파, 마늘, 깨소금, 후춧가루, 설탕, 참기름, 간장을 넣고 양념하여 1시간 정도 재운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1747, 330, 2, '넓은 접시에 찹쌀가루를 펴고 밑간한 쇠고기의 앞 뒤를 고루 묻힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1748, 330, 3, '프라에팬에 기름을 넉넉히 두르고 찹쌀가루를 묻힌 쇠고기를 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1749, 330, 4, '겨자, 식초, 설탕을 각각 2큰술씩 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1750, 331, 1, '고추는 어리고 맵지 않은것을 준비하여 반을 갈라서 씨를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1751, 331, 2, '찹쌀가루에 약간의 소금을 넣어  씨를  제거한 고추를 살짝 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1752, 331, 3, '찜통에 김이 오르면 찹쌀가루에 버무린 고추를 넣어 찐 다음 햇볕에  말린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1753, 331, 4, '180℃ 정도의 기름에 살짝 튀겨 건진 다음 설탕을 솔솔 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1754, 332, 1, '오징어는 껍질과 먹통을 제거한 후 5cm 정도의 길이로 채썰어 끓는물에 살짝 데쳐 찬물에 담가 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1755, 332, 2, '오징어에 고춧가루 1큰술, 설탕 1/2큰술, 식초 1큰술, 다진 마늘. 다진 파, 참기름, 깨소금을 넣어 오물조물 무쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1756, 332, 3, '무도 크지 않게 썰어 식초물에 잠시 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1757, 332, 4, '무가 어느 정도 삭히면 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1758, 332, 5, '설탕 1큰술, 식초 2큰술, 고춧가루 1과 1/2큰술, 액젓 1큰술, 다진 마늘, 다진 파 등의 양념장을 만들어 ④의 무를 간한 후 냉장고에 보관해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1759, 332, 6, '김은 반 잘라서 밥을 얇게 편 다음 돌돌 말아서 3등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1760, 333, 1, '닭다리, 대파, 생강, 맛술, 물을 넣고 중불에서 서서히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1761, 333, 2, '40분 정도 은근히 끓여서 육수가 다 우러나면 살은 건져내고 국물은 체에 밭쳐 맑은 스톡을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1762, 333, 3, '중국 부추는 다듬어서 4cm 길이로 썰고, 당근은 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1763, 333, 4, '닭살은 한번 식으면 결대로 잘게 찢어 놓고, 닭스톡은  냄비에 넣고 끓이다가 당근과 부추를 넣어 다시 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1764, 333, 5, '닭스톡에 간장과 소금, 후춧가루를 넣어 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1765, 333, 6, '물이 팔팔 끓으면 생소면을 넣고 삶는다. 끓어오르기 시작하면 찬물을 넣는다. 생소면이 어느정도 익었으면 찬물에 헹구어 쫄깃한 면발을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1766, 334, 1, '감자는 껍질을 벗겨서 물에 씻은 후 얇게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1767, 334, 2, '양파와 대파도 얇게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1768, 334, 3, '계란노른자는 크림을 섞어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1769, 334, 4, '냄비에 버터를 두르고 양파와 파를 넣어서 볶다가 감자를 넣고 살짝 볶은 후 육수를 붓고 뚜껑을 덮어서 뭉근히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1770, 334, 5, '감자가 푹 익으면 체에 걸러준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1771, 334, 6, '걸른 것을 다시 냄비에 담아 소금, 후춧가루로 간을 하고 불을 끈 다음 계란노른자을 넣고 잘 저어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1772, 335, 1, '바게트 빵을 준비하여 1.5~2cm 두께로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1773, 335, 2, '버터를 그릇에 넣고 거품기로 쳐 부드럽게 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1774, 335, 3, '마늘은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1775, 335, 4, '버터에 마늘과 파슬리가루를 넣고 섞어 빵에 바르고 170℃ 오븐에서 10분간 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1776, 336, 1, '닭은 푹 삶아서 살은 찢어 준비하고 육수는 거즈에 걸러둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1777, 336, 2, '죽순은 빗살무늬를 살려 썰고 당근은 5cm 길이로 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1778, 336, 3, '표고버섯은 얇게 저미고 실파도 5cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1779, 336, 4, '①의 육수에 닭살과 채소를 넣고 끓이다가 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1780, 336, 5, '③에 물녹말을 넣어 걸쭉하게 농도를 조절한 다음 계란을 푼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1781, 336, 6, '불을 끄고 ④에 참기름을 둘러 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1782, 337, 1, '옥수수통조림은 물기를 빼둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1783, 337, 2, '당근, 옥수수통조림은 잘게 다져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1784, 337, 3, '시금치는 소금을 넣고 살짝 데쳐서 물기를 제거한 후 잘게 다져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1785, 337, 4, '큰 볼에 계란, 당근, 옥수수, 다진 파를 넣고 소금, 설탕, 후춧가루, 새우젓으로 간하여 분량의 물을 넣고 거품기로 잘 풀어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1786, 337, 5, '깊은 냄비에 ④를 넣고 끓는 물에서 중탕하여 중불로 15분 정도 쪄낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1787, 338, 1, '쌀, 보리등은 깨끗이 씻어 고슬고슬하게 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1788, 338, 2, '당근, 오이는 5cm 길이로 채썰어서 굵은 소금 한 숟가락에 절여 물기를 꼭 짜서 기름에 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1789, 338, 3, '콩나물은 꼬리를 떼고 물에 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1790, 338, 4, '다진쇠 고기는 분량의 양념장 재료를 넣고 양념하여 기름에 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1791, 338, 5, '분량의 밥 위에 데친 콩나물, 볶은 오이, 볶은 당근, 볶은 쇠고기를 보기좋게 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1792, 338, 6, '간장, 참기름, 깨소금, 후춧가루, 다진 파, 마늘 등을 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1793, 339, 1, '콩을 씻어 일어 건진다음 콩조각과 껍질을 버리고 소금 1/2작은술을 넣고 곱게 빻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1794, 339, 2, '불린 찹쌀의 물기를 빼고 찐다. 익을 무렵 물에 소금을 풀어 익어가는 밥에 끼얹어 주걱으로 뒤적거리고 다시 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1795, 339, 3, '절구에 밥을 넣고 절굿공이를 간간한 소금물에 적셔 가며 잘 찧는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1796, 339, 4, '넓은 양푼에 콩가루를 체로 쳐서 깔아놓고, 그 위에 떡을 평평하게 놓는다. 그 위에 다시 콩가루를 체로 쳐서 떡을 덮는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1797, 339, 5, '떡을 손질하여 작은 접시를 굴려 떡의 폭을 자른 뒤 도마에 놓고 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1798, 339, 6, '썬 인절미에 다시 콩가루를 묻힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1799, 340, 1, '들깻잎, 엽상치, 쑥갓 등은 먹기 좋은 크기로 뜯어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1800, 340, 2, '대파, 마늘은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1801, 340, 3, '①을 싱싱하게 하기위해 물에 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1802, 340, 4, '고운 고춧가루 1작은술, 설탕 1/2작은술, 소금, 식초 1/2작은술, 조림간장 1/4작은술, 깨소금, 참기름으로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1803, 340, 5, '물에서 채소를 건져내어 물기를 없앤 후 ④의 양념장에 묻혀낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1804, 341, 1, '호박은 0.5cm 두께로 반달썰기한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1805, 341, 2, '양파도 같은 굵기로 채썰어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1806, 341, 3, '두부는 크지않게 네모지게 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1807, 341, 4, '대파, 홍고추를 어슷하게 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1808, 341, 5, '기름을 두르지 않고 볶은 국멸치를 한소끔 끓인 물에 된장 한 큰술을 덩어리가 지지 않도록 잘 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1809, 341, 6, '⑤가 끓기 시작하면 호박, 양파, 두부를 차례대로 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1810, 342, 1, '꽁치는 머리, 꼬리를 자르지 말고 배를 갈라 내장을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1811, 342, 2, '물에 한번 씻은 꽁치를 소금에 절여둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1812, 342, 3, '소금에 절여둔 꽁치에 칼집을 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1813, 342, 4, '예열시킨 그릴에 호일을 깔고 꽁치를 타지 않게 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1814, 342, 5, '간장, 갠와사비, 레몬을 곁들인 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1815, 343, 1, '오징어는 껍질이 검고 투명한 것으로 골라 소금물에 깨끗이 씻은 뒤 흐르는 물에 헹구어 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1816, 343, 2, '오징어의 몸통과 다리를 잘라 소금 4큰술을 뿌려 8시간 정도 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1817, 343, 3, '삭힌 오징어는 0.5cm 두께로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1818, 343, 4, '소금 1/2큰술, 다진 파 1큰술, 다진 마늘 1/2큰술, 다진 생강 1/4큰술, 고춧가루 2큰술을 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1819, 343, 5, '준비한 양념장에 오징어채를 넣어 버무린 다음 항아리에 담아 서늘한 곳에서 일주일 정도 삭힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1820, 344, 1, '무는 5cm 길이로 곱게 채를 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1821, 344, 2, '다진마늘, 다진파를 비롯한 분량의 양념 재료를 넣고 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1822, 344, 3, '양념장이 만들어지면 무를 넣고 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1823, 344, 4, '그릇에 담아낼때 깨소금을 약간 뿌려서 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1824, 345, 1, '다시마는 찬 물에 1시간 정도 담가 놓았다가 다시마 맛이 우러나오면 불에 올려 살짝 한번 끓인다.(대파, 양파등 야채들과 같이 국물을 우려 내면 시원한 맛이 난다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1825, 345, 2, '새우, 바지락, 홍합 등은 깨끗이 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1826, 345, 3, '호박은 반달썰기 하고, 감자는 채를 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1827, 345, 4, '대파, 홍고추, 청고추는 어슷하게 썰어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1828, 345, 5, '①의 국물을 간장, 소금으로 간하고 ②의 해물과 채소를 넣고 한소끔 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1829, 345, 6, '한소끔 끓여낸 다음 마지막에 대파, 쑥갓을 띄워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1830, 346, 1, '안심은 스테이크감용으로 준비하여 소금과 후춧가루로 밑간해둔다. 고기의 맛을 좋게 하기위하여 당근, 샐러리 등의 채소를 저며 같이 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1831, 346, 2, '밀가루 2큰술, 버터 2큰술을 팬에 넣어 갈색이 나도록 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1832, 346, 3, '밀가루가 버터에 볶아지면 육수를 조금씩 넣어가면서 멍울을 풀어준다. 그리고 우스터소스, 토마토페스트, 소금, 후춧가루, 레드와인등을 넣어 스테이크 소스를 만들어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1833, 346, 4, '센불에 ①의 안심을 갈색이 나도록 지져준 다음 중불에서 속까지 익히도록 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1834, 346, 5, '가니쉬로 당근, 오이, 감자 등을 모양있게 썰어 버터에 한번 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1835, 347, 1, '파래는 물에 씻어 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1836, 347, 2, '당근은 길이대로 썰어놓고 마늘과 파는 다져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1837, 347, 3, '간장 1큰술, 설탕 2큰술, 식초1과 1/2큰술, 깨소금, 참기름으로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1838, 347, 4, '물기를 짠 파래를 ③의 양념장에 새콤달콤하게 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1839, 348, 1, '단단한 단호박은 반으로 잘라 씨를 제거한 다음 끓는물에 한번 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1840, 348, 2, '단호박 살이 1/3 정도 익으면 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1841, 348, 3, '두께 0.5cm 정도로 썰어서 껍질을 벗겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1842, 348, 4, '③에 밀가루를 뿌려둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1843, 348, 5, '계란, 물, 밀가루, 튀김가루를 섞어서 반죽을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1844, 348, 6, '170℃ 정도의 기름의 온도가 되면 단호박을 반죽에 넣어 바삭하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1845, 349, 1, '끓는 물에 소금, 식용유를 넣고 스파게티면을 넣어 8분 정도 삶아 체에 밭쳐 물기를 뺀 후 올리브오일에 버무려놓는다. 스파게티를 삶아낸 물 1컵은 따로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1846, 349, 2, '오징어는 껍질을 벗겨 링 모양으로 썰고, 새우는 등의 내장을 제거하며 홍합은 깨끗이 씻어 불순물을 떼어 준비해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1847, 349, 3, '양파와 마늘, 양송이, 샐러리를 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1848, 349, 4, '프라이팬에 올리브오일을 두르고 다진 양파와 마늘, 양송이, 샐러리를 볶다가 토마토를 잘게 썰어 같이 볶고, 다진 홀토마토를 넣은 후 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1849, 349, 5, '다른 프라이팬에 버터를 녹여 뜨거워지면 밀가루 1큰술을 넣고 타지 않게 볶다가 스파게티 삶은 물 1컵을 붓고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1850, 349, 6, '②에 ④를 붓고 끓이다가 소금, 후춧가루로 간하고 오레가노를 넣고 불에서 내려 삶은 면을 버무려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1851, 350, 1, '쌀국수는 미지근한 물에 불려 담갔다가 끓는 물에 삶아 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1852, 350, 2, '오징어는 칼집을 내어 끓는 물에 데친 후 찬물에 한번 헹구어 5cm 길이로 채썰고, 오이, 당근, 양파, 표고버섯도 5cm 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1853, 350, 3, '②를 식초, 설탕, 소금을 섞어 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1854, 350, 4, '키위, 파인애플은 가로 1cm로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1855, 350, 5, '라이스페이퍼는 따뜻한 물에 담근 뒤 부드러워지면 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1856, 350, 6, '라이스페이퍼를 깔고 준비한 모든 재료를 가운데 놓고 돌돌 말아 먹기 좋게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1857, 351, 1, '무는 세로5cm, 가로0.5cm 정도로 일정하게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1858, 351, 2, '프라이팬에 기름을 두른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1859, 351, 3, '어느정도 프라이팬이 달구어지면 채썬 무를 넣고 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1860, 351, 4, '어느정도 무가 볶아지면 새우젓으로 간을 맞춘 후 흑임자를 넣어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1861, 351, 5, '마지막으로 참기름을 조금 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1862, 352, 1, '신선한 고등어를 적당하게 잘라 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1863, 352, 2, '무를 생선 크기만하게 자른 후 한 번 삶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1864, 352, 3, '다시마 물을 우려내어 준비한 무와 고등어를 넣고 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1865, 352, 4, '미림 6큰술, 설탕 3큰술, 된장 50g, 술 3/4컵, 간장 3큰술로 양념장을 만들어 ③에 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1866, 352, 5, '중불에서 2시간정도 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1867, 352, 6, '파를 일정하게 채썰어 조려낸 고등어 위에 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1868, 353, 1, '두부는 가로 3cm, 세로 6cm 정도로 썬 후에 끓는 물에 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1869, 353, 2, '양파, 양배추, 대파, 홍고추, 풋고추등은 채썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1870, 353, 3, '고춧가루, 간장, 설탕, 후춧가루, 마늘, 참기름, 깨소금 등으로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1871, 353, 4, '프라이팬에 기름을 두르고 ②의 야채를 볶다가 양념장과 물 1/2컵을 넣은 후 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1872, 353, 5, '녹말물을 만들어 ④에 넣어 국물을 걸죽하게 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1873, 353, 6, '데친 두부를 ⑤에 넣어 다시 한번 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1874, 354, 1, '고구마는 깨끗이 씻어 껍질째 찜통에서 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1875, 354, 2, '찐 고구마는 윗면의 1/3 부분을 잘라 속을 파내어 고구마 그릇을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1876, 354, 3, '파낸 고구마 속살은 숟가락으로 으깨어 생크림, 소금, 설탕, 치즈를 잘게 다져 섞은 뒤 소금간 하여 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1877, 354, 4, '②에 ③을 채우고 모짜렐라 치즈를 뿌려 200℃로 예열한 오븐에서 5분 정도 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1878, 354, 5, '구워낸 4에 파슬리가루를 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1879, 355, 1, '불린쌀은 1/2정도 으깬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1880, 355, 2, '시금치는 한 번 데친 후 잘게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1881, 355, 3, '다시마와 가쓰오브시로 국물을 우려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1882, 355, 4, '쇠고기는 다져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1883, 355, 5, '냄비에 참기름을 두르고 쇠고기를 볶다가 불린쌀을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1884, 355, 6, '③번 국물을 ⑤에 넣은 후 일본된장을 1/2큰술을 넣은 후 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1885, 356, 1, '돼지고기 안심을 준비하여 대파, 통후추, 양파 등을 넣고 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1886, 356, 2, '당근, 양파, 깻잎은 5cm 정도로 채썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1887, 356, 3, '양겨자 1/2큰술, 설탕 1/2작은술, 소금 약간, 식초 1/4 작은술로 양념장을 만들어 ②번을 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1888, 356, 4, '삶은 돼지고기는 한 입 크기로 준비하여 ③의 채소를 넣어 마주보게 한다.(샌드위치모양으로 겹쳐지게 한다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1889, 357, 1, '페투치네면은 소금, 올리브오일을 넣은 넉넉한 물에 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1890, 357, 2, '삶은 페투치네면은 찬물에 헹구지 말고 체에 밭쳐 그대로 물기를 빼낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1891, 357, 3, '베이컨, 양파는 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1892, 357, 4, '마늘은 편으로 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1893, 357, 5, '팬에 올리브오일을 두르고 편으로 썬 마늘을 볶다가 ③의 베이컨과 양파를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1894, 357, 6, '양파가 투명해질 정도로 익으면 휘핑크림을 넣어 중불에서 은근히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1895, 358, 1, '밥은 고슬하게 지어서 식초 1과1/2큰술, 설탕 2/3큰술, 소금 1/4큰술로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1896, 358, 2, '계란은 완숙으로 삶아서 노른자만 체에 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1897, 358, 3, '파슬리는 곱게 다져서 거즈에 싸서 물에 한 번 씻어 내린 후 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1898, 358, 4, '밥을 3그릇에 나눠 담은 후 노른자, 분홍색 오보로, 파슬리 다진 것을 넣고 삼색을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1899, 358, 5, '김을 1/3등분한 후 각각의 김에 삼색으로 낸 밥을 얇게 편 후 돌돌 말아 끝을 약간 올려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1900, 358, 6, '다시 김 1장을 준비하여 흰밥을 얇게 펴 바른 후 삼색으로 만 김밥 3개를 나란히 놓고 다시 한 번 말아 모양을 잡는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1901, 359, 1, '밥은 고슬하게 지어서 식초 1과1/2큰술, 설탕 2/3큰술, 소금 1/4큰술의 배합초를 섞어 초밥을 만들어 식혀둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1902, 359, 2, '게살은 살만을 발라서 준비하고 오이는 돌려깎기하여 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1903, 359, 4, '계란은 풀어서 소금간 한 다음 팬에 말아둔다.(두껍게 지단 부치듯이)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1904, 359, 5, '아보카도는 씨를 제거하고 껍질을 벗긴 다음 길이로 4등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1905, 359, 6, '박고지는 깨끗하게 씻어 불린 후 물 1, 간장 1, 설탕 1의 비율로 박고지가 잠길 정도로 붓고 약한불에서 국물이 거의 남지 않을 때까지 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1906, 359, 7, '김발에 랩을 깔고 김을 놓고 초밥을 얇게 편 후 뒤집어서 위의 재료들을 중앙에 가지런하게 놓은 다음 돌돌 만다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1907, 360, 1, '찹쌀, 조, 수수, 콩, 쌀은 30분 정도 물에 불려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1908, 360, 2, '불려놓은 팥은 끓는 물에 삶아 1/2정도 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1909, 360, 3, '①과 ②을 섞어 팥 삶은 물을 부어 밥을 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1910, 360, 4, '다진 쇠고기에 분량의 양념을 하여 프라이팬에 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1911, 360, 5, '오곡밥이 다 되면 한김 나가게 식혀준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1912, 360, 6, '오곡밥 70g 정도를 쥐고 안에 ④의 볶아놓은 쇠고기를 조금 넣어 모양을 동그랗게 잡아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1913, 361, 1, '무는 껍질을 벗기고  최대한 얇게 썰어 엷은 소금물에 담가 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1914, 361, 2, '미나리는 다듬에 5cm 길이로 자르고 배와 당근도 손질 한 후 5cm 길이로 채썬다. 생강도 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1915, 361, 3, '무가 약간 절여 졌으면 건져서 물기를 뺀 후 미나리와 배, 당근, 생강을 하나씩 올려 돌돌 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1916, 361, 4, '분량의 식초와 설탕을 타서 단촛물을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1917, 361, 5, '예쁘게 말아둔 무쌈을 준비한 촛물에 반나절 이상 담궜다가 차게 해서 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1918, 362, 1, '찹쌀가루를 익반죽하여 오랫동안 치댄다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1919, 362, 2, '쑥갓은 찬물에 담갔다가 잎을 떼고 대추는 돌려깎아 돌돌 말아 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1920, 362, 3, '찹쌀반죽을 동글게 모양을 잡아 5cm 정도로 둥글고 납작하게 빚는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1921, 362, 4, '팬에 기름을 두르고 반죽을 넣어 약한 불에서 서서히 익히다가 뒤집어 먼저 익은 쪽에 대추와 쑥갓잎을 붙여 모양을 낸 후 다시 뒤집어 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1922, 362, 5, '물과 설탕을 동량으로 끓여 젓지 않고 시럽을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1923, 362, 6, '지져낸 화전에 시럽을 묻혀 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1924, 363, 1, '냉동 만두는 기름에 바삭하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1925, 363, 2, '오이는 어슷하게 썰고 양파는 얇게 링으로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1926, 363, 3, '양상추는 손으로 뜯어 찬물에 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1927, 363, 4, '올리브도 얇게 저며둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1928, 363, 5, '머스터드 2큰술, 마요네즈 1큰술, 꿀 1큰술, 식초 1/2큰술을 섞어 허니머스터드 드레싱을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1929, 363, 6, '큰 볼에 먹음직스럽게 재료를 담은 후 드레싱을 끼얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1930, 364, 1, '콩나물은 꼬리를 다듬어 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1931, 364, 2, '끓는물에 소금을 약간 넣고 콩나물을 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1932, 364, 3, '미나리는 길이 6cm 정도로 썰어 소금물에 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1933, 364, 4, '데친 콩나물과 미나리는 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1934, 364, 5, '고춧가루, 소금, 참기름, 깨소금, 다진 파, 다진 마늘을 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1935, 364, 6, '콩나물과 미나리를 ⑤의 양념장에 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1936, 365, 1, '냄비에 가쓰오브시국물 5큰술, 간장 1큰술, 설탕 1큰술을 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1937, 365, 2, '양파, 쇠고기, 미나리는 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1938, 365, 3, '냄비에 ①과 쇠고기를 넣고 잠시 끓이다가 양파를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1939, 365, 4, '③을 한소끔 끓인 후 마지막에  미나리를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1940, 365, 5, '불을 끄기 전에 ④에 계란을 풀어 넣고 한소끔 끓인 후 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1941, 365, 6, '그릇에 밥을 담고 ⑤를 밥 위에 뿌려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1942, 366, 1, '연어알을 깨지지 않게 준비한다.(연어알대신 날치알을 사용해도 됨)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1943, 366, 2, '밥은 배합초로 양념하여 초밥을 만든다.(식초 1과1/2큰술, 설탕 2/3큰술, 소금 1/4큰술을 냄비에 넣어 입자가 녹을때까지 끓인다.)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1944, 366, 3, '김은 세로 10cm, 가로 1cm 정도로 잘라 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1945, 366, 4, '②번 밥에 김띠를 두른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1946, 366, 5, '④번 밥위에 와사비를 바르고 연어알로 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1947, 367, 1, '새우의 머리를 떼고 등쪽으로 내장을 뺀 다음 꼬리 1마디만 남기고 껍질을 벗긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1948, 367, 2, '풋고추, 홍고추는 길이로 반 갈라 씨를 털어 내고 0.3cm x 0.3cm 정도로 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1949, 367, 3, '양파, 마늘, 생강, 대파도 0.3cm x 0.3cm 정도로 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1950, 367, 4, '녹말 가루에 계란을 풀어 튀김 반죽을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1951, 367, 5, '①의 새우를 ④의 튀김 반죽에 버무려 기름에 넣고 살짝 튀겨 낸 다음 다시 한 번 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1952, 367, 6, '오목한 그릇에 간장, 설탕, 식초, 육수를 넣고 골고루 섞어 간장 소스를 만들어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1953, 368, 1, '두부, 생선살, 오징어는 작은 주사위 모양으로 손질하여 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1954, 368, 2, '게살, 새우와 ①번 재료를 같이 넣고 한 번 삶아 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1955, 368, 3, '육수에 정종, 소금, 미원, 설탕을 넣고 스프를만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1956, 368, 4, '③번 스프에 ②번 재료를 넣고 전분으로 농도를 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1957, 368, 5, '마지막에 계란 흰자를 풀어준 후 참기름으로 마무리 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1958, 369, 1, '채소를 손질해 사방 1.5cm 크기로 자르는데 무는 따로 삶아서 놔둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1959, 369, 2, '버터에 당근, 양파, 샐러리를 가벼운 갈색이 날 때까지 팬에 볶으면서 토마토 페이스트를 넣고 타지 않게 잘 저어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1960, 369, 3, '스톡에 채소와 페이스트 볶은 것을 잘 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1961, 369, 4, '10분정도 약한불에서 끓인 다음, 무와 양배추를 넣고 5분정도 더 약한불에서 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1962, 369, 5, '마지막에 피망을 넣고 간을 한 다음 위에 파슬리가루를 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1963, 370, 1, '쇠고기는 약간 얼린 상태에서 얇게 썰어서 접시에 돌려 담아둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1964, 370, 2, '배추는 폭 1cm,  길이 5cm로 썰어서 준비하고 당근은 가장자리를 둥글려 꽃모양, 무는 골패형으로 얇게 썰어 씻어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1965, 370, 3, '대파는 어슷하게 썰고 죽순은 삶아 얇게 썰어두며 팽이버섯은 뿌리부분을 잘라내고 표고버섯은 깨끗하게 다듬어서 적당히 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1966, 370, 4, '쑥갓은 다듬은 후 찬물에 담가 싱싱하게 해 놓고 실곤약은 10cm 길이로 썰어 삶아내어 흐르는 물에 헹궈둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1967, 370, 5, '두부는 폭 2cm, 길이 4cm로 잘라 준비된 재료를 접시에 보기 좋게 돌려담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1968, 370, 6, '냄비에 분량의 물과 다시재료를 넣고 물이 끓으면 다시마와 멸치를 건져내고 양념간을 하여 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1969, 371, 1, '끓는 물에 메밀 국수를 넣고 5~6분 동안 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1970, 371, 2, '찬물에 2~3회 잘 씻어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1971, 371, 3, '면을 그릇에 담고 채썬 김을 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1972, 371, 4, '송송 썬 대파와 와사비, 양념장을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1973, 372, 1, '쇠고기는 길이 5cm 정도로 채썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1974, 372, 2, '실파, 팽이버섯, 느타리버섯, 표고버섯 등의 채소도 길이 5cm 정도로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1975, 372, 3, '계란은 살짝 풀어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1976, 372, 4, '냄비에 다시마국물 5큰술,  간장 1과1/2큰술,  설탕 1큰술, 미림 약간을 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1977, 372, 5, '④번이 끓기 시작하면 쇠고기, 채소 순으로 넣은 후 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1978, 372, 6, '어느 정도 채소의 숨이 죽으면 풀어 놓은 계란을 넣고 잘 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1979, 373, 1, '꽃게는 솔로 문질러 씻어 등딱지를 떼고 모래주머니를 제거한다. 집게발은 떼어내고 끝을 다듬는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1980, 373, 2, '애호박은 0.5cm 두께로 반달썰고 , 무는 2cm x 2cm 크기로 얇게 나박썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1981, 373, 3, '청고추, 홍고추, 대파는 깨끗이 씻어 어슷썰고 팽이버섯은 살살 흔들어 씻어 큰 것은 길이대로 적당한 크기로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1982, 373, 4, '냄비에 물 3컵을 붓고 체에 고추장을 걸러 푼 다음 나박썬 무와 바지락과 맛살조개를 넣고 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1983, 373, 5, '무가 어느정도 익으면 준비한 꽃게, 오징어, 어슷썬 고추, 다진 마늘, 생강을 넣고 끓이다가 애호박, 대파, 고춧가루를 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1984, 373, 6, '재료가 다 익어 맛이 어우러지면 위에 떠오르는 거품을 걷고 국간장, 소금, 후춧가루로 간을 맞춰 살짝 끓여낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1985, 374, 1, '해초는 살짝 얼렸다가 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1986, 374, 2, '미역은 물에 불려 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1987, 374, 3, '오징어는 소금물에 살짝 삶아 찬물에 담근다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1988, 374, 4, '새우는 식초물에 살짝 삶아 찬물에 담근다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1989, 374, 5, '간장 2큰술, 식용유(또는 올리브오일)1/2컵, 식초 2큰술, 레몬 약간, 흰후춧가루약간, 양파1/4개, 당근 1/4개, 마늘1 작은술, 사과 1/4개를 믹서에 갈아 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1990, 374, 6, '미역과 오징어 새우 등은 그릇에 담아 먹기 직전에 소스를 뿌려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1991, 375, 1, '(감자전) 감자는 깨끗하게 씻고 껍질을 벗겨 1/2은 곱게 채썰고, 1/2은 강판에 곱게 갈아 냉수에 15분정도 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1992, 375, 2, '전분을 빼낸 ①을 고운 거즈에 싸서 꼭 짠 뒤 밀가루와 소금을 넣고 섞는다. 반죽이 되면 물을 조금씩 섞어주고 1cm 길이로 썬 부추도 같이 넣어 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1993, 375, 3, '반죽에 소금과 참기름을 조금 넣어 간을 맞춘뒤 팬에 식용유를 조금 두르고 한 숟가락씩 떠서 5cm 정도의 크기로 둥글게 만든다. 불을 약하게 하여 노릇하게 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1994, 375, 4, '(양파전) 양파는 껍질을 말끔히 벗긴 뒤 0.5cm 두께로 둥글게 썰고 쇠고기는 곱게 다져서 소금, 참기름, 후춧가루로 밑간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1995, 375, 5, '양파의 중심부를 떼어내 링 모양으로 만든 뒤 링의 안쪽에 밀가루를 살짝 바르고 양념한 쇠고기를 양파 가운데의 빈 곳에 채워 넣는다. 위로 올라오게 채우면 익으면서 더 많이 올라오게 되므로 약간 납작한 듯이 채우도록 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1996, 375, 6, '전체적으로 밀가루를 묻힌 뒤 털어내고 계란물에 담갔다가 건져내어 팬에 식용유를 두르고 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1997, 376, 1, '민물장어는 넓적하게 편다음 뼈와 내장을 제거하고 깨끗하게 씻어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1998, 376, 2, '민물장어를 2~3등분하여 석쇠에 놓고 노릇노릇하게 구워서 찜통에서 25분정도 쪄낸다. 굽고나서 한번 찌게 되면 느끼하지 않고 부드럽게 된다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (1999, 376, 3, '미리 만들어 둔 장어대리를 2~3회 발라가면서 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2000, 376, 4, '그릇에 뜨거운 밥 1공기를 담고 장어대리를 밥 위에 붓으로 발라둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2001, 376, 5, '구워둔 장어를 올리고 산초가루를 실짝 뿌린다음 초생강을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2002, 377, 1, '계란 5개는 깨어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2003, 377, 2, '장어대리소스를 바른 장어는 적당하게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2004, 377, 3, '①번 계란에 설탕, 소금, 미림, 다시물을 넣어 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2005, 377, 4, '프라이팬에 ③번 계란을 말면서 반쯤 익었을때 장어대리소스를 바른 장어를 가운데 넣고 계속 만다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2006, 377, 5, '계란을 다 말면 뜨거운 상태에서 김발에 말아 모양을 고정시키고 다 식으면 적당한 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2007, 378, 1, '새우와 꼴뚜기는 내장을 제거하여 손질하고 홍합과 모시조개는 해감을 시켜놓고 오징어는 링모양으로 잘라두고 동태살은 한입크기로 잘라놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2008, 378, 2, '당근은 작게 깍뚝썰기하고 샐러리는 1.5cm 폭으로 잘라놓고 양파는 잘게 다져놓는다. 토마토는 속의 씨를 덜어내고 깍뚝썰기해 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2009, 378, 3, '냄비에 올리브오일을 두르고 다진 마늘(2쪽)을 넣고 볶다가 냄새가 올라오면 당근과 샐러리를 넣고 볶다가  썰어놓은 토마토와 토마토페이스트를 넣어 충분히 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2010, 378, 4, '육수와 월계수잎을 넣고 조린 뒤 소금, 후춧가루로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2011, 378, 5, '올리브오일을 두른 팬에 다진 마늘(1쪽)과  다진 양파를 넣고 볶다가 꼴뚜기, 새우, 오징어를 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2012, 378, 6, '어느정도 볶아지면 토마토소스, 홍합, 모시조개를 넣고 뚜껑을 덮고 중불에서 은근히 가열하고 소금, 후춧가루로 간한 후 말린 바질이 있을 경우 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2013, 379, 1, '돼지고기를 0.8cm 두께로 잘라서 군데군데 칼집을 넣고 편 다음 후춧가루, 소금을 약간 뿌리고 밀가루를 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2014, 379, 2, '계란과 우유를 혼합하여  밀가루칠한 안심을 듬뿍 적신다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2015, 379, 3, '빵가루를 누르면서 충분히 바른 다음 살짝 털어내고  165~170℃의 온도에 약 3~4분정도 튀겨내고 기름을 빠지게 한 다음 썰어서 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2016, 379, 4, '돈까스소스, 우스터소스, 토마토케첩, 버터,  꿀, 미림, 정종, 사과, 바나나, 양파, 샐러리 1대, 마늘, 파인애플을 혼합하여 살짝 끓인다음 식혀서 겨자, 타바스코, 핫소스를 혼합하여 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2017, 379, 5, '냄비에 다시물 3큰술, 설탕, 간장, 미림을 넣고 살짝 끓인 다음 소스에 돈까스를 살짝 담근 후 밥위에 얹어 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2018, 380, 1, '양배추, 양파, 대파, 표고버섯은 약간 굵게 채썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2019, 380, 2, '피망과 죽순은 얇게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2020, 380, 3, '숙주나물은 깨끗하게 다듬어 씻어서 건져놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2021, 380, 4, '팽이버섯은 뿌리부분을 자르고 2등분한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2022, 380, 5, '우동면은 부드럽게 삶아 익혀둔 다음 햄은 굵게 채썰어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2023, 380, 6, '팬을 달구어 식용유를 두르고 먼저 햄을 넣어 볶다가 간장 약간과 다진 마늘을 넣고 살짝 볶은 후 채소를 모두 넣고 분량의 양념 재료를 넣고 재빨리 볶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2024, 381, 1, '옥수수통조림은 체에 받쳐 물기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2025, 381, 2, '오징어는 깨끗이 씻어 껍질을 벗기고, 몸통에 안쪽으로 사선으로 칼집을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2026, 381, 3, '삼겹살은 먹기 좋은 크기로 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2027, 381, 4, '실파는 4cm 길이로 썰어 놓고, 청고추, 홍고추는 송송 썰어 놓으며 양파는 채썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2028, 381, 5, '위의 재료를 모두 섞고 양념을 분량대로 넣어 버무려주고, 프라이팬에 물기 없이 볶아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2029, 382, 1, '쌀은 씻어 충분히 물에 불려 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2030, 382, 2, '은행은 볶아 껍질을 까 놓고 깐 밤과 대추는 깨끗이 씻어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2031, 382, 3, '스위트콘은 체에 밭쳐 물기를 빼놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2032, 382, 4, '느타리버섯은 깨끗이 씻어 물기를 꼭 짜놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2033, 382, 5, '냄비에 쌀과 물을 붓고 소금으로 간을 한 뒤 위의 재료를 모두 얹어 밥을 짓는다. 밥이 고슬고슬하게 지어지면  주걱으로 잘 섞어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2034, 383, 1, '두부는 사방 1cm 크기로 썰어 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2035, 383, 2, '프라이팬에 고추기름 1큰술을 넣고 다진 마늘, 생강을 넣고 볶다가 다진 돼지고기를 넣어 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2036, 383, 3, '고기가 하얗게 익으면  청고추, 홍고추 다진 것을 넣고 육수를 1컵 정도 붓고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2037, 383, 4, '소스가 끓으면 두반장 2큰술과 간장, 맛술, 설탕을 넣고 간을 맞춘 후 녹말물을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2038, 383, 5, '걸쭉해 지면 데쳐낸 두부를 넣고 살살 버무리듯 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2039, 383, 6, '마지막에 참기름을 두른 후 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2040, 384, 1, '좋은 국산콩을 잘 골라서 세심하게 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2041, 384, 2, '찬물에 콩을 불리는데 겨울철에는 약 12시간, 봄가을철에는 8시간, 여름철에는 6시간 정도만 불리면 적당하다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2042, 384, 3, '믹서에 불린콩과 물을 3 : 2정도의 비율이 되게 붓고 갈아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2043, 384, 4, '갈은 콩을 촘촘한 천으로 걸러 콩물을 빼낸다. 여기서 남은 찌꺼기가 바로 비지이고 생긴 물이 두유이다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2044, 384, 5, '거른 두유를 30분 정도  끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2045, 384, 6, '끓인 두유의 불을 끄고 한 김 나간 후 간수를 한 숟갈씩 넣어 가면서 잘 저어주면 두유가 몽글몽글 응고되며 순두부가 된다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2046, 385, 1, '우묵은 먹기 좋은 크기로 채를 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2047, 385, 2, '다시마, 다듬은 멸치로 육수를 낸 후 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2048, 385, 3, '②의 육수가 어느정도 식으면 냉장고에 넣어 시원한 국물로 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2049, 385, 4, '오이는 5cm 정도로 돌려깎기 하여 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2050, 385, 5, '분량의 재료로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2051, 385, 6, '그릇에 우묵과 오이를 넣어 차가워진 육수를 넣고 양념장을 끼얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2052, 386, 1, '냄비에 물을 넉넉하게 올리고 쇠고기를 넣고 끓인다. 이때 약간의 국간장을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2053, 386, 2, '한 번 국물이 팔팔 끓은 다음 중간불로 줄인 후 썰어둔 감자와 양파를 넣고 고춧가루, 후춧가루 등의 양념을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2054, 386, 3, '여기서 다른 한쪽에서는 국수를 준비한다. 물을 끓인 후 칼국수를 넣고 쌀짝 데치듯이 삶아');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2055, 386, 4, '옆에서 계속 끓고 있는 국물의 간을 맞춘다. 국간장이나 소금 어느 것으로 해도 좋으며 준비해둔 마늘과 버섯, 호박, 쑥갓, 파 등을 넣고 끓이면서 먹으면 더욱 맛있다.(버섯 중 느타리 버섯은 손으로 찢어 손질한다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2056, 387, 1, '쌀은 충분히 불려 가루로 빻는다.(분말기나 믹서에 갈아도 된다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2057, 387, 2, '실파는 깨끗이 다듬어 잎 부분을 잘라 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2058, 387, 3, '쇠고기는 잘게 썰어 놓고 마늘은 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2059, 387, 4, '굴은 소금물에 흔들어 씻어 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2060, 387, 5, '홍합과 조갯살은 내장을 빼내어 깨끗이 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2061, 387, 6, '쇠고기에 다진 마늘, 소금, 후춧가루, 깨소금, 참기름으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2062, 388, 1, '조기는 비늘을 긁고 배를 갈라 내장과 아가미를 꺼내고 지느러미도 잘라 낸 다음 찬물에 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2063, 388, 2, '무와 감자는 큼직하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2064, 388, 3, '양파는 한입 크기로 썰고 청고추, 홍고추, 대파는 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2065, 388, 4, '마늘 2쪽은 얇게 썰고 나머지는 다진다. 손질된 채소에 고추장, 고춧가루, 생강즙, 참기름, 소금, 후춧가루를 넣어 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2066, 388, 5, '분량의 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2067, 388, 6, '냄비 바닥에 무와 감자, 조기를 넣고 준비한 양념장의 1/2과 물 1컵을 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2068, 389, 1, '참치는 체에 밭쳐 기름을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2069, 389, 2, '오이, 당근, 양파는 잘게 다진 후 소금을 약간 뿌리고 물기를 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2070, 389, 3, '참치와 ②번 채소를 마요네즈, 설탕, 소금, 머스터드를 넣고 묻혀준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2071, 389, 4, '양상추는 한입 크기로 찢어놓고 ③번을 보기좋게 올려 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2072, 389, 5, '④번 위에 모양을 내기 위해 토마토케첩을 한방울 떨어뜨린 후 적채로 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2073, 390, 1, '돼기고기는 한입 크기로 썬 후 간장, 다진 파, 생강즙, 소금을 조금 넣고 주물러서 20분 정도 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2074, 390, 2, '피망, 표고버섯, 대파, 홍고추는 납작하게 편썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2075, 390, 3, '①에 불린 녹말과 계란 노른자를 넣어 튀김옷을 입힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2076, 390, 4, '충분히 열이 오른 튀김기름에 ③번 고기를 바삭하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2077, 390, 5, '뜨겁게 달군 프라이팬에 식용유와 고추기름을 두르고 생강즙, 마늘 등을 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2078, 390, 6, '⑤번의 향이 어느정도 나기 시작하면 간장과 술로 양념하고 나서 버섯, 피망 순서로 넣고 볶는다. 대파는 마지막에 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2079, 391, 1, '김치는 신맛이 도는 것으로 준비하여 국물이 지저분해지지 않도록 소를 털어내고 송송썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2080, 391, 2, '홍합살은 소금물에 살살 흔들며 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2081, 391, 3, '대파, 홍고추는 어슷하게 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2082, 391, 4, '멸치는 장국용으로 준비하여 국물을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2083, 391, 5, '④의 멸치 국물에 김치를 넣고 시원한 맛이 우러나도록 팔팔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2084, 391, 6, '⑤의 국물이 어느정도 끓으면 홍합을 넣고 한소끔 다시 한번 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2085, 392, 1, '가지는 어슷하게 썰어서 약간의 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2086, 392, 2, '돼지고기는 곱게 다져서 간장, 설탕, 후춧가루, 맛술, 깨소금, 참기름으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2087, 392, 3, '①의 가지에 물기가 생기면 깨끗한 거즈로 닦아낸 후 밀가루를 살짝 묻힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2088, 392, 4, '③의 가지에 ②의 돼지고기를 얇게 바르고 다른 가지로 샌드위치처럼 겹쳐준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2089, 392, 5, '④의 가지에 밀가루, 계란, 빵가루 순으로 묻힌 후 180℃ 온도에서 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2090, 392, 6, '머스터드 1/2큰술 , 마요네즈 2큰술, 설탕 1/4큰술, 소금 약간으로 드레싱을 만들어 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2091, 393, 1, '통조림 골뱅이는 끓는물에 살짝만 데쳐내어 깔끔하게 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2092, 393, 2, '골뱅이가 식으면 얇게 저며썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2093, 393, 3, '표고버섯, 홍피망, 청피망, 양파는 얇고 네모나게 썰고 마늘과 파는 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2094, 393, 4, '팬에 기름을 두르고 표고버섯, 양파를 같이 볶다가 청피망, 홍피망을 넣고 같이 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2095, 393, 5, '④의 채소들이 볶아지면 물 1/4컵을 붓고 끓이다가 진간장으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2096, 393, 6, '데친 골뱅이, 다진 파, 다진 마늘, 후춧가루, 깨소금, 참기름을 넣고 젓다가 녹말물을 조금씩 넣어가며 걸쭉하게 농도를 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2097, 394, 1, '닭가슴살은 끓는물에 삶은 뒤 손으로 먹기좋게 찢어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2098, 394, 2, '해파리는 소금기를 씻어내고 끓는물에 삶아서 찬물에 식힌 후 설탕 2큰술, 소금 약간, 식초 2큰술, 물 8큰술을 섞어  담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2099, 394, 3, '홍고추, 청고추는 잘게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2100, 394, 4, '그릇에 물 3큰술, 식초 4큰술, 참기름 1작은술, 간장 1작은술, 소금 약간, 설탕 2큰술, 꿀 2큰술, 홍고추, 청고추를 넣고 잘 섞어 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2101, 394, 5, '오이와 레몬은 편으로 얇게 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2102, 394, 6, '④의 소스에 찢어놓은 닭가슴살과 해파리를 묻힌 후 접시에 오이를 깔고 그 위에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2103, 395, 1, '어묵은 적당한 크기로 썬 다음 끓는물에 살짝 데쳐낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2104, 395, 2, '마늘은 다지고 김치, 채소는 한입 크기로 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2105, 395, 3, '냄비에 기름을 두르고 약불에서 마늘을 볶다가 식용유 1큰술, 고춧가루 1/2큰술을 넣어 타지 않게 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2106, 395, 4, '③에 데친 어묵, 김치, 채소들을 넣어 볶아주다가 간장 1/4큰술, 설탕 1/2큰술, 참기름 약간, 깨소금 약간 등을 넣어 양념을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2107, 395, 5, '④가 다 볶아지면 한김 식힌 후 그릇에 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2108, 396, 1, '오징어는 내장을 빼고 껍질을 벗겨서 칼집을 낸다. (어슷하게, 촘촘하게)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2109, 396, 2, '두부는 사방 3cm 크기로 도툼하게 썰고 호박은 반달 모양으로 썬다. 양파는 굵게 채썬다. 대파, 홍고추, 청고추는 어슷하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2110, 396, 3, '모시조개는 소금물에 담가 해감을 토하게 한 후 끓는 물 3컵에 소금을 약간 넣고 데쳐서 조개는 건지고 국물은 맑게 걸러낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2111, 396, 4, '고춧가루에 다진 마늘과 다진 파, 간장, 소금, 후춧가루, 맛술을 넣고 조갯국물을 약간 부어 잘 섞어 양념을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2112, 396, 5, '냄비에 김치를 넣어 볶다가 조갯국물을 부어서 끓이다가 미리 만들어둔 양념을 풀고 두부, 호박을 넣고 끓인 후 모시조개, 오징어, 대파, 홍고추, 청고추를 넣고 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2113, 397, 1, '떡은 미지근한 물에 살짝 불려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2114, 397, 2, '브로콜리는 뜨거운 물에 식초, 소금을 약간 넣고 살짝 데쳐 찬물에 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2115, 397, 3, '쇠고기는 곱게 다져서 준비한 후 기본 양념을 하여 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2116, 397, 4, '프라이팬에 참기름을 두르고 쇠고기, 마늘을 볶다가 고추장 2큰술, 고춧가루 1/2작은술, 설탕 1큰술, 간장 1/2큰술, 후춧가루 약간, 깨소금 약간, 물 1/2컵을 넣고 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2117, 397, 5, '①의 살짝 불린 떡과 브로콜리를 꼬치에 끼워 ④의 양념한 소스를 발라가며 타지 않게 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2118, 397, 6, '마지막에 깨소금을 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2119, 398, 1, '냉동된 훈제연어를 자연해동 시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2120, 398, 2, '바게트빵은 기름 없는 후라이팬에 살짝 토스트한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2121, 398, 3, '②의 바게트 빵 위에 머스터드를 살짝 바르고 상추 등을 깔아 훈제연어를 보기 좋게 올려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2122, 398, 4, '바게트 빵 위에 땅콩버터를 바르고 상추 등을 깔아 훈제연어를 보기 좋게 올려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2123, 398, 5, '오픈샌드위치에 체리알, 사워크림으로 장식한다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2124, 399, 1, '김치는 속을 털어내고 4cm 길이 정도로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2125, 399, 2, '참치는 체에 밭쳐 기름기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2126, 399, 3, '양파, 대파, 홍고추, 청고추는 적당하게 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2127, 399, 4, '두부는 0.5cm 두께로 져며낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2128, 399, 5, '냄비에 참기름을 두르고 김치, 양파를 볶다가 물을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2129, 399, 6, '⑤가 한소끔 끓으면 참치를 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2130, 400, 1, '오징어는 배를 갈라 내장과 다리를 떼어낸 뒤, 겉면의 껍질을 위에서 아래로 벗긴다. 미끌거려서 잘 벗길 수 없으면 굵은 소금을 뿌리거나 종이 타월로 껍질을 붙잡고 벗기면 손쉽다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2131, 400, 2, '오징어를 깨끗이 씻은 뒤, 안쪽에 사선으로 교차되게 칼집을 촘촘히 넣은 후 폭 1cm, 길이 4cm 크기의 길쭉한 네모로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2132, 400, 3, '양파, 양배추, 당근은 굵게 채썰고 청고추, 홍고추는 어슷썰어 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2133, 400, 4, '볼에 손질한 오징어와 채소를 담은 뒤, 매운 볶음 양념장을 넣어 간이 고루 배도록 주물러 10분 정도 재운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2134, 400, 5, '팬을 달구어 기름을 두른 뒤, 양념한 오징어와 채소를 한꺼번에 넣어 센불에서 재빨리 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2135, 400, 6, '오징어가 솔방울 모양으로 오그라들면서 익으면 꺼낸 뒤, 그릇에 담고 통깨를 넉넉히 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2136, 401, 1, '양파, 홍고추, 청고추는 얇게 채를 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2137, 401, 2, '돼지고기는 얇게 채를 썰어 마늘, 생강, 굴소스, 깨소금을 넣고 양념해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2138, 401, 3, '부추는 4cm 길이로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2139, 401, 4, '재료가 준비되면 프라이팬에 기름을 두르고 양념한 돼지고기를 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2140, 401, 5, '돼지고기를 볶다가 양파, 고추를 넣고 소금을 뿌려 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2141, 401, 6, '채소가 어느 정도 숨이 죽으면 준비한 부추를 넣어 살짝 볶은 후 참기름 한방울을 넣어 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2142, 402, 1, '쇠고기는 사태로 준비하여 찬물에 담가 핏물을 뺀 후 냄비에 대파, 마늘 등을 넣어 푹 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2143, 402, 2, '계란은 완숙으로 익힌 후 식으면 껍질을 벗겨 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2144, 402, 3, '꽃상추, 치커리, 파프리카, 깻잎 등은 굵게 채썰어 찬물에 담가 싱싱하게 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2145, 402, 4, '고추장1큰술, 간장 1/2큰술, 식초 1큰술, 설탕 1큰술, 깨소금 약간, 간양파 1큰술, 후춧가루 약간, 다진 마늘, 갠겨자 1/2큰술을 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2146, 402, 5, '라면은 꼬들하게 삶아서 찬물에 비벼 씻은 후 체에 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2147, 402, 6, '접시에 준비해 놓은 채소와 고기 등을 보기 좋게 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2148, 403, 1, '밀가루에 소금, 치자, 계란을 넣고 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2149, 403, 2, '다진 소고기에 다진 마늘, 양파, 피망 등을 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2150, 403, 3, '②를 프라이팬에 볶으면서 케첩, 머스터드, 소금, 후춧가루로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2151, 403, 4, '반죽한 밀가루를 8cm 정도의 원형으로 만들어 국자에 담아 모양을 만들어 바삭하게 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2152, 403, 5, '튀긴 반죽과 ③을 먹음직스럽게 담아 마무리 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2153, 404, 1, '찬물에 다시마를 넣고 잠시 끓이다가 불을 끄고 가쓰오브시를 넣고 10분 후에 걸러 약간의 간장, 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2154, 404, 2, '새우는 내장을 제거하고 맛조개는 소금물에 해감시키고 낙지 또는 오징어를 손질하여 소금물에 한번 씻어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2155, 404, 3, '당근은 꽃모양으로 자르고 살짝 데친 곤약은 한번 꼬아주고 미나리는 5cm 길이로 썰고 표고버섯은 굵지 않게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2156, 404, 4, '팽이버섯은 홍고추의 링 안에 꽂아 모양을 만들고 끓는 물에 새우, 맛조개, 낙지 등을 살짝 데친 후 바로 찬물에 담가 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2157, 404, 5, '접시에 채소와 해물을 보기 좋게 담고 소스를 만들어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2158, 405, 1, '간장을 비롯한 분량의 부재료를 넣고 끓여서 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2159, 405, 2, '손질한 꽃게를 그릇에 담고 간장으로 만든 양념을 부어 무거운 것으로 눌러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2160, 405, 3, '3~4일 후 간장양념을 따라내 다시 끓이는데, 이때 물 1/2컵을 같이 넣어 끓인 후 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2161, 405, 4, '꽃게는 아래, 위로 바꾸어 담은 다음 다시 끓여 식힌 간장양념을 부어 무거운 것으로 눌러놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2162, 405, 5, '양념에 담았던 꽃게의 게딱지를 떼내고 모래주머니와 수염을 뗀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2163, 405, 6, '게딱지의 장은 한 곳으로 모으고 몸통은 2~4등분으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2164, 406, 1, '녹두는 깨끗이 씻어 일어 건진 다음 물을 붓고 푹 삶아 체에 걸러 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2165, 406, 2, '쌀은 깨끗이 씻어 물에 불려 건져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2166, 406, 3, '냄비에 녹두의 윗 물만 덜어 내어 붓고 쌀을 끓여 쌀알이 어느 정도 퍼지게 되면 녹두 앙금을 넣어 섞으면서 다시 한 번 끓인다. 녹두 앙금은 나중에 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2167, 406, 4, '쌀알이 잘 퍼지면 그릇에 담아 대추채를 고명으로 띄워내고, 기호에 따라 소금이나 설탕을 넣어서 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2168, 407, 1, '돼지고기를 불고기감으로 얇게 썬 다음 먹기 좋도록 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2169, 407, 2, '손질한 돼지고기를 30분 정도 망에 담아 미림으로 냄새를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2170, 407, 3, '분량대로 양념을 섞어 기본 양념장을 만들어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2171, 407, 4, '일단 양념장을 조금씩 나누어 고기 앞 뒤로 골고루 묻힌 다음 조물조물 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2172, 407, 5, '팬을 달군 다음 식용유를 두른다. 팬에 어느정도 달구어 지면 고기를 맛있게 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2173, 408, 1, '단호박을 잘 씻어서 반으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2174, 408, 2, '안의 씨와 실 같은것을 다 긁어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2175, 408, 3, '②의 단호박을 적당한 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2176, 408, 4, '오븐그릇에 물을 깔고 껍질쪽이 위로 가도록 엎어놓고 180℃의 오븐에 40분 간 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2177, 408, 5, '④를 꺼내서 뒤집어 황설탕 1큰술, 버터 약간, 소금 약간, 후춧가루도 약간 패인곳에 넣어서 10분 정도 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2178, 409, 1, '모든 재료는 납작하게 편썰고, 마른 고추는 씨를 털어내고 어슷하게 썰고 마늘은 얇게 저미고 대파는 줄기만 준비해 반으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2179, 409, 2, '표고버섯은 포 뜨듯이 넓적하게 저며 썰고 피망은 버섯과 비슷한 크기로 썰고 양파는 얄팍하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2180, 409, 3, '한입 크기로 썬 닭고기에 간장, 파, 생강즙, 소금을 조금 넣고 주물러 20분 정도 재워둔 후 계란을 1/2개 정도 풀어 넣고 녹말가루 3큰술, 튀김가루 1큰술을 넣어 반죽한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2181, 409, 4, '고기를 한 움큼씩 잡고 충분히 열이 오른 튀김 기름에 한 두개씩 떨어뜨려 노릇하게 두번 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2182, 409, 5, '뜨겁게 달군 프라이팬에 식용유와 고춧기름을 두르고 마른 고추, 생강, 파, 마늘을 볶고 마른 고추가 약간 거무스름한 색깔이 날 때까지 볶아 기름에 매콤한 맛이 배도록 라조기의 맛과 향을 살린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2183, 409, 6, '매운 맛이 나면 먼저 간장과 술로 양념하고 표고버섯, 양파, 피망의 순서로 넣고 볶는다.(항상 맛이 들어야 하는 버섯을 먼저 넣고 푸른 채소는 마지막에 넣도록 한다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2184, 410, 1, '만두피를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2185, 410, 2, '양파는 깨끗이 손질하여 얇게 썰어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2186, 410, 3, '냄비에 설탕, 물, 계핏가루를 넣고 끓으면 썰어 놓은 양파를 넣고 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2187, 410, 4, '만두피를 한장 놓고 가장자리에 물을 바른 다음 가운데 ③을 소복히 놓고 만두피를 마주 덮어 꼭꼭 붙여 포크로 가장자리를 다시 눌러준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2188, 410, 5, '식용유가 160℃로 끓여지면 ④를 넣어 노릇하게 튀긴다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2189, 411, 1, '냄비에 물 5컵을 붓고 닭고기, 마늘, 파, 생강을 넣어서 40분정도 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2190, 411, 2, '닭고기가 익으면 체에 면보를 깔고 국물을 걸러 기름기를 제거한 다음 국간장, 소금, 후춧가루로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2191, 411, 3, '①의 닭살을 찢어서 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2192, 411, 4, '밀가루에 소금을 약간 넣고 물을 부어 반죽을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2193, 411, 5, '미역은 물에 불려 깨끗이 손질한 다음 물기를 빼고 2~3번 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2194, 411, 6, '걸러둔 닭국물을 끓이다가 미역을 넣고 국간장으로 간을 맞춘 다음 양념한 닭고기를 넣고 끓으면 밀가루 반죽을 떼어내 넣고 끓인다. 수제비가 위로 뜨면서 끓어 오르면 불을 끈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2195, 412, 1, '토막 낸 생대구를 깨끗한 물에 한번 씻은 후 체에 밭쳐 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2196, 412, 2, '마늘은 다지고 대파, 홍고추, 청고추는 어슷하게 썰고 두부는 적당한 크기로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2197, 412, 3, '냄비에 물 3컵을 담아 다시마를 넣고 살짝 한번 끓인 후 가쓰오브시를 넣어 10분 후에 걸른 후 국물을 우려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2198, 412, 4, '③의 국물이 끓어오르기 시작하면 ①의 손질한 대구를 넣어 한소끔 끓인 후 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2199, 412, 5, '대구의 시원한 맛이 어느정도 우러나오면 두부, 마늘, 홍고추, 청고추, 팽이버섯, 미나리 등을 넣어 마무리 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2200, 413, 1, '손질한 채소는 깨끗이 씻어 곱게 다져 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2201, 413, 2, '브로콜리는 소금물에 넣고 데친 후 찬물에 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2202, 413, 3, '베이컨으로 말아놓은 ⑤개의 브로콜리를 작은 꼬치로 고정시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2203, 413, 4, '팬에 기름을 두르고 노릇노릇 지진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2204, 413, 5, '브로콜리베이컨말이에 케첩이나 머스타드를 뿌려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2205, 414, 1, '쇠고기는 불고기용으로 손질하여 간장, 맛술, 설탕, 참기름, 깨소금, 다진 파, 다진 마늘로 양념하여 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2206, 414, 2, '양파, 대파, 표고버섯은 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2207, 414, 3, '기름두른 팬에 양념한 쇠고기를 볶다가 양파, 대파, 표고버섯을 같이 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2208, 414, 4, '프라이팬에 밥을 넣고 소금, 깨소금, 참기름을 약간 넣고 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2209, 414, 5, '접시에 밥과 볶은 불고기를 함께 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2210, 415, 1, '고등어는 싱싱한것을 골라서 머리를 자르고 내장을 빼낸다음 포를 뜬후 살만 먹기 좋은 크기로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2211, 415, 2, '양파, 홍/청피망은 채를 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2212, 415, 3, '마늘은 곱게 다지고, 실파는 5cm 길이로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2213, 415, 4, '먹기 좋은 크기로 썰어둔 고등어에 맛술, 후추가루, 생강즙을 넣어 밑간하여 비린맛을 없앤다.(10분정도)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2214, 415, 5, '4번 고등어에 녹말가루를 묻힌 후 털어낸후 넉넉하게 달군 기름에 바삭하게 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2215, 415, 6, '팬에 식용유를 두르고 양파, 피망을 볶다가 육수1/2컵, 케첩4큰술, 간장1/2큰술, 설탕11/2큰술을 넣어 나무주걱으로 잘 저어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2216, 416, 1, '굴은 소금물에 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2217, 416, 2, '마늘, 오이, 배는 껍질을 깐다음 편으로 썬다. 청고추, 홍고추는 어슷하게 썰어 씨를 털어내고 실파는 3cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2218, 416, 3, 'TIP을 참고해 양념장을 만들어 굴과 준비해놓은 채소를 섞어 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2219, 416, 4, '접시에 배추속대를 놓고 굴무침을 먹음직스럽게 담은 다음 파슬리로 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2220, 417, 1, '대구살을 전으로 부치기 쉽도록 포를 뜬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2221, 417, 2, '①의 생선의 물기를 제거한 후 소금, 후춧가루로 밑간 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2222, 417, 3, '쑥갓은 어린잎으로 준비하여 물에 담가둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2223, 417, 4, '달걀을 흰자, 노른자 잘 섞어 소금으로 약하게 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2224, 417, 5, '적당하게 포를 뜬 대구살에 밀가루-계란 순으로 옷을 입힌 후 후라이팬에 노릇하게 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2225, 418, 1, '연한 6쪽 통마늘을 골라 마른 껍질 2컵 정도를 벗긴 후 뿌리는 잘라내고 줄기부분은 1cm 정도 남기고 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2226, 418, 2, '깨끗이 씻어 건져 물기를 완전히 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2227, 418, 3, '항아리에 마늘을 차곡차곡 담고 마늘이 푹 잠기도록 식초를 부은 후 뚜껑을 꼭 덮어 1주일 정도 시원한 곳에 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2228, 418, 4, '냄비에 절임간장, 설탕, 육수를 넣어 끓여 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2229, 418, 5, '식초물을 따라 버리고 ④의 간장물을 넣고 뚜껑을 꼭 닫는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2230, 418, 6, '3~4일 지난 후 간장물만 따라내어 끓여 식힌 후 다시 붓는다. 두 차례 반복한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2231, 419, 1, '북어는 껍질이 없는 것으로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2232, 419, 2, '물에 적셔서 부드럽게 불린 뒤 물기를 꼭 짠다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2233, 419, 3, '불린 북어를 길이 3~4cm 정도가 되도록 손으로 찢어 북어 양념을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2234, 419, 4, '냄비에 멸치 다시마 육수로 국물을 우려낸 다음 끓기 시작하면 북어와 깨끗이 씻은 콩나물을 넣어 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2235, 419, 5, '④가 충분이 끓어 시원한 국물이 우러나오면 다진 마늘, 송송 썬 고추를 넣고 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2236, 419, 6, '그릇에 밥을 담고 ⑤를 부어 먹음직스럽게 담아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2237, 420, 1, '어린 봄동을 깨끗이 손질해 먹기 좋게 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2238, 420, 2, '영양부추는 이물질을 제거하고 깨끗하게 씻어 적당한 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2239, 420, 3, '분량의 양념 재료를 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2240, 420, 4, '준비한 봄동과 영양부추는 체에 밭쳐 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2241, 420, 5, '양념장에 봄동과 영양부추를 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2242, 421, 1, '양파는 링이 작은것으로 준비하여 0.5cm 두께로 모양을 살려 썬 다음 겉쪽에 2~3겹을 남기고 속을 빼내어 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2243, 421, 2, '두부는 물기를 꼭 짜서 으깬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2244, 421, 3, '다진 쇠고기, 으깬 두부, 다진 파, 마늘 약간, 소금, 참기름, 깨소금 등으로 양파 안에 들어갈 속을 만든다.(속을 빼낸 양파도 같이 다져 넣어준다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2245, 421, 4, '①의 안쪽에 밀가루를 뿌린 다음 ③의 속을 밀리지 않게 잘 채워넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2246, 421, 5, '채워 넣은 양파는 밀가루, 계란 순으로 묻혀 기름을 두른팬에 노릇하게 지져낸다. 홍고추 등으로 포인트를 주면 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2247, 422, 1, '쇠고기, 양파는 다져서 준비한다.(양파는 다진 후 물기를 제거한다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2248, 422, 2, '①의 재료에 불고기 양념(간장 1/2큰술, 설탕 1/4큰술, 참기름, 깨소금, 다진 마늘, 다진 파)을 약하게 하여 고슬고슬하게 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2249, 422, 3, '뜨거운 밥을 준비하여 ②의 재료와 깨소금등을 넣어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2250, 422, 4, '상추는 한 입 크기 정도로 손으로 찢어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2251, 422, 5, '밥이 어느정도 식으면 한 입 크기로 찢어놓은 상추에 밥을 동글게 빚어 그 위에 올려놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2252, 422, 6, '먹기 직전에 고추장을 약간 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2253, 423, 1, '우엉은 껍질 쪽에 특유의 맛과 향이 있으므로 칼등으로 겉 껍질만 살살 긁어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2254, 423, 2, '껍질을 벗긴 우엉은 길이 10cm, 굵기 0.5cm로 썬 다음 끓는 물에 삶아낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2255, 423, 3, '돼지고기는 얇게 포를 뜨듯이 썰어 도마위에 펼쳐놓고 ②를 올려 돌돌 만뒤 풀리지 않게 꼬치로 고정한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2256, 423, 4, '간장 3큰술, 다시물 1/2컵, 고운 고춧가루 1/2큰술, 정종 2큰술, 설탕 2큰술, 생강즙 약간으로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2257, 423, 5, '냄비에 ③의 우엉말이를 담고 ④의 조림양념을 부어 골고루 섞이도록 뒤적인 다음 약한 불에서 서서히 조린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2258, 423, 6, '⑤가 어느정도 조려지면 알맞은 길이로 잘라 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2259, 424, 1, '쌈다시마를 준비하여 물에 두 번 이상 씻어 소금기를 뺀 뒤 찬물에 담가 충분히 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2260, 424, 2, '게맛살, 피망, 파프리카, 오이를 5cm 길이로 잘라 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2261, 424, 3, '오징어는 끓는 물에 데쳐서 체에 밭쳐 물기를 뺀 뒤 채소와 같은 길이로 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2262, 424, 4, '쌈다시마는 물기를 없앤 뒤 김발 위에 놓고 ②와 ③을 올려 김밥 말듯이 말아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2263, 424, 5, '먹기 좋게 썰어 접시에 담고 초고추장을 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2264, 424, 6, '초고추장을 준비하여 곁들여 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2265, 425, 1, '무말랭이는 물에 불려 하룻밤 그대로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2266, 425, 2, '파, 마늘은 다져서 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2267, 425, 3, '간장 1큰술, 고추장 1큰술, 다진 마늘 1큰술, 깨소금 약간, 멸치액젓 1큰술, 고춧가루 1과1/2큰술, 설탕 2큰술로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2268, 425, 4, '물에 불린 무말랭이의 물기를 꼭 짜고 양념장에 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2269, 425, 5, '먹기 직전에 깨소금을 뿌려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2270, 426, 1, '쇠고기는 불고기감으로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2271, 426, 2, '낙지는 반으로 갈라 내장을 빼고 먹물주머니를 제거한다. 굵은 소금을 뿌리고 손으로 박박 문질러 씻는다. 거품이 일지 않고 꼬들꼬들해지면 찬물에 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2272, 426, 3, '대파, 호박은 송송 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2273, 426, 4, '당근은 납작하게 썰고 홍고추는 꼭지를 떼고 씨를 털어낸 후 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2274, 426, 5, '양파는 반을 자른 후 굵게 채썰고 미나리는 적당한 길이로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2275, 426, 6, '준비한 고기양념 재료를 섞어 간장 2큰술, 설탕 1큰술, 다진 파 1큰술, 다진 마늘 1/2큰술, 조미술 1/2큰술, 참기름 1/2큰술, 깨소금 1/2큰술, 후춧가루 약간으로 양념장을 만들어 쇠고기를 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2276, 427, 1, '애호박은 반달썰기를 하고 다진 파, 마늘, 소금, 깨소금으로 양념하여 기름에 볶은 다음 참기름을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2277, 427, 2, '취나물은 손질하여 등분한 뒤 다진 파, 마늘, 깨소금, 간장을 넣어 무친 다음 팬에 기름을 넉넉히 넣고 부드럽게 볶은 후 참기름을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2278, 427, 3, '시금치는 다듬어 씻은 후 끓는 물에 살짝 데친 후 등분하여 다진 파, 마늘, 간장, 소금, 깨소금, 참기름을 넣어 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2279, 427, 4, '도라지는 가늘게 찢어 끓는 물에 삶아 기름에 볶으면서 다진 파, 깨소금, 소금, 참기름으로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2280, 427, 5, '고사리는 다듬어 등분하여 다진 파, 마늘, 간장, 깨소금, 후춧가루, 참기름으로 양념하고 기름에 볶다가 물을 약간 넣어 부드럽게 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2281, 427, 6, '상추는 채썰어 찬물에 담가두어 싱싱하게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2282, 428, 1, '고등어를 머리, 꼬리와 내장을 제거하고 손질한 후, 포를 떠서 뼈를 발라낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2283, 428, 2, '양념장을 만들어 고등어를 12시간 재운 후 석쇠에 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2284, 428, 3, '구울 때 양념장을 발라 가면서 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2285, 428, 4, '센 불에 구우면 생선이 완전히 익지 않으므로 중, 약불에 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2286, 429, 1, '고등어를 깨끗이 손질하여 4~5cm 정도 알맞은 크기로 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2287, 429, 2, '채소는 4~5cm, 두께 2.5cm 정도로 얇게 썰어 보기 좋게 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2288, 429, 3, '그 위에 생선을 얹고 양념장을 뿌려 중, 약불에 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2289, 429, 4, '끓인 후, 홍고추, 청고추와 팽이버섯으로 고명한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2290, 430, 1, '갈치를 손질한 후, 5cm정도의 크기로 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2291, 430, 2, '준비한 갈치에 소금과 후춧가루를 뿌려 밀가루를 묻혀 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2292, 430, 3, '접시에 구운 갈치를 놓고 레몬 한 조작을 곁들여 내면 좋다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2293, 431, 1, '갈치를 깨끗이 손질하여 알맞은 크기로 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2294, 431, 2, '채소는 길이 4~5cm, 두께 2.5cm 정도로 얇게 썰어 보기 좋게 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2295, 431, 3, '그 위에 생선을 얹고 양념장을 뿌려 중, 약불에 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2296, 431, 4, '끓인 후, 홍고추와 청고추로 장식한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2297, 432, 1, '새우를 삶은 후, 껍질을 제거하고 포를 떠 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2298, 432, 2, '겨자소스는 겨자, 식초, 설탕을 넣고 잘 섞은 후 소금으로 간하여 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2299, 432, 3, '나머지 채소는 채를 썰어 접시 위에 가지런히 놓고 그 위에 새우를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2300, 432, 4, '곁들인 겨자소스를 뿌린 뒤 버무려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2301, 433, 1, '프라이팬에 식용유를 두르고 멸치를 볶다가 간장, 설탕을 넣고 멸치 조림을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2302, 433, 2, '밥에 ①의 멸치조림을 넣고 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2303, 433, 3, '②의 잘 버무린 밥을 둥글게 만들어 손으로 꼭꼭 누르면서 감싸쥔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2304, 433, 4, '③의 동그랗게 만든 밥에 치즈를 감싼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2305, 434, 1, '햄, 양파, 청피망, 홍피망은 가늘게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2306, 434, 2, '냄비에 버터를 녹이고, 양파와 햄, 청피망, 홍피망을 볶아준 후 밥과 육수를 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2307, 434, 3, '②의 재료에 우유를 넣어 끓인 후 소금, 후춧가루로 맛을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2308, 434, 4, '③에 치즈를 떼어 넣고 다시 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2309, 434, 5, '충분히 끓으면 파슬리가루를 위에 솔솔 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2310, 435, 1, '수삼은 잔뿌리를 제거하고 어슷하게 저민다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2311, 435, 2, '양상추는 한 입 크기로 뜯고 래디쉬는 얇게 썰고 적채는 곱게 채 썰어 치커리와 함께 찬물에 담갔다가 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2312, 435, 3, '분량의 크림치즈와 마요네즈를 섞어 드레싱을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2313, 435, 4, '③의 드레싱에 ①과 ②의 재료를 넣고 가볍게 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2314, 436, 1, '청고추, 홍고추는 다지고 양파는 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2315, 436, 2, '버터에 다진 마늘을 넣고 볶다가 청주를 넣고 완전히 졸인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2316, 436, 3, '②에 닭육수를 넣어서 졸이다가 ①의 청고추, 홍고추, 양파를 넣고 볶고 체에 걸려둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2317, 436, 4, '프라이팬에 버터를 넣고 우동면을 볶다가 ③의 소스를 넣고 볶아주다가 굴소스, 간장, 맛술을 넣고 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2318, 436, 5, '④를 접시에 담고 가니쉬로 생 숙주를 올려서 마무리 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2319, 437, 1, '분량의 쇠고기육수 재료를 넣고 푹 삶아 육수를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2320, 437, 2, '육수에 피시소스를 넣고 모자라는 간은 소금으로 맞춰 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2321, 437, 3, '숙주는 꼬리를 떼고 다듬어 씻어 건지고 샤브샤브용 쇠고기는 삶은 후 한 입 크기로 썰어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2322, 437, 4, '대파는 얇게 어슷썰고 고추는 송송 썰어 속씨를 털고 고수는 잘게 뜯는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2323, 437, 5, '쌀국수는 끓는 물에 5분 정도 부드럽게 삶아 물기를 빼고 그릇에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2324, 437, 6, '쌀국수에 한 입 크기로 자른 고기와 숙주를 얹고 끓여둔 뜨거운 육수를 넉넉히 부어서 송송 썬 고추와 고수를  뿌려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2325, 438, 1, '깻잎을 깨끗이 씻어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2326, 438, 2, '청고추와 홍고추는 잘게 다져둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2327, 438, 3, '간장, 물, 조청, 청고추, 홍고추, 들기름으로 조림장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2328, 438, 4, '냄비에 깻잎과 조림장을 켜켜이 쌓고 뚜껑을 덮어 한번 살짝 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2329, 439, 1, '얼갈이와 열무는 씻은 후 5cm 정도로 잘라 소금에 20~30분 정도 절여둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2330, 439, 2, '미나리, 쪽파는 5cm 정도로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2331, 439, 3, '홍고추, 청고추는 2.5cm 정도로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2332, 439, 4, '쌀가루는 물 1컵에 밀가루 2큰술 정도의 농도로 죽을 쑤어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2333, 439, 5, '생수나 끓여 식힌 물에 소금을 넣어 소금물(물50 :소금1)을 만든 다음 삶아서 채에 내려놓은 감자와 찹쌀가루 죽을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2334, 439, 6, '마늘, 생강, 무우, 양파, 배는 믹서해서 소창에 짜 소금물에 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2335, 440, 1, '벌레 먹었거나 썩은 것이 없이 잘 여문 메주콩을 준비해 물에 담가 충분히 불리고 김이 오른 찜통에 콩을 넣고 3∼4시간 동안 손으로 비벼보아 쉽게 뭉그러지는 정도가 될 때까지 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2336, 440, 2, '①이 식기 전에 절구에 찧거나 믹서에 넣고 굵게 갈아 벽돌 모양으로 만들어 실온에서 3일 정도 말려서 메주 겉면의 수분을 없앤다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2337, 440, 3, '겉면이 완전히 굳으면 상자에 짚을 깔고 메주가 서로 붙지 않게 담고, 짚으로 잘 덮어서 따뜻한 실온에서 2주 정도 둔다. 메주에 곰팡이가 피면 짚을 열십자로 묶어서 햇빛에 쬐어 바싹 말린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2338, 440, 4, '분량의 물에 소금을 풀어 하루 정도 두어 이물질을 가라앉힌 다음 고운 체에 다시 걸러둔다. 소금물의 농도는 18도 정도로 계란을 넣었을 때 2/3 정도 떠오르면 된다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2339, 440, 5, '항아리는 끓는 물을 부어 깨끗이 소독하여 씻어내고 말린 메주를 차곡차곡 쌓은 후 ④의 소금물을 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2340, 440, 6, '물 위로 메주가 1cm 정도 떠오르면 메주의 겉면에 소금을 한줌씩 뿌린 다음 숯, 고추를 띄우고 뚜껑을 꼭 닫아 3일간 두었다가 열어 햇볕을 쬔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2341, 441, 1, '간장을 떠내고 남은 메주를 으깨어 소금 1Kg을 넣고 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2342, 441, 2, '씻어 말려둔 항아리 바닥에 소금을 약간 뿌린 다음 으깨어놓은 메주를 눌러 담고 위가 하얗게 덮이도록 웃소금을  골고루 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2343, 441, 3, '얇은 헝겊으로 밀봉하여 낮에는 뚜껑을 열어 햇볕을 쪼이고, 밤에는 뚜껑을 닫아서 한 달 정도 두면 숙성하여 맛이 든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2344, 442, 1, '메주콩을 준비하여 상한 것을 골라 낸 다음 잘 씻어 삶고 멥쌀은 하룻밤 물에 불렸다가 건져 가루로 빻아서 시루에 쪄낸 후 한데 합쳐 절구에 찧는다.(메주 만드는 준비 과정)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2345, 442, 2, '①을 주먹 크기로 뭉친 다음 동글납작하게 빚어 한가운데 구멍을 내서 이틀 정도 말린다. 시루를 준비하여 바닥에 짚을 깐 다음, 말린 메주를 놓고 다시 짚으로 덮는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2346, 442, 3, '②를 따뜻한 곳에 놓은 뒤 담요로 덮어 일주일 동안 띄운다. 띄운 메주는 꺼내어 바람에 이틀 정도 말린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2347, 442, 4, '메주를 잘게 부수고 한번 더 말려 빻아 고운 가루로 만들고 통풍이 잘되는 곳에 종이를 깔고 펼쳐놓아 냄새가 날아가도록 하면서 밤 이슬을 이틀 정도 맞게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2348, 442, 5, '찹쌀가루를 익반죽하여 둥글게 빚어 가운데 구멍을 뚫은 다음 끓는 물에 삶아 건져 물은 버리지 않고 따로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2349, 442, 6, '삶아서 건진 찹쌀떡을 양푼에 담아 뜨거울 때 되직하게 거품기에 감기도록 쳐서 푼다. 조금 되다 싶으면 떡 삶은 물을 부어 된 풀 정도로 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2350, 443, 1, '고구마는 굵게 채로 썰어 찬물에 담가 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2351, 443, 2, '고구마는 140~150℃에서 튀겨낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2352, 443, 3, '호박씨를 팬에 살짝 볶아 굵게 다지거나 반만 갈라 체에 쳐 지저분한 가루를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2353, 443, 4, '시럽을 젓지말고 끓여 만들어 놓고 튀긴 고구마와 건포도, 호박씨를 넣고 나무주걱으로 잘 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2354, 443, 5, '틀에 넣어 모양을 잡은 다음 알맞은 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2355, 444, 1, '근대는 물로 깨끗이 씻어 적당한 크기로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2356, 444, 2, '쇠고기는 가늘게 채썰어 된장, 후춧가루에 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2357, 444, 3, '냄비에 멸치와 물을 넣고 끓기 시작하면 불을 줄이고 15분 정도 더 끓여 멸치를 건져내고 육수를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2358, 444, 4, '다른 냄비에 ②의 쇠고기를 넣고 볶다가 육수를 넣고 끓으면 된장, 고추장, 고춧가루를 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2359, 444, 5, '국이 끓으면 근대를 넣고 다진 마늘, 어슷썬 파를 넣고 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2360, 445, 1, '멸치는 내장과 머리를 제거하고 살을 가른 다음 다시마, 표고버섯과 함께 물 3컵을 부어 육수를 끓인다. 육수가 끓으면 멸치와 다시마는 건져내고 표고버섯은 건져서 굵게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2361, 445, 2, '육수에 된장과 고춧가루를 푼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2362, 445, 3, '애호박은 길이로 반 갈라 0.5cm로 썰고, 두부는 3cm x 4cm로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2363, 445, 4, '청고추, 홍고추는 꼭지를 떼고 씨를 털어 어슷썰고, 대파도 같은 크기로 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2364, 445, 5, '끓는 육수에 표고버섯, 애호박을 넣고 끓으면 두부를 넣어 더 끓인후, 청고추, 홍고추, 대파, 다진 마늘, 참기름을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2365, 446, 1, '깻잎은 잘 씻어 소금물(물 4컵, 소금 1.5큰술)에 절여 체에 밭쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2366, 446, 2, '대파, 양파, 생강, 멸치, 액젓, 설탕, 물을 넣고 끓여 국물만 걸러 식혀서 사용한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2367, 446, 3, '고추는 반으로 갈라 씨를 털어낸 후 3cm 길이로 잘라서 사용하고 모든 재료는 곱게 채를 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2368, 446, 4, '잘 절여진 깻잎 여러장을 겹쳐 소를 넣고 돌돌 말은 후 통에 가진런히 담아 국물을 부은 후 냉장보관하여 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2369, 447, 1, '무는 1cm x 3cm 정도로 썰어 굵은 소금에 1시간정도 절인다. 썰고 남은 자투리는 버리지 않는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2370, 447, 2, '붉은 갓은 무와 같은 길이로 썰어 씻어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2371, 447, 3, '절인 무는 씻은 다음 물기를 빼 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2372, 447, 4, '물 2.5리터를 미리 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2373, 447, 5, '배 1/4개, 양파, 마늘, 생강, 자투리 무를 물 약간과 함께 넣고 믹서에 곱게 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2374, 447, 6, '체에 고운 보자기를 놓고 ②를 놓고 물로 거른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2375, 448, 1, '감자, 호박, 피망, 양파, 당근은 모두 0.3cm 정도로 깍둑썰기 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2376, 448, 2, '프라이팬에 베르톨리 엑스트라 라이트 올리브오일을 두르고 딱딱한 재료부터 볶아서 소금 간을 한 후 넓은 접시에 한 김 나가도록 펼쳐놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2377, 448, 3, '프라이팬에 베르톨리 엑스트라 라이트 올리브오일를 두르고 찬밥을 볶다가 ②를 넣고 소금, 후춧가루를 넣어 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2378, 448, 4, '파프리카는 꼭지 부분을 뚜껑모양으로 잘라내고 그 속에 ③의 재료를 모두 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2379, 448, 5, '180℃로 예열된 오븐에 ④를 넣고 20분 간 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2380, 449, 1, '돼지갈비는 먹기 좋게 잘라서 찬물에 1시간 정도 담갔다가 건져서 물기를 뺀 후 소금, 후춧가루, 다진 마늘을 넣고 레몬은 5mm 두께로 슬라이스하고 마른 고추는 꼭지를 떼어내고 작게 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2381, 449, 2, '갈비에 준비된 재료를 섞어 향이 배도록 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2382, 449, 3, '프라이팬에 베르톨리 엑스트라 버진 올리브오일을 두르고 저민 마늘을 넣고 볶아 향이 나면 재어둔 돼지갈비와 레몬을 넣어서 노릇하게 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2383, 449, 4, '노릇하게 색이 나면 화이트와인과 물 1컵을 넣고 약한 불에서 30분 정도 조린다. 간이 부족하면 소금과 후춧가루를 넣어서 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2384, 450, 1, '깨끗하게 씻은 시금치는 끓는물에 소금을 넣어 살짝 데쳐낸 후 찬물에 헹궈 물기를 짜고 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2385, 450, 2, '게맛살은 적당한 굵기로 찢어 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2386, 450, 3, '볼에 계란을 깨뜨리고 소금을 넣고 풀어준 뒤 체에 걸러 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2387, 450, 4, '프라이팬에 베르톨리 엑스트라 라이트 올리브오일을 두르고 약불에서 계란물이 얇게 깔리도록 부어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2388, 450, 5, '반 정도 익어가면 시금치와 게맛살을 가지런히 올려놓고 익히면서 돌돌 말아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2389, 451, 1, '모시조개는 해감을 시킨 뒤 깨끗이 씻어 냄비에 넣고 입이 벌어지도록 데쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2390, 451, 2, '낙지는 소금을 1/2컵을 넣고 주물러준 뒤 맑은 물에 여러번 헹군다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2391, 451, 3, '뜨겁게 달군 냄비에 참기름을 두르고 낙지를 넣은 뒤 뚜껑을 덮고 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2392, 451, 4, '①을 면보에 걸러 모시조개는 건지고 육수는 따로 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2393, 451, 5, '배추는 끓는 물에 데친 후 2cm 크기로 썰고 풋고추, 홍고추는 어슷썰며 대파는 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2394, 451, 6, '③의 낙지를 한 입 크기로 썬 후 배추와 ④의 육수를 넣고 한소끔 끓인 뒤 모시조개, 청고추, 홍고추, 대파, 다진 마늘을 넣고 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2395, 452, 1, '새우는 꼬치로 등쪽의 내장을 빼고, 옅은 소금물에 씻은 뒤 물기를 제거하고 굵게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2396, 452, 2, '쌀은 씻어 물에 충분히 불린 뒤 건져 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2397, 452, 3, '냄비에 참기름을 두르고 다진 새우와 쌀을 넣고 볶다가 쌀알이 투명해지면 물을 붓고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2398, 452, 4, '한소끔 끓어 오르면 불을 약하게 한 뒤 쌀알이 푹 퍼지도록 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2399, 452, 5, '쌀알이 퍼지면 소금으로 간을 하고 그릇에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2400, 453, 1, '전복은 껍질과 내장을 떼낸 뒤 솔로 전복살을 깨끗이 문질러 씻은 다음 얇게 저며 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2401, 453, 2, '쌀은 씻어 물에 불린 뒤 믹서에 넣고 너무 곱지 않게 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2402, 453, 3, '달군 냄비에 참기름을 두르고 준비한 전복을 넣고 볶다가 쌀과 물 6컵을 붓고 중불에서 은근히 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2403, 453, 4, '쌀알이 푹 퍼지면 소금 간을 한 뒤 그릇에 담은 다음, 채썬 김과 송송 썬 실파를 고명으로 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2404, 454, 1, '죽순은 껍질 벗긴 후 쌀뜨물이나 밀가루 푼 물에 10분정도 데쳐 죽순의 아린맛을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2405, 454, 2, '죽순은 빗살무늬로 얇게 썰어서 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2406, 454, 3, '미나리는 끓는 물에 소금을 넣고 살짝 데친 후 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2407, 454, 4, '계란은 황백으로 나눈 후 각각의 지단을 부치고 5cm x 0.5mm 폭으로 얇게 채썬다. 홍고추도 황백지단과 같은 크기로 채썰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2408, 454, 5, '채썬 황백지단을 4~5줄기 정도 적당히 집고 홍고추는 한 두개 정도만 끼워넣어 미나리줄기로 돌돌 만 후 미나리 끝부분을 속으로 집어넣어 마무리해준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2409, 454, 6, '분량의 재료로 초고추장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2410, 455, 1, '시금치를 끓는 물에 소금을 넣고 살짝 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2411, 455, 2, '데친 시금치는 찬물에 헹구어 물기를 꼭 짜놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2412, 455, 3, '대파는 흰부분만 4cm 길이로 채썰고, 홍고추도 같은 길이로 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2413, 455, 4, '멸치액젓, 대파채, 홍고추, 통깨, 참기름을 넣어 조물조물 무쳐 소금으로 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2414, 456, 1, '메주콩은 5배의 물을 붓고 반나절 정도 불려 콩이 완전히 불면 냄비에 물을 밥물 정도로 부어 뚜껑을 덮지 않고 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2415, 456, 2, '콩이 끓기 시작하면 5분 정도 더 끓여서 찬물에 여러 번 헹궈준 뒤, 양손으로 비벼가며 껍질을 벗기고 콩만 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2416, 456, 3, '믹서에 삶은 콩을 넣고 물을 부어가며 곱게 간 뒤, 체에 밭쳐 건더기는 걸러내고 국물만 받아 냉장고에 차게 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2417, 456, 4, '오이는 5cm 길이로 잘라 껍질을 돌려 깎아 얇게 채썰고, 방울토마토는 반으로 잘라둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2418, 456, 5, '끓는 물에 소금을 약간 넣고 녹차국수를 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2419, 456, 6, '냉면 용기에 삶은 녹차국수를 넣고, 육수와 콩국을 1:1 비율로 섞은 콩국물을 부은 후 오이채와 방울토마토를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2420, 457, 1, '메주콩은 분량의 5배의 물을 붓고 반나절 정도 담가 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2421, 457, 2, '콩이 완전히 불면 냄비에 담아 물을 밥물 정도로 부어 뚜껑을 열고 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2422, 457, 3, '콩이 끓기 시작하면 5분 정도 더 끓여 삶고, 찬물에 여러 번 헹궈준 뒤 양손으로 비벼가며 껍질을 벗기고 콩만 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2423, 457, 4, '믹서에 삶은 콩, 통깨, 잣을 넣어 생수를 부어가며 곱게 간 뒤 체에 걸러내고 국물만 받아 냉장고에 차게 두며 기호에 따라 소금으로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2424, 457, 5, '우무는 1cm 두께로 채썰고, 오이는 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2425, 457, 6, '그릇에 우무와 채썬 오이를 담고 ④의 콩국물을 부어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2426, 458, 1, '메주콩, 검은콩은 각각 5배의 물을 붓고 반나절 정도 담가 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2427, 458, 2, '콩이 완전히 불면 각각 다른 냄비에 콩을 넣고 물을 밥물 정도로 부어 뚜껑을 덮지 않고 삶다가 끓기 시작하면 5분 정도 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2428, 458, 3, '삶은 콩은 찬물에 여러 번 헹궈 준 뒤, 양손으로 비벼가며 껍질을 벗기고 콩만 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2429, 458, 4, '믹서에 삶은 각종 콩과 검은깨, 잣, 호두를 넣고 갈아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2430, 458, 5, '식빵을 1cm x 1cm 정사각형으로 잘라 버터를 두른 프라이팬에 바삭하게 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2431, 458, 6, '볼에 수프를 담고 구운 식빵을 올린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2432, 459, 1, '서리태콩은 잘 씻은 후 하룻밤 동안 불려둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2433, 459, 2, '냄비에 물을 넉넉히 붓고 불린 콩을 삶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2434, 459, 3, '삶은 콩은 찬물에 헹군 후 손으로 비벼서 껍질을 말끔히 벗기고 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2435, 459, 4, '콩을 물과 함께 곱게 간 다음 고운 체에 걸러서 소금으로 간을 한다. (물은 기호에 따라 넣는다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2436, 459, 5, '국수는 삶아 냉동된 차가운 물에 충분히 헹구어 사리를 만들어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2437, 459, 6, '그릇에 국수 사리를 담고 서리태 콩국수를 부은 후 오이와 토마토, 참깨를 뿌려 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2438, 460, 1, '날콩가루(50g)를 곱게 갈아 밀가루와 합하여 반죽을 한 후 젖은 면보에 싸둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2439, 460, 2, '닭과 닭발, 통파, 생강을 넣어 무르게 끓인 닭육수를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2440, 460, 3, '①의 반죽을 밀가루를 뿌려가면서 얇게 밀어 돌돌만 후 곱게 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2441, 460, 4, '채썬 반죽은 밀가루를 뿌린 쟁반에 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2442, 460, 5, '②의 닭이 익으면 건져 뼈를 발라내고 닭살은 찢어둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2443, 460, 6, '①의 콩가루보다는 굵게 갈은 콩을 ⑤의 닭살과 섞는다.(콩가루 50g)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2444, 461, 1, '전어는 비늘을 제거하고 머리, 지느러미, 꼬리, 내장을 깨끗이 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2445, 461, 2, '손질한 전어를 흐르는 물에 깨끗이 씻은 후 물기를 완전히 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2446, 461, 3, '무는 가늘게 채썰고, 미나리는 5cm 길이로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2447, 461, 4, '양파는 굵게 채썰고 청고추는 어슷썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2448, 461, 5, '콩가루, 고춧가루, 식초, 고추장, 다진 마늘, 설탕을 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2449, 461, 6, '②의 전어를 통채 먹기좋은 크기로 썬 후 ③과 ④를 넣고 ⑤의 양념장을 넣고 무친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2450, 462, 1, '왕새우는 깨끗이 손질한 다음 타임, 바질과 마늘(1쪽), 통후추, 샐러드 오일, 소금에 절인 후 160℃ 오븐에서 10분 간 굽는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2451, 462, 2, '감자는 삶아서 으깬 후 버터, 소금, 통후추, 와사비와 잘 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2452, 462, 3, '호박과 새송이버섯은 슬라이스하여 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2453, 462, 4, '나머지 채소도 한입 크기로 잘라 마늘(2쪽)과 올리브오일에 양념하여 팬에 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2454, 462, 5, '접시에 보기 좋게 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2455, 463, 1, '소꼬리는 흐르는 물에 손으로 문질러 가면서 불순물을 제거하고 깨끗이 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2456, 463, 2, '씻은 소꼬리는 찬 물을 두 세번정도 바꿔가면서 담가 두어 핏물을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2457, 463, 3, '냄비에 물을 넉넉하게 넣고 사골과 ②의 꼬리뼈를 넣고 3~4시간 푹 고아서 육수를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2458, 463, 4, '③의 육수에 꼬리를 넣고 마늘, 대추, 밤을 넣고 다시 30분 정도 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2459, 463, 5, '④에 청양고추를 반으로 잘라 넣고 30분 가량 더 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2460, 463, 6, '육수가 어느 정도 우러나면 송송 썬 파를 넣고 한소끔 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2461, 465, 1, '표고버섯은 기둥을 떼고 씻어서 4등분하고 닭고기는 사방 3cm 크기로 썰고 우엉은 껍질을 벗겨서 3cm 길이로 어슷썰고 당근도 껍질을 한입 크기로 썰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2462, 465, 2, '식용유 두른 냄비에 닭고기를 볶는다. 닭고기가 노릇해지면 꺼내고 우엉, 당근, 표고버섯을 넣고 볶다가 생강즙과 청주를 조금 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2463, 465, 3, '②에 토란탕 국물을 붓고 끓이면서 거품을 걷어낸다. 닭고기 볶은 것, 설탕, 맛술을 넣고 끓인다. 국물이 바닥에 약간 남을 때까지 졸여 그릇에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2464, 465, 4, '토란탕에서 건진 토란을 ③에 넣고 간장을 반 넣은 뒤 5분쯤 끓이다가 나머지 간장을 붓고 간장을 반 넣은 뒤 5분쯤 끓이다가 나머지 간장을 붓고 4~5분 다시 끓인다. 국물이 바닥에 약간 자작하게 남을 대까지 조려 그릇에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2465, 466, 1, '녹두는 6시간 정도 불린 후 껍질이 다 벗겨질 때까지 깨끗이 씻고, 김이 오른 찜통에서 40분 정도쪄서 뜨거울 때 체에 내려 고물을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2466, 466, 2, '단호박은 얇게 편썰어 설탕을 약간 뿌려 체에 밭쳐둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2467, 466, 3, '멥쌀가루에 소금을 약간 넣고, 멥쌀가루를 쥐어 살짝 던져 부서지지 않는 정도가 될 때까지 물을 약간씩 넣어가며 되기를 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2468, 466, 4, '시루에 시루본을 깔고, 케이크 틀 안에 준비해둔 녹두고물을 조금 깔고 설탕을 살짝 뿌린후 그 위에③의 절반을 놓고 단호박, ③을 켜켜이 넣어 윗면을 평평하게 다진 뒤 녹두고물을 고루 뿌린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2469, 466, 5, '김이 오른 찜통에서 중간 불로 20분간 찌고, 불을 끄고 5분간 뜸들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2470, 467, 1, '양파는 채썰고, 베이컨은 먹기 좋은 크기로 썰어놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2471, 467, 2, '냄비에 버터를 두르고, 양파와 다진 마늘을 넣고 볶다가 베이컨을 넣고 함께 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2472, 467, 3, '②의 양파가 투명해질 정도로 볶아지면 우유, 생크림, 바질을 넣고 끓이다가 끓어 오르면 불을 약하게 줄이고, 2분 정도 더 끓여 소금, 후춧가루로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2473, 467, 4, '끓는 물에 소금, 식용유를 약간 넣고 파스타를 넣어 기호에 맞게 적당히 삶아 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2474, 467, 5, '프라이팬에 버터를 두르고, 삶아놓은 파스타면을 볶다가 끓여 놓은 소스를 버무려 접시에 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2475, 468, 1, '상추는 깨끗이 씻어 물기를 제거하고 먹기 좋게 찢는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2476, 468, 2, '두부는 1cm 크기로 깍둑썰고, 실파는 송송 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2477, 468, 3, '다시마는 젖은 수건으로 깨끗이 닦은 후 가장자리에칼집을 내서, 냄비에 물 5컵을 넣고 함께 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2478, 468, 4, '국물이 끓으면 다시마를 건지고 불을 끈 후 가쓰오브시를 넣어 5분간 우려내고 불에서 내려, 볼에 면보를 깔고 걸러내 맑은 육수를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2479, 468, 5, '걸러낸 육수를 다시 냄비에 넣고 끓이다가 미소된장을 풀고 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2480, 468, 6, '준비해둔 두부, 상추, 실파를 그릇에 담고 된장국물을 담아 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2481, 469, 1, '가지는 연하고 부드러운 것을 준비해 꼭지를 잘라내고 열십자로 칼집을 넣어 소금물에 절였다가 씻어서 물기를 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2482, 469, 2, '부추, 실파는 깨끗이 씻어 2cm 길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2483, 469, 3, '불린 고춧가루에 다진 마늘, 다진 생강, 설탕, 액체 육젓을 넣어 고추 양념을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2484, 469, 4, '우묵한 그릇에 부추와 실파를 넣고 고추 양념으로 버무려 소금으로 간을 맞춘 후 물기를 뺀 가지의 칼집 사이로 버무린 소를 넣어 가지김치를 항아리에 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2485, 470, 1, '쇠고기를 잘게 채썰고, 청피망, 홍피망도 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2486, 470, 2, '채썬 쇠고기에 분량의 재료로 양념을 재운다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2487, 470, 3, '느타리버섯은 먹기 좋게 찢어 두고 표고버섯은 채썰어 준비하고, 팽이버섯은 밑둥을 잘라 흐르는 물에 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2488, 470, 4, '팬에 식용유를 넣고 쇠고기를 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2489, 470, 5, '볶은 쇠고기에 ③의 느타리버섯, 표고버섯, 팽이버섯을 넣고 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2490, 470, 6, '⑤의 재료에 ①의 채썬 청피망, 홍피망을 넣고 마지막에 소금과 후춧가루를 뿌려 완성한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2491, 471, 1, '부추는 깨끗이 다듬은 후 물기를 제거하고 5cm 길이로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2492, 471, 2, '분량의 양념 재료로 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2493, 471, 3, '부추와 양념장을 냉장고에 차갑게 보관해 두었다가 상에 내기 직전에 버무려 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2494, 472, 1, '돼지등갈비를 구이용으로 준비하여 찬물에 헹구어 핏물을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2495, 472, 2, '샐러리와 양파는 채썰고 마늘은 다진 후 바베큐 소스와 레드와인, 올리브오일, 소금, 후춧가루를 섞어 만든 다음 등갈비를 재워 찜통에 쪄낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2496, 472, 3, '쪄낸 등갈비에 종합소스를 발라 잠시 재운 후 오븐에 구워낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2497, 472, 4, '남아있는 소스를 한번 더 발라준 후 파슬리가루를 뿌려 접시에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2498, 473, 1, '비트는 칼로 잘 깎아 믹서기에 곱게 갈아둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2499, 473, 2, '절임 무는 갈아 놓은 비트에 물을 들인다.(밀폐용기에 무를 담고 비트를 담고 꾸욱 눌러주면 물에 물이 든다)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2500, 473, 3, '오이는 돌려깎기해서 썰고 당근, 파프리카, 표고버섯, 햄은 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2501, 473, 4, '계란은 지단을 부쳐 식으면 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2502, 473, 5, '준비한 채소들은 볶아 한 김 나가게 식혀둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2503, 473, 6, '비트에 절인 무는 물기를 제거하고 준비한 채소들을 담고 돌돌 말아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2504, 474, 1, '닭가슴살을 청주에 재워둔 후 깨끗하게 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2505, 474, 2, '팬에 기름을 두르고 닭가슴살을 노릇하게 구워준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2506, 474, 3, '노릇하게 구워낸 닭가슴살은 다진 마늘, 후춧가루, 마른 고추, 소금으로 간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2507, 474, 4, '상추, 치커리, 겨자잎은 결대로 찢고, 양파, 오이, 당근은 적당한 크기로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2508, 474, 5, '닭가슴살과 준비한 채소, 과일 등을 적당하게 버무려 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2509, 474, 6, '허니머스터드 소스와 레몬즙을 섞은 소스를 끼얹어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2510, 475, 1, '물에 된장을 넣고 풀어준 뒤 체에 한 번 내린다. 여기에 밀가루를 넣고 반죽해 여러번 치댄 다음 비닐백에 넣어 30분 간 냉장보관해 반죽을 휴지시켜준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2511, 475, 2, '버섯은 기둥을 떼어낸 후 저며 썰고 감자는 도톰하게 반달 모양으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2512, 475, 3, '대파는 어슷썰고 양파는 굵직하게 채썰고 고추는 어슷썰어 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2513, 475, 4, '멸치는 내장을 정리하고 물에 넣어 10분 정도 팔팔 끓인 다음 건져낸 후 감자와 표고버섯을 넣어 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2514, 475, 5, '끓은 육수에 수제비 반죽을 얄팍하게 뜯어 넣고 반죽이 떠오르기 시작하면 대파, 양파, 고추를 넣어 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2515, 475, 6, '불에서 내리기 전에 다진 마늘과 참기름, 국간장, 후춧가루로 간한 뒤 모자라는 간은 소금으로 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2516, 476, 1, '닭고기는 한 입 크기로 썰어 우유에 30분 이상 재워둔 후 건져서 소금, 후춧가루로 밑간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2517, 476, 2, '①에 계란을 넣은 후 분량의 녹말가루, 튀김가루, 카레가루를 넣고 튀김옷을 입힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2518, 476, 3, '170℃의 기름에 ②의 닭고기를 한번 튀겨내고, 180℃에서 한번 더 튀겨 바삭하게 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2519, 476, 4, '고추기름을 두른 프라이팬에 고추장을 비롯한 분량의 양념 재료를 넣고 한번 끓으면 튀겨진 닭고기를 넣어 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2520, 476, 5, '윤기가 나게 소스에 버무려지면 호두를 다져 넣고 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2521, 477, 1, '참치통조림은 체에 밭쳐 기름기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2522, 477, 2, '당근, 감자, 표고버섯, 호박은 크기를 맞춰 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2523, 477, 3, '냄비에 참기름을 살짝 두르고 불린 쌀을 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2524, 477, 4, '쌀이 어느 정도 투명해지면 실파를 제외한 야채, 참치를 넣고, 다시물 2컵을 넣어 한소끔 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2525, 477, 5, '바닥이 눌지 않도록 나무주걱으로 저어가면서 끓여 물이 졸아들면, 다시물 3컵을 넣고 쌀알이 푹 퍼지도록 끓여 국간장과 참기름으로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2526, 477, 6, '먹기 직전에 통깨, 다진 실파를 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2527, 478, 1, '두부는 곱게 다져 면보로 물기를 짜준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2528, 478, 2, '새우살은 곱게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2529, 478, 3, '파프리카는 1cm 두께의 링으로 썰어 안쪽의 하얀 심을 제거해준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2530, 478, 4, '파프리카 안쪽에 밀가루를 가볍게 묻힌 후 털어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2531, 478, 5, '①과 ②를 섞어 소금, 후춧가루로 간하여 잘 치대준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2532, 478, 6, '③의 파프리카에 소가 빠지지 않도록 잘 채워준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2533, 479, 1, '청고추와 홍고추, 당근은 5cm 길이로 채썰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2534, 479, 2, '오이도 같은 길이로 돌려 깎아 채썰고 새송이버섯도 같은 길이로 채썰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2535, 479, 3, '새우는 이쑤시개로 등쪽의 내장을 빼내고 껍질과 물총을 제거한 뒤 청주로 밑간해둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2536, 479, 4, '기름을 넉넉히 두른 프라이팬을 달구어 채소 재료와 새우를 따로 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2537, 479, 5, '따로 볶아 놓은 채소와 새우를 프라이팬에 함께 넣고 굴소스를 넣어 간이 밸 정도로만 센불에서 가볍게 볶은 후 후춧가루와 참기름으로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2538, 479, 6, '김이 오른 찜통에 5분간 쪄낸 꽃빵을 곁들인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2539, 480, 1, '쭈꾸미는 깨끗이 씻어 통으로 준비하고 미더덕은 옅은 소금물에 해감 시키며, 오징어는 껍질을 벗겨 안쪽에 대각선으로 칼집을 낸 후 한 입 크기로 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2540, 480, 2, '새우는 위에서 2~3번째 마디에 꼬치를 이용해 내장을 뺀 후 껍질째 씻고, 모시조개와 홍합은 껍질을 깨끗이 씻어 모시조개만 옅은 소금물로 해감 시킨다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2541, 480, 3, '미나리는 잎을 다듬어 헹궈낸 후 4~5cm 길이로 자르고 양파는 채썰고 무는 3cm x 3cm 크기로 썰어둔다. 고추와 대파는 어슷썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2542, 480, 4, '냄비에 준비한 무와 양파, 다시마를 넣고 끓이다가 국물이 끓으면 다시마를 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2543, 480, 5, '분량의 양념 재료를 넣어 양념장을 만든 후 ④에 손질해둔 해물을 함께 넣고 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2544, 480, 6, '해물이 익으면 고추, 미나리, 대파를 넣고 간을 맞춰 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2545, 481, 1, '배추김치는 먹기 좋은 크기로 자르고 고추와 대파는 어슷썰며, 표고버섯은 길이대로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2546, 481, 2, '맛술과 후춧가루로 밑간한 고기는 기름 두른 냄비에 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2547, 481, 3, '고기 표면이 익었으면 준비한 배추김치와 표고버섯, 청정원 국선생 육수를 500ml 부어 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2548, 481, 4, '김치가 익었으면 고추와 대파로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2549, 482, 1, '두부는 4cm x 3cm 크기로 썰어 앞뒤로 소금을 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2550, 482, 2, '고추는 어슷썰어 씨를 털어낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2551, 482, 3, '①의 두부는 물기를 닦아내고 녹말가루를 묻혀 튀기듯이 지져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2552, 482, 4, '기름을 두른 프라이팬에 마늘과 고추를 넣어 타지 않게 볶아준 후 분량의 굴소스와 물을 넣어 걸쭉하게 소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2553, 482, 5, '소스에 농도가 생겼으면 ③의 두부를 넣어 약불로 조려준 후 두부에 소스가 배어들면 참기름을 뿌려 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2554, 483, 1, '양배추와 양파는 가늘게 채썰고, 실파와 팽이버섯은 송송 썰어두고 새우와 베이컨은 손질해 팽이버섯과 같은 크기로 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2555, 483, 2, '볼에 밀가루, 다시물, 소금, 후춧가루를 넣어 멍울지지 않게 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2556, 483, 3, '베이컨과 새우를 프라이팬에 볶아 한 김 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2557, 483, 4, '실파와 베이컨, 새우를 제외한 재료를 반죽에 넣어 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2558, 483, 5, '기름 두른 프라이팬에 반죽을 부어 평평하게 펴준 후 베이컨, 새우, 실파를 얹고, 반죽 중앙에 계란을 깨뜨리고 반죽 위에 고루 퍼지도록 프라이팬을 움직여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2559, 483, 6, '반죽이 익는데 시간이 걸리므로 너무 센 불에서 조리하지 않도록 하며 바닥이 익어서 들리면 뒤집어 익혀서 접시에 담고 굴소스와 마요네즈를 골고루 뿌려준 후 가쓰오브시로 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2560, 484, 1, '새우와 오징어, 바지락은 깨끗이 씻고, 오징어는 새우와 비슷한 크기로 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2561, 484, 2, '파프리카와 팽이버섯은 곱게 다져 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2562, 484, 3, '기름 두른 프라이팬에 해물과 청주를 넣어 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2563, 484, 4, '새우에 붉은 빛이 돌면 야채 재료를 넣어 소금과 후춧가루로 간하여 볶는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2564, 484, 5, '④에 밥을 넣어 고루 섞어준 후 분량의 굴소스를 넣고 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2565, 485, 1, '새송이버섯은 흐르는 물에 헹궈 1cm 두께로 모양대로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2566, 485, 2, '쪽파는 송송 채썰고, 고추는 쪽파와 비슷한 크기로 잘게 다져준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2567, 485, 3, '분량의 쌈장을 비롯한 양념 재료를 넣어 양념을 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2568, 485, 4, '버섯의 앞뒤로 양념을 발라 20분 정도 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2569, 485, 5, '기름 두른 프라이팬에 ④를 올려 양념을 발라가며 앞뒤로 물기가 생기지 않게 구워준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2570, 486, 1, '양파는 채썰고 무는 한 입 크기로 두툼하게 깍둑썰며 고추는 어슷썰고 미나리와 쑥갓은 손질해 4~5cm 길이로 잘라둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2571, 486, 2, '새우는 물총을 제거하고 게는 몸체와 등딱지를 분리해 먹기 좋게 손질한다. 오징어는 껍질을 벗겨 안쪽에 대각선 모양으로 칼집을 내어 5cm x 2cm 길이로 손질해 준비하고, 나머지 해산물 재료는 흐르는 물에 헹궈 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2572, 486, 3, '냄비에 무, 양파, 다시마를 넣고 약불에서 끓이다 끓기 시작하면 다시마를 건져내고 게를 넣어 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2573, 486, 4, '다진 마늘과 생강을 제외한 분량의 양념 재료를 섞어 해물탕 양념장을 만들어 ③에 넣고 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2574, 486, 5, '국물이 끓으면 나머지 해물 재료를 넣고 끓인 후 새우가 빨갛게 익으면 미나리, 쑥갓, 청/홍고추, 다진 마늘과 생강을 넣어 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2575, 487, 1, '뚝배기에 2cm x 2cm로 썬 양파, 다시마, 멸치를 넣어 약불에서 끓이다가 끓기 시작하면 다시마와 멸치를 건져낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2576, 487, 2, '국물이 끓으면 해감 시킨 바지락과 씻은 미더덕, 잔새우를 넣어 바지락의 입이 벌어지면 순두부와 고춧가루를 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2577, 487, 3, '새우젓국으로 간을 맞추고 계란과 약간의 참기름, 깨소금을 넣어 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2578, 488, 1, '쑥갓은 흐르는 물에 헹구고, 김은 가위로 얇게 잘라 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2579, 488, 2, '김치는 송송 썰어 분량의 재료를 섞은 김치양념을 넣고 버무려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2580, 488, 3, '새우는 꼬리를 제외한 머리와 몸통을 껍질을 벗기고 등쪽의 내장을 제거해 소금, 후춧가루, 맛술로 재워둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2581, 488, 4, '냄비에 분량의 시판용 장국과 물을 넣어 끓으면 준비된 우동면과 고춧가루, 새우를 넣어 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2582, 488, 5, '모자라는 간은 국간장으로 맞춰 불에서 내리고 쑥갓과 김가루를 넣어 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2583, 489, 1, '끓는 물에 3분 가량 쫄면을 넣고 삶아서 익으면 찬물에 비벼가며 씻어서 물기를 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2584, 489, 2, '새싹채소는 물에 헹구고 나머지 준비된 채소는 곱게 채썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2585, 489, 3, '식초를 비롯한 분량의 양념 재료를 넣어 비빔장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2586, 489, 4, '넓은 볼에 차게 준비한 쫄면을 담고 비빔장을 넣어 비빈다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2587, 489, 5, '그릇에 ④의 쫄면을 사리 지어 담고 준비한 채소를 얹어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2588, 490, 1, '설탕이 첨가되지 않은 플레인요구르트를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2589, 490, 2, '분량의 플레인요구르트에 취향에 따라 발효초를 7~10큰술 넣고 잘 섞는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2590, 490, 3, '기호에 따라 과일을 다져 넣거나 초콜릿을 다져서 곁들여 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2591, 491, 1, '준비된 재료를 깨끗하게 손질해 브로콜리를 제외한 채소를 한 입 크기로 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2592, 491, 2, '브로콜리는 끓는 물에 소금을 넣고 데친 후 과도를 이용해 한마디씩 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2593, 491, 3, '발효초를 비롯한 분량의 양념 재료를 한소끔 끓여 식혀서 촛물을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2594, 491, 4, '밀폐 용기에 손질한 채소를 넣고 식혀둔 촛물을 부어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2595, 491, 5, '2시간 이상 절여두었다가 냉장고에 두어 차게 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2596, 492, 1, '뜨거운 물에 떡국떡을 불려 부드럽게 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2597, 492, 2, '대파는 어슷썰고, 김은 프라이팬에 앞뒤로 바삭하게 구워 부셔둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2598, 492, 3, '배추김치는 속을 털어 잘게 다지고, 숙주는 끓는 물에 데쳐 다지고, 두부는 물기를 제거하고 으깬다. 파는 곱게 다져 만두소를 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2599, 492, 4, '분량의 만두소 재료를 섞어 만두를 빚는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2600, 492, 5, '끓는 물에 준비된 떡과 만두를 넣어 끓어오르면 국간장과 소금, 다진 마늘, 파를 넣어 간을 맞춘다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2601, 492, 6, '계란을 풀어서 끓는 국물에 넣고 국자로 휘저어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2602, 493, 1, '무는 껍질을 벗기고 3cm 길이로 썰고 1/2등분하여 냄비 바닥에 깐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2603, 493, 2, '김치는 길이대로 반 갈라 냄비에 넣고 김칫국물과 물을 1/2컵씩 부어 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2604, 493, 3, '대파는 손질해 5cm 길이로 썰어 준비하고 마늘은 꼭지를 제거해서 ②에 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2605, 493, 4, '꽁치통조림의 꽁치는 체에 밭쳐 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2606, 493, 5, '찌개가 끓기 시작하면 꽁치를 넣고 김치가 충분히 익을 때까지 끓여준 후 대파를 넣고 간장으로 간을 맞춰 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2607, 494, 1, '두부는 사방 1.5cm의 크기로 깍둑썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2608, 494, 2, '손질한 실파는 파란 부분을 송송 썰어 준비하고 팽이버섯은 2cm 간격으로 썰어 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2609, 494, 3, '냄비에 물5컵과 다시마를 넣고 물이 끓으면 미소된장을 체를 이용해 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2610, 494, 4, '다시마를 건져 길이 4cm로 곱게 채썰어 ③에 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2611, 494, 5, '준비한 두부를 넣어 두부가 위로 떠오르면 장으로 간하고 실파를 띄워 마무리한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2612, 495, 1, '무는 넓적하고 얇게 채썰어 분량의 배합초 재료에 절여둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2613, 495, 2, '냄비에 물, 소금, 계란을 넣고 물이 끓기 시작하면 10분 정도 삶아 반으로 잘라둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2614, 495, 3, '방울토마토와 곁들임용 샐러드채소는 흐르는 물에 씻어 손질한 후 차게 둔다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2615, 495, 4, '분량의 비빔양념 재료를 넣어 비빔장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2616, 495, 5, '끓는 소금물에 냉면을 2~3분간 삶아 찬물에 헹궈 1인분씩 사리 짓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2617, 495, 6, '그릇에 냉면사리를 담고 비빔장과 준비한 재료를 올린 후 얼음을 넣어 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2618, 496, 1, '메기는 지느러미를 잘라내고 내장을 뺀 뒤 물에 씻어 4cm로 토막 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2619, 496, 2, '무는 3cm 정도 되게 나박썰고 쑥갓은 깨끗이 다듬어 같은 크기로 뚝뚝 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2620, 496, 3, '식용유 1큰술에 분량의 고춧가루와 고추장, 다진 마늘, 생강, 소금을 넣어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2621, 496, 4, '냄비에 물 4컵을 넣고 끓이다가 양념장을 풀고 무를 넣는다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2622, 496, 5, '양념장 푼 물이 끓으면 손질한 메기를 넣고 충분히 살이 물러지도록 끓인다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2623, 496, 6, '충분히 끓여 생선이 익으면 쑥갓과 파를 넣고 조금 더 끓여 불에서 내린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2624, 497, 1, '비늘과 내장을 제거한 잉어를 마른 면보로 닦아낸 후 사선으로 칼집을 내어 부서지지 않게 기름에 살짝 튀긴 다음 찜솥에 구멍 낸 은박지를 깔고 중불에서 30∼40분 정도 찐다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2625, 497, 2, '콩나물은 머리를 떼고 살짝 삶아 씻어 건진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2626, 497, 3, '고명 (미나리, 계란노른자지단, 홍고추)은 6∼7cm 길이로 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2627, 497, 4, '찐 잉어 위에 분량의 재료로 만든 양념장을 듬뿍 발라 10분 정도 찐 다음 가장자리에 콩나물을 놓고 준비한 고명을 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2628, 498, 1, '접시에 양상추와 무순, 파슬리, 오이 등 신선한 채소를 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2629, 498, 2, '채소 위에 송어를 얹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2630, 498, 3, '피클, 파인애플, 마늘, 양파, 꿀을 넣고 믹서에 갈아 드레싱소스를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2631, 498, 5, '샐러드와 드레싱을 곁들여 그릇에 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2632, 499, 1, '베이컨은 1cm 크기로 잘라 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2633, 499, 2, '양파는 잘게 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2634, 499, 3, '이태리파슬리는 잘게 다져 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2635, 499, 4, '그라나 빠다노는 잘게 갈아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2636, 499, 5, '면 삶을 물이 끓으면 소금을 넣고 면을 넣어 6분 동안 삶아 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2637, 499, 6, '프라이팬에 오일을 조금 두르고 베이컨을 볶는다. 기름이 바짝 빠지면 베이컨을 접시에 꺼내어 놓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2638, 500, 1, '채소는 모두 5mm 정도의 주사위 모양으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2639, 500, 2, '프라이팬에 올리브오일을 두르고 자른 채소를 볶은 후 생쌀을 넣어 토스트 하다가 화인트와인을 넣고 휘발시킨 다음 완두콩을 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2640, 500, 3, '②를 오븐용기에 옮겨 담아 뜨거운 닭 육수로 가득 채우고 180℃ 오븐에서 물기를 모두 흡수할 때까지 약 12분간 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2641, 500, 4, '오븐에서 익힌 쌀은 꺼내어 재빨리 식힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2642, 500, 5, '식은 쌀에 올리브오일, 다진 고추, 사라다나, 트레비소, 소금, 후춧가루로 양념한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2643, 500, 6, '끓는 물에 낙지를 데친 후 작은 메달모양으로 자른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2644, 516, 1, '불린 멥쌀과 찹쌀은 체에 건저 물기를 빼고, 쌀알 크기가 반만하게 믹서에 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2645, 516, 2, '시금치는 잎만 깨끗이 씻어 끓는 물에 데쳐, 찬물에 헹궈 물기를 꼭 짜고 곱게 다진다.(약 2-3잎)');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2646, 516, 3, '배는 껍질을 벗기고 속과 씨를 도려낸 뒤, 강판에 곱게 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2647, 516, 4, '냄비에 쌀가루와 쇠고기 육수를 넣고 센 불에서 저어가며 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2648, 516, 5, '미음이 한소끔 끓으면 불을 약하게 줄이고, ②와 ③의 재료를 넣고 쌀알이 완전히 퍼질 때까지 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2649, 531, 1, '쌀은 깨끗하게 씻어 차가운 물에 1시간 정도 담가 불린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2650, 531, 2, '표고버섯, 감자, 시금치와 양파, 당근은 손질한 후 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2651, 531, 3, '된장을 약간의 물에 푼다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2652, 531, 4, '냄비에 ②의 재료를 넣어 볶다가 ③과 불린 쌀, 물을 넣고 강한 불에서 저어가며 끓이고, 끓기 시작하면 약한 불에서 쌀이 푹 퍼질 때까지 10분정도 더 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2653, 5836, 1, '쇠고기, 양파, 당근, 감자, 홍피망, 청피망, 양송이버섯은 큼직하게 썬다. 쇠고기는 소금, 후추를 약간 넣어 따로 밑간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2654, 5836, 2, '토마토는 윗면에 십자칼집을 내고 끓는물에 넣어 살짝 데쳐 껍질을 벗긴 다음 큼직하게 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2655, 5836, 3, '냄비에 기름을 두르고 다진 마늘과 양파를 넣어 볶은 뒤 양파가 투명해지면 쇠고기를 넣고 볶아 겉면을 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2656, 5836, 4, '물 2.5컵과 당근, 감자, 홍피망, 청피망, 토마토, 토마토페이스트, 칠리, 월계수잎, 바질을 넣고 중불에서 은근히 끓인다. 감자가 익으면 마지막에 양송이버섯을 넣어 살짝 더 끓인 뒤 소금, 후추로 간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2657, 39665, 1, '닭은 깨끗이 씻어 냄비에 넣고 물 2L와 인삼, 대파, 양파, 대추, 통후추를 함께 넣어 푹 삶는다. 삶아진 닭은 건져내어 살만 발라내고 손으로 잘게 뜯는다. 육수는 체에 거르고 육수에 쓰인 인삼은 굵직하게 다지고 대추는 채썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2658, 39665, 2, '찹쌀은 씻어 1시간 이상 불려두었다가 체에 밭친 뒤 물 2컵과 함께 믹서에 곱게 간다. 땅콩은 껍질을 벗기고 검은깨, 물 1컵과 함께 믹서에 곱게 간 후 체에 거른다. 체에 남아있는 껍질에 물 1컵을 더 넣고 거른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2659, 39665, 3, '냄비에 먼저 갈아둔 찹쌀, 잘게 뜯은 닭, 다진 인삼, 닭육수 3컵을 넣고 바닥이 눌어붙지 않도록 나무주걱으로 바닥을 저으며 끓인다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2660, 39665, 4, '찹쌀이 뭉근히 퍼지면 검은깨와 땅콩을 거른 물, 채썬 대추를 넣고 물 1컵으로 농도를 조절한 후 소금으로 간한다. 잣은 고깔을 제거하여 고명으로 올려낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2661, 39692, 1, '돼지갈비는 흐르는 물에 깨끗이 씻은 다음 찬물에 30분 정도 담가 핏물을 뺀다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2662, 39692, 2, '냄비에 찬물 1.5L, 대파, 양파, 통마늘, 통후추를 넣고 끓인다. 핏물을 뺀 돼지갈비를 넣고 살짝 데쳐내어 불순물을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2663, 39692, 3, '갈비양념을 만들어 데친 돼지갈비에 넣고 버무려 밑간한다. 시래기는 된장과 다진파를 넣고 버무려 밑간한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2664, 39692, 4, '냄비에 밑간한 돼지갈비와 시래기, 다시마 우린 물 2컵을 넣은 다음 뚜껑을 닫고 푹 익힌다. 바닥이 눌어붙지 않도록 중간 중간 뒤섞어가며 익힌다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2665, 62330, 1, '절인배추는 물기를 뺀 후 겉잎은 6~7장정도 보자기용으로 떼어두고, 속 부분은 3×3×0.3㎝로 썬다. 무와 배도 배추와 같은 크기로 썬다. 갓, 미나리, 실파는 2.5㎝길이로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2666, 62330, 2, '밤은 동글납작하게 편으로 썰고, 대추는 곱게 채 썰고, 잣은 고깔을 뗀다. 굴은 소금물에 담가 씻어 물기를 빼고, 낙지다리는 소금으로 문질러 씻어 3㎝길이로 썬다. 마늘 2쪽과 생강 1/2톨은 채 썰고 나머지는 다져서 분량의 재료를 넣고 김치양념을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2667, 62330, 3, '큰 볼에 배추, 무, 김치양념을 먼저 넣고 버무린 후 갓, 미나리, 실파, 배, 굴, 낙지를 고루 섞어 김칫소를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2668, 62330, 4, '넓은 그릇에 절인 배춧잎을 깔고 버무린 김칫소를 소복하게 채운다. 대추채, 잣을 고명으로 얹고 펼쳐진 배춧잎 보자기로 한 장씩 차례로 덮어 싸고 2~3일 후 삼삼한 소금물을 보쌈김치가 절반 정도 잠기도록 붓는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2669, 73679, 1, '절인배추는 물기를 뺀 후 겉잎은 제거하고 3×3×0.3㎝로 썬다. 무와 배도 배추와 같은 크기로 썬다. 미나리, 실파는 2.5㎝길이로 썰고 밤은 동글납작하게 편으로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2670, 73679, 2, '굴은 소금물에 담가 씻어 물기를 빼고, 낙지다리는 소금으로 문질러 씻어 3㎝길이로 썬다. 북어는 물에 불려 껍질을 벗겨내고 뼈를 발라낸 후 2.5×2.5×0.3cm 크기로 썬다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2671, 73679, 3, '큰 볼에 배추, 무, 배, 밤, 김치양념을 넣고 버무린 다음 미나리, 실파, 다진마늘, 다진생강을 넣고 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2672, 73679, 4, '마지막에 생굴, 낙지, 북어를 넣고 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2673, 73699, 1, '비닐에 씌운 단호박을 전자렌지에 5분 돌려준후 씨앗을 제거하고 반달모양이 되도록 편썰기를 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2674, 73699, 2, '후라이팬에 앞뒤로 구워준다.');
