INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2678, 73712, 1, '손질한 시금치를 씻어 물 반 컵과 함께 믹서기에 넣고 곱게 갈아준다.');
