INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2752, 90980, 5, '양파와 대파, 고추를 잘게 썰어 볼에 넣는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2753, 90980, 6, '호박이 담긴 볼에 양념을 넣고 잘 무쳐준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2754, 90997, 1, '상추, 치커리등의 어린잎채소를 물에 담갔다가 깨끗하게 씻어 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2755, 90997, 2, '치즈와 햄은 세모모양이 되도록 반으로 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2756, 90997, 3, '토마토를 슬라이스 해준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2757, 90997, 4, '모닝빵을 빵칼을 이용하여 반을 갈라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2758, 90997, 5, '빵사이에 어린잎과 햄,치즈, 토마토를 넣고, 소스를 발라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2759, 91010, 1, '굵은소금이나 베이킹 소다를 이용하여 라임 껍질을 깨끗하게 문질러 닦는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2760, 91010, 2, '허브잎을 흐르는 물로 깨끗하게 씻는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2761, 91010, 3, '라임을 적당한 두께로 썰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2762, 91010, 4, '물병에 허브와 라임을 적당히 넣어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2763, 91010, 5, '라임과 허브를 살짝 으깨준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2764, 91010, 6, '기호에 맞게 설탕을 조절하여 넣어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2765, 91010, 7, '병에 얼음을 넣고 탄산수를 부어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2766, 91023, 1, '고추는 깨끗하게 씻어 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2767, 91023, 2, '냄비에 간장, 설탕, 식초 물을 넣고 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2768, 91023, 3, '물기를 제거한 고추를 이쑤시개로 구멍을 뚫어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2769, 91023, 4, '통에 고추를 넣고 식힌 간장을 부어 그늘지고 시원한 곳에서 15일간 숙성 시켜준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2770, 91023, 5, '숙성된 고추장아찌는 냉장고에 보관한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2771, 120344, 1, '깻잎은 흐르는 물에 깨끗하게 씻어 꼭지를 잘라준 후 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2772, 120344, 2, '홍고추는 반으로 갈라 씨를 제거한 후 잘게 다져준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2773, 120344, 3, '쪽파도 깨끗하게 씻어 뿌리를 제거 후 잘게 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2774, 120344, 4, '볼에 양념 재료를 넣고 다진파와 홍고추를 함께 잘 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2775, 120344, 5, '깻잎 3~4장씩 올려 양념을 골고루 발라준다. 이를 반복하여 깻잎에 양념을 바른다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2776, 120344, 6, '상온에서 반나절 숙성시켜준다');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2777, 120360, 1, '두부를 볼에 넣고 으깨준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2778, 120360, 2, '으깬 두부에 굴소스와 매실을 넣고 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2779, 120360, 3, '양념한 두부에 다진고추 와 다진 아몬드를 넣어 버무린다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2780, 120360, 4, '가지는 세로로 칼집을 내어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2781, 120360, 5, '기름을 두른 팬에 가지를 2~3분가량 뚜껑을 닫고 익혀준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2782, 120360, 6, '익은 가지에 준비해둔 두부를 칼집을 낸 곳에 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2783, 120360, 7, '다시 한 번 팬에 굴소스를 두른 후 뚜껑을 닫고 익혀 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2784, 120360, 8, '접시에 에멘탈 치즈가루를 깔고 그 위에 가지두부스테이크를 올려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2785, 120360, 9, '완성된 가지두부스테이크 위에 실파를 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2786, 120379, 1, '삶은 달걀과 단무지를 잘게 다져준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2787, 120379, 2, '양파는 잘게 썰어 식초, 황설탕, 소금(1T)을 넣어 잠시 절여 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2788, 120379, 3, '깨끗하게 씻은 오이를 길게 반으로 가른 다음 3~4Cm 간격으로 잘라 씨를 파준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2789, 120379, 4, '참치캔은 기름을 제거하고 크기가 큰 견과류는 잘게 다져준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2790, 120379, 5, '볼에 참치, 다진 삶은 달걀, 단무지를 넣고, 절인 양파는 물기를 꼭 짠 후 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2791, 120379, 6, '볼에 양념제료 마요네스, 머스타드소스, 황설탕, 소금(1T), 꿀, 허브솔트를 넣어 잘 섞어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2792, 120379, 7, '준비된 오이 보트에 양념을 한 참치를 올리고 견과류로 장식을 해 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2793, 120401, 1, '청양고추와 대파를 다진다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2794, 120401, 2, '그릇에 간장, 맛술, 올리브유, 다진마늘과 식초, 참기름을 넣고 다진 청양고추와 다진 파를 넣어 잘 섞어준다. 참기름과 통깨는 적당히 첨가한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2795, 120401, 3, '애호박은 0.5CM 정도의 두께로 썰어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2796, 120401, 4, '달궈진 팬에 기름을 두르고 썰어둔 애호박을 올려 구워준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2797, 120401, 5, '구워진 애호박을 접시에 둘러 담고 가운데 어린잎채소를 올려 양념장을 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2798, 120419, 1, '참기름과 매실액에 다진 고추와 깨를 넣어 잘 섞어 소스를 만들어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2799, 120419, 2, '시금치는 끓는 물에 살짝 데친 후 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2800, 120419, 3, '고사리는 물러질 때까지 삶은 후, 깨끗하게 헹궈 물기를 빼 참기름과 소금으로 간을 하고 팬에 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2801, 120419, 4, '콩나물은 깨끗하게 씻을 후 끓는 물에 넣고 뚜껑을 닫고 익혀 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2802, 120419, 5, '무는 채 썰어 팬에 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2803, 120419, 6, '팬에 연어살을 올려 앞뒤로 구워준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2804, 120419, 7, '라이스페이퍼는 말기 좋게 사각형으로 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2805, 120419, 8, '따듯한 물에 라이스페이퍼를 1~2초간 담군 후 접시에 깔아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2806, 120419, 9, '라이스페이퍼위에 준비한 재료를 취항대로 넣고 돌돌 말아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2807, 120419, 10, '접시에 담아 준비한 소스와 함께 먹는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2808, 120441, 1, '간장, 다진마늘, 고춧가루, 설탕, 들기름을 잘 섞어 양념장을 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2809, 120441, 2, '냄비에 쌀뜨물은 붓고  육수재료와 무를 넣고 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2810, 120441, 3, '육수가 끓으면 고추장과 된장으로 밑간을 한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2811, 120441, 4, '육수에 손질한 고등어를 넣어 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2812, 120441, 5, '고등어가 살짝 익으면 단고구마와 양파를 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2813, 120441, 6, '냄비에 준비한 양념장을 넣어 고등어가 잘 익을 때 까지 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2814, 120441, 7, '홍고추와 청양고추를 마지막에 넣어 한 번 더 끓여준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2815, 120463, 1, '껍질을 벗긴 감자를 강판에 간다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2816, 120463, 2, '볼에 강판에 간 감자와 다진 양파를 넣고 소금을 살짝 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2817, 120463, 3, '볼에 부침가루를 농도에 맞게 넣어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2818, 120463, 4, '당근 잎은 큰 줄기는 때어내어 깨끗하게 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2819, 120463, 5, '중불로 달군 팬에 감자전 반죽을 넓게 펼친 다음 당근잎과 당근채를  반죽위에 올려 모양을 낸다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2820, 120463, 6, '앞뒤로 뒤집으며 잘 익혀준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2821, 120476, 1, '채소들은 깨끗하게 씻어 물기를 빼준 후 만두소에 들어갈 재료는 잘게 다져 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2822, 120476, 2, '다짐육은 키친타올로 살짝 눌러 핏물을 제거하고, 두부는 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2823, 120476, 3, '큰 볼에 준비한 만두소 재료를 넣고 잘 섞어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2824, 120476, 4, '만두의 피가 될 배추는 잎 부분을 뺀 하얀 대 부분을 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2825, 120476, 5, '대를 제거한 배추잎을 찜통에 넣고 살짝 쪄준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2826, 120476, 6, '배추만두를 묶을 미나리를 소금물에 살짝 데쳐준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2827, 120476, 7, '만두소는 고기가 익을 때 까지 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2828, 120476, 8, '데친 배추잎을 펼치고 가운데 만두소를 넣고 복주머니 형태로 감싼 뒤 미나리로 묶어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2829, 120476, 9, '복주머니 형태가 어려우면 막대 형태로 말아 미나리로 묶어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2830, 120476, 10, '찜통에 배추만두를 넣고 5분간 쪄준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2831, 180330, 1, '깨끗하게 씻은 고구마를 찜통에 넣고 쪄준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2832, 180330, 2, '잘 쪄진 고구마는 껍질을 벗기고 볼에 담에 으깨고 마요네즈, 우유, 건블루베리, 꿀, 소금을 넣고 으깬 고구마와 잘 섞어 고구마메시를 만들어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2833, 180330, 3, '식빵을 밀대로 얇게 밀어 납작하게 만들어 준 후 가장자리는 깔끔하게 잘라 낸다. 식빵의 가장자리에 달걀물을 바르고 고구마메시를 식빵위에 올려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2834, 180330, 4, '식빵을 반으로 접고 고구마메시가 튀어나오지 않도록 빵 가장자리를 포크를 이용해 꾸욱~눌러준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2835, 180330, 5, '사선으로 빵에 칼집을 내어 빵 겉에 달걀물을 고루 발라준다. 200도로 예열된 오븐에 약 6분간 구워준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2836, 180344, 1, '볶음밥에 들어갈 양파와, 셀러리, 햄을 다져서 준비한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2837, 180344, 2, '계란은 볼에 풀어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2838, 180344, 3, '방울토마토는 칼집을 내어 끓는 물을 부었다가 건져 껍질을 제거한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2839, 180344, 4, '달궈진 팬에 올리브유를 두르고 양파, 셀러리, 토마토 순서대로 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2840, 180344, 5, '볶아진 재료에 밥을 넣고 후추를 뿌려 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2841, 180344, 6, '볶아진 밥을 한쪽으로 몰아놓고 풀어놓은 달걀을 팬에 부어 스크램블에그를 만든다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2842, 180344, 7, '만들어진 스크램블에그를 볶음밥과 잘 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2843, 180344, 8, '냄비에 물과 카레가루를 넣고 끓여 카레소스를 만들어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2844, 180344, 9, '그릇에 볶음밥과 카레소스를 함께 담는다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2845, 180363, 1, '새우와 쪽파는 데치기 전에 깨끗하게 손질한다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2846, 180363, 2, '쪽파는 끓는 물에 소금을 넣고 살짝 데친 후 찬물에 담갔다 물기를 빼준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2847, 180363, 3, '새우는 끓는 물에 소금을 약간 넣고 데친다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2848, 180363, 4, '그릇에 고추장, 식초, 설탕, 깨를 넣고 잘 섞어 초장을 만들어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2849, 180363, 5, '새우에 쪽파를 감아 접시에 담아 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2850, 195428, 1, '당근과 오이는 얇게 슬라이스 해준다. 가지는 길게 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2851, 195428, 2, '오이를 제외한 당근과 가지를 기름 두른 팬에 볶아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2852, 195428, 3, '밥에 통깨와 참기름  맛소금을 넣고 주걱을 세워 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2853, 195428, 4, '김말이 위에 랩을 깔아주고 김을 올려놓고 밥을 김 위에 잘 펴 뒤집어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2854, 195428, 5, '뒤집은 김위에 김밥속 재료인 당근, 가지, 오이를 올려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2855, 195428, 6, '김발과 랩을 빼 주며 김밥을 말아 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2856, 195428, 7, '완성된 김밥위에 참기름과 통깨를 발라 먹기 좋은 크기로 잘라준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2857, 195428, 8, '접시에 어린잎 채소를 깔아주고 그 위에 자른 김밥을 올려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2858, 195428, 9, '믹서기에 두부, 식초, 설탕, 마요네즈, 통깨를 넣고 갈아 두부드레싱을 만들어 준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2859, 195428, 10, '접시에 담긴 김밥위에 두부 드레싱을 뿌려준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2860, 195453, 1, '대파, 호박, 배추는 작게 다져준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2861, 195453, 2, '볼에 콩비지와 고기, 다진 채소, 다진마늘, 후추, 소금, 참기름을 넣어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2862, 195453, 3, '부침가루와 전분을 넣고 계란 1개를 넣어 반죽하듯 잘 섞어준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2863, 195453, 4, '동그랑땡 반죽을 동글납작하게 모양을 잡아준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2864, 195453, 5, '모양을 낸 반죽에 밀가루를 묻히고, 계란을 푼 계란물을 묻혀준다.');
INSERT INTO what2cook.detail (ID, RECIPE_ID, `ORDER`, DESCRIPTION) VALUES (2865, 195453, 6, '달궈진 팬에 기름을 두르고 옷을 계란물옷을 입힌 동그랑땡을 노릇하게 익혀준다.');